export const insertPreguntas = [
    `INSERT INTO "preguntas" VALUES (1, 'La Constitución Española de 1978, debida a una iniciativa parlamentaria, fue elaborada y aprobada por las Cortes formadas como resultado de las Elecciones Generales del 15 de junio de 1977. De las siguientes fechas, señala la que corresponde con la publicación del citado texto en el Boletín Oficial del Estado.', '6 de diciembre de 1978.', '31 de octubre de 1978.', '27 de diciembre de 1978.', '29 de diciembre de 1978.', 'D');`,
    `INSERT INTO "preguntas" VALUES (2, 'De las siguientes características, señala la que NO define a la Constitución Española.', 'Posee aplicabilidad directa e inmediata.', 'Es una constitución flexible en cuanto a su procedimiento de reforma.', 'Es una constitución derivada por las influencias recibidas en su redacción.', 'Es una constitución extensa, solo superada por la de 1812, con 169 artículos y más de 17.000 palabras.', 'B');`,
    `INSERT INTO "preguntas" VALUES (3, 'La estructura de la Constitución Española de 1978, consta de 169 artículos. Contiene un preámbulo, título preliminar, 10 títulos, 4 disposiciones adicionales, 9 disposiciones transitorias, 1 disposición derogatoria y 1 disposición final. Algunos autores hablan de que esta norma contiene dos partes, la Parte Dogmática, ¿qué incluye?', 'Los Títulos del II al IX.', 'El Título Preliminar, el Título III y la declaración de los derechos y libertades.', 'El Título Preliminar, el Título II y la declaración de los valores primarios del Estado.', 'El Título Preliminar, el Título I y la declaración de los valores primarios del Estado y de los derechos y libertades.', 'D');`,
    `INSERT INTO "preguntas" VALUES (4, 'La estructura de la Constitución española de 1978 consta de 169 artículos. Contiene un preámbulo, título preliminar, 10 títulos, 4 disposiciones adicionales, 9 disposiciones transitorias, 1 disposición derogatoria y 1 disposición final. Algunos autores hablan de que esta norma contiene dos partes, la Parte Orgánica, ¿qué incluye?', 'Los Títulos del III al IX.', 'Los Títulos del IV al IX.', 'Los Títulos del II al VIII.', 'Los Títulos del II al IX.', 'D');`,
    `INSERT INTO "preguntas" VALUES (5, '¿Qué valor superior de la Constitución de 1978 se recoge en el Título Preliminar y se define como "la diversidad de ideas políticas que compiten por obtener el apoyo de la ciudadanía a los diferentes partidos y organizaciones"?', 'El pluralismo político.', 'La justicia.', 'La libertad.', 'La igualdad.', 'A');`,
    `INSERT INTO "preguntas" VALUES (6, 'Los derechos y deberes fundamentales se encuentran recogidos en el Título I de la Constitución Española. Son derechos y deberes fundamentales de los ciudadanos porque son básicos para que un Estado pueda ser democrático y están muy relacionados con la dignidad humana. De los siguientes derechos señala el que NO es fundamental:', 'Derecho al honor, intimidad personal y familiar y a la propia imagen.', 'Derecho a la libre elección de residencia y circulación por el territorio nacional.', 'Derecho a la protección y conservación de un medio ambiente adecuado para el desarrollo de la persona.', 'Derecho a la libertad y seguridad.', 'C');`,
    `INSERT INTO "preguntas" VALUES (7, 'El derecho a la propiedad privada y a la herencia, recogido en la Constitución de 1978 ¿Qué clase de derecho es?', 'Derechos y deberes de la ciudadanía.', 'Derechos sociales.', 'Derechos fundamentales.', 'Deberes de la ciudadanía.', 'A');`,
    `INSERT INTO "preguntas" VALUES (8, '¿Qué artículo de la Constitución Española de 1978 reconoce el derecho a la protección de la salud?', 'Artículo 34.', 'Artículo 43.', 'Artículo 13.', 'Artículo 31.', 'B');`,
    `INSERT INTO "preguntas" VALUES (9, 'El derecho a la protección de la salud es un derecho esencial de todos los ciudadanos, que debe ser protegido, organizado y tutelado. De las siguientes afirmaciones, señala la que NO le corresponde:', 'Es un derecho social que garantizan los poderes públicos.', 'Sus principios, criterios sustantivos y acciones se regulan en la Ley 14/1986, General de Sanidad.', 'Es un derecho fundamental y goza de especial protección constitucional.', 'Establece el derecho a la protección de la salud y a la atención sanitaria.', 'C');`,
    `INSERT INTO "preguntas" VALUES (10, '¿Qué competencia del Estado en materia de sanidad se refiere al establecimiento de normas que fijen las condiciones y requisitos mínimos, persiguiendo una igualación básica de condiciones en el funcionamiento de los servicios sanitarios públicos?', 'Legislación sobre productos farmacéuticos.', 'Sanidad exterior y las relaciones y acuerdos sanitarios internacionales.', 'Ninguna respuesta es correcta.', 'Bases y coordinación general de la sanidad.', 'D');`,
    `INSERT INTO "preguntas" VALUES (11, '¿Qué ley fue la primera en contemplar el Consejo Interterritorial con carácter de órgano de coordinación?', 'Ley 29/2006, de 26 de julio.', 'Real Decreto 446/1994, de 11 de marzo.', 'Ley 14/1986 de 25 de abril.', 'Ley 16/2003, de 28 de mayo.', 'C');`,
    `INSERT INTO "preguntas" VALUES (12, 'A través del Instituto Nacional de Gestión Sanitaria (INGESA) la Administración Central del Estado mantiene la gestión de la sanidad en determinadas ciudades autónomas, ¿cuáles son?', 'La Administración Central del Estado ha trasferido la gestión sanitaria a todas las ciudades de España.', 'Bilbao, San Sebastián y Vitoria.', 'Ceuta y Melilla.', 'INGESA no gestiona la sanidad.', 'C');`,
    `INSERT INTO "preguntas" VALUES (13, 'El Estatuto de Autonomía es, según el texto constitucional "la norma institucional básica de la Comunidad Autónoma", ¿Qué artículo de la Constitución lo recoge?', 'Artículo 146.', 'Artículo 149.', 'Artículo 147.', 'Artículo 148.', 'C');`,
    `INSERT INTO "preguntas" VALUES (14, '¿Qué ley regula el Estatuto de Autonomía de Canarias en la actualidad?', 'Ley Orgánica 1/2018, de 5 de noviembre.', 'Ley Orgánica 4/1996, de 30 de diciembre.', 'Ley Orgánica 10/1982, de 10 de agosto.', 'Ninguna respuesta es correcta.', 'A');`,
    `INSERT INTO "preguntas" VALUES (15, 'De las siguientes afirmaciones sobre el Estatuto de Autonomía de Canarias, señala la que NO es veraz:', 'Los derechos y principios reconocidos por el Estatuto de Autonomía no supondrán ni una alteración del régimen de distribución de competencias, ni la creación de títulos competenciales nuevos o la modificación de los ya existentes.', 'Los nuevos Estatutos de Autonomía incorporan, con estructuras y formulaciones diversas, catálogos de derechos, deberes y principios rectores.', 'Los derechos que el Estatuto de Autonomía reconoce y garantiza a todos los ciudadanos deben ser respetados, tanto por la Administración Pública como por el resto de la población.', 'Todas las disposiciones del Estatuto pueden ser desarrolladas, aplicadas o interpretadas de forma que reduzcan o limiten los derechos fundamentales y reconocidos por la Constitución y por los tratados y convenios internacionales ratificados por España.', 'D');`,
    `INSERT INTO "preguntas" VALUES (16, 'La carta de derechos del Estatuto de Autonomía de Canarias, viene reconocida en el Capítulo II del Título I, ¿qué artículos comprende?', 'Del artículo 2 hasta el artículo 12.', 'Del artículo 12 hasta el artículo 35.', 'Del artículo 21 hasta el artículo 35.', 'Del artículo 11 hasta el artículo 35.', 'B');`,
    `INSERT INTO "preguntas" VALUES (17, 'En las disposiciones generales recogidas en el Capítulo I del Estatuto de Autonomía de Canarias y en concreto en el artículo 11, se enmarcan derechos con carácter general y programático. De los siguientes Derechos, señala el que pertenece al mencionado artículo:', 'Derecho a la igualdad entre mujeres y hombres.', 'Derecho a la voluntad anticipada.', 'Derecho a una renta de ciudadanía.', 'Derecho a la participación en la vida pública.', 'D');`,
    `INSERT INTO "preguntas" VALUES (18, 'El derecho a la salud en la Constitución de 1978 se encuentra recogido en el artículo 43. En el caso del Estatuto de Autonomía de Canarias, ¿qué artículo recoge el derecho a la protección de la salud?', 'Artículo 16.', 'Artículo 18.', 'Artículo 17.', 'Artículo 19.', 'D');`,
    `INSERT INTO "preguntas" VALUES (19, 'En las disposiciones generales recogidas en el Capítulo I del Estatuto de Autonomía de Canarias y en concreto en el artículo 11, se enmarcan derechos con carácter general y programático. De los siguientes derechos, señala el que NO pertenece al mencionado artículo:', 'Derecho a la igualdad y cooperación.', 'Derecho al desarrollo económico.', 'Derecho al respeto a los Derechos Humanos.', 'Derecho a la orientación sexual.', 'D');`,
    `INSERT INTO "preguntas" VALUES (20, 'A partir de la carta de derechos relacionados en el Estatuto de Autonomía de Canarias, se derivan una serie de deberes, tanto para la Administración y los poderes públicos como para el resto de la ciudadanía, contemplados en el Capítulo II del Título I. Señala el artículo que hace referencia al deber de información integral de servicios sociales y planes especializados:', 'Artículo 29.', 'Artículo 16.', 'Artículo 19.', 'Artículo 17.', 'A');`,
    `INSERT INTO "preguntas" VALUES (21, 'En el Preámbulo del Estatuto de Autonomía de Canarias se avanzan valores de carácter democrático y constitucional, que deben ser exigidos dentro del nuevo marco de actuación de toda la sociedad canaria y de sus poderes públicos, por lo que constituyen principios programáticos. De entre los siguientes principios, señala el que se considera programático:', 'El fomento de la cohesión económica, territorial y social del Archipiélago, garantizando la igualdad de derechos independientemente del lugar de residencia.', 'La defensa, promoción y estudio del español de Canarias, como variedad lingüística del español atlántico.', 'La definición del ámbito espacial de Canarias, con la reafirmación de las islas, de los cabildos y de sus municipios como entidades básicas dotadas de autonomía.', 'La promoción de Canarias como plataforma de paz y solidaridad.', 'C');`,
    `INSERT INTO "preguntas" VALUES (22, 'En el articulado del Estatuto de Autonomía de Canarias, concretamente en el artículo 37 (Capítulo III del Título I), se desarrollan los principios rectores de la política de los poderes públicos canarios. De los siguientes principios señala el que se enmarca en este desarrollo:', 'La erradicación de la sociedad canaria de actitudes sexistas, xenófobas, racistas, homófobas, bélicas o de cualquier otra naturaleza que atenten contra la igualdad y la dignidad de las personas.', 'El fortalecimiento de la cohesión de los canarios, facilitando, dentro del marco constitucional, su vocación como eslabón entre Europa, América y África, contribuyendo a la paz y a un orden internacional más justo.', 'La consolidación y mejora de la calidad de nuestro sistema democrático y de un progreso económico compatible con el excepcional patrimonio natural del archipiélago.', 'La relevancia política del régimen económico y fiscal, avalado por la Constitución, reforzando su garantía institucional y el establecimiento de mecanismos de coordinación en la modificación.', 'A');`,
    `INSERT INTO "preguntas" VALUES (23, 'La Constitución Española diseñó una organización territorial del Estado que posibilita la asunción por las comunidades autónomas de competencias en materia de sanidad. ¿En qué año se inició y terminó el proceso de transferencia de la asistencia sanitaria gestionada por el Instituto Nacional de la Salud (INSALUD)?', 'Se inició en 1978 y culminó en el año 2002.', 'Se inició en 1985 y culminó en el año 2002.', 'Se inició en 1981 y culminó en el año 2002.', 'Se inició en 1981 y culminó en el año 2000.', 'C');`,
    `INSERT INTO "preguntas" VALUES (24, '¿Qué órgano de coordinación sanitaria a nivel estatal se crea con la Ley 14/1986 de 25 de abril, General de Sanidad?', 'Consejo Interterritorial del Sistema Nacional de Salud.', 'Consejo de Salud de Zona.', 'Consejo Asesor de Atención a la Salud.', 'Consejo Territorial de Salud Pública.', 'A');`,
    `INSERT INTO "preguntas" VALUES (25, 'El Estatuto de Autonomía de Canarias regula los símbolos que definen su identidad, ¿Cuáles son?', 'Bandera, escudo, himno y festividad institucional.', 'Bandera.', 'Escudo e himno', 'Festividad institucional.', 'A');`,
    `INSERT INTO "preguntas" VALUES (26, 'La Comunidad Autónoma de Canarias asume la integridad de las competencias en materia de salud, sanidad y farmacia según un real decreto de transferencias. En el caso del Estatuto de Autonomía de Canarias, ¿dónde sitúa esta norma las competencias mencionadas?', 'Capítulo VII del Título V.', 'Capítulo VI del Título V.', 'Capítulo VII del Título IV.', 'Capítulo VII del Título VI.', 'A');`,
    `INSERT INTO "preguntas" VALUES (27, 'De conformidad con el artículo 1.2 de la Ley 14/1986, de 25 de abril, General de Sanidad, son titulares del derecho a la protección de la salud y a la atención sanitaria:', 'Todos los españoles y los ciudadanos extranjeros que tengan establecida su residencia en el territorio nacional.', 'Todos los extranjeros.', 'Todos los españoles.', 'Todos los españoles y los extranjeros.', 'D');`,
    `INSERT INTO "preguntas" VALUES (28, 'Tanto el Estado como las comunidades autónomas y las demás administraciones públicas competentes, organizarán y desarrollarán todas las acciones sanitarias a que se refiere el Título Preliminar de la Ley General de Sanidad...', 'Dentro de una concepción integral del sistema sanitario.', 'De acuerdo con los principios de eficacia, celeridad, economía y flexibilidad.', 'De manera que sea posible articular la participación comunitaria a través de las corporaciones territoriales correspondientes.', 'Dentro del marco de esta Ley y de sus respectivos estatutos de autonomía.', 'D');`,
    `INSERT INTO "preguntas" VALUES (29, 'El objeto de la Ley 14/1986, de 25 de abril, General de Sanidad, será:', 'La regulación general de todas las acciones que permitan hacer efectivo el derecho a la protección de la salud reconocido en el artículo 43 y concordantes de la Constitución.', 'La regulación general de todas las acciones que permitan hacer efectivo el derecho a la protección de la salud reconocido en el artículo 41 y concordantes de la Constitución.', 'La regulación general de todas las acciones que permitan hacer efectivo el derecho a la protección de la salud reconocido en el artículo 42 y concordantes de la Constitución.', 'La regulación general de todas las acciones que permitan hacer efectivo el derecho a la protección de la salud reconocido en el artículo 44 y concordantes de la Constitución.', 'B');`,
    `INSERT INTO "preguntas" VALUES (30, 'Los servicios sanitarios adecuarán su organización y funcionamiento a los principios de:', 'Eficacia, celeridad, economía y flexibilidad.', 'Eficacia, celeridad, economía y transparencia.', 'Eficacia, economía y flexibilidad.', 'Eficiencia, celeridad, economía y flexibilidad.', 'D');`,
    `INSERT INTO "preguntas" VALUES (31, 'Según el artículo 10 de la Ley General de Sanidad, serán derechos de todos frente a las distintas administraciones públicas sanitarias:', 'El respeto a la personalidad, dignidad humana e intimidad.', 'Obtener los medicamentos y productos sanitarios que se consideren necesarios para promover, conservar o restablecer su salud, en los términos que reglamentariamente se establezcan por las distintas administraciones autonómicas.', 'Todas las respuestas son correctas.', 'Elegir médico y los demás sanitarios titulados de acuerdo con las condiciones contempladas en la Constitución Española.', 'D');`,
    `INSERT INTO "preguntas" VALUES (32, 'La Ley 14/1986, de 25 de abril, General de Sanidad, tendrá la condición de norma básica en el sentido previsto en el artículo:', '149.1.16 de la Constitución.', '148.1.16 de la Constitución.', '149.1.19 de la Constitución.', '148.1.19 de la Constitución.', 'D');`,
    `INSERT INTO "preguntas" VALUES (33, 'De conformidad con el artículo 3.4 de la Ley General de Sanidad, los medios y actuaciones del sistema sanitario público estarán orientados prioritariamente:', 'A políticas estratégicas y programas de salud que integren activamente en sus objetivos y actuaciones el principio de igualdad entre hombres y mujeres.', 'A la superación de los desequilibrios territoriales y sociales.', 'A garantizar la igualdad de acceso a los servicios sanitarios públicos en todo el territorio español.', 'Todas las respuestas son correctas.', 'A');`,
    `INSERT INTO "preguntas" VALUES (34, 'Una vez superadas las posibilidades de diagnóstico y tratamiento de la atención primaria, los usuarios del Sistema Nacional de Salud tienen derecho:', 'En el marco de su área de salud, a ser atendidos en los servicios especializados hospitalarios.', 'En el marco de su centro de salud, a ser atendidos en los servicios especializados hospitalarios.', 'En el marco de su zona de salud, a ser atendidos en los servicios especializados hospitalarios.', 'En el marco de su sistema de salud, a ser atendidos en los servicios especializados hospitalarios.', 'A');`,
    `INSERT INTO "preguntas" VALUES (35, 'Todos los ciudadanos tienen los siguientes derechos con respecto a las distintas administraciones públicas sanitarias:', 'A responsabilizarse del uso adecuado de las prestaciones ofrecidas por el sistema sanitario.', 'A cuidar las instalaciones y colaborar en el mantenimiento de la habitabilidad de las instituciones sanitarias.', 'A la confidencialidad de toda la información relacionada con su proceso y con su estancia en instituciones sanitarias públicas y privadas que colaboren con el sistema público.', 'Todas las respuestas son correctas.', 'C');`,
    `INSERT INTO "preguntas" VALUES (36, 'Todos los ciudadanos tienen las siguientes obligaciones con respecto a las distintas administraciones públicas sanitarias:', 'Responsabilizarse del uso adecuado de las prestaciones ofrecidas por el sistema sanitario.', 'A participar, a través de las instituciones comunitarias, en las actividades sanitarias.', 'A utilizar las vías de reclamación y de propuesta de sugerencias en los plazos previstos.', 'Ninguna respuesta es correcta.', 'A');`,
    `INSERT INTO "preguntas" VALUES (37, 'De conformidad con el artículo 15.2 de la Ley General de Sanidad, ¿Quién acreditará servicios de referencia a los que podrán acceder todos los usuarios del Sistema Nacional de Salud, una vez superadas las posibilidades de diagnóstico y tratamiento de los servicios especializados de la comunidad autónoma donde residan?', 'El Consejo Interterritorial de Salud.', 'El Instituto de Atención Social y Sociosanitaria.', 'El Ministerio de Sanidad y Consumo.', 'Los poderes públicos.', 'C');`,
    `INSERT INTO "preguntas" VALUES (38, 'Integrarán el Sistema Nacional de Salud, según la Ley General de sanidad:', 'Todas las estructuras y servicios públicos al servicio de la sanidad.', 'Todas las estructuras, unidades y servicios públicos al servicio de la salud.', 'Todas las estructuras y servicios públicos al servicio de la salud.', 'Todas las estructuras, unidades y servicios públicos al servicio de la sanidad.', 'C');`,
    `INSERT INTO "preguntas" VALUES (39, 'Según la Ley 14/1986, los medios y actuaciones del sistema sanitario estarán orientadas prioritariamente:', 'A la superación de los desequilibrios interterritoriales y sociales', 'A la promoción de la salud y a la prevención de las enfermedades.', 'A la superación de los desequilibrios territoriales y sociales.', 'A la orientación de la salud y a la prevención de las enfermedades.', 'B');`,
    `INSERT INTO "preguntas" VALUES (40, 'La Ley General de Sanidad consta del siguiente número de artículos:', '113.', '112.', '122.', '121.', 'A');`,
    `INSERT INTO "preguntas" VALUES (41, '¿En qué artículo de la Ley de Ordenación Sanitaria de Canarias se recoge el objeto y alcance de la misma?', 'Artículo 2.', 'Artículo 3.', 'Artículo 1.', 'Artículo 4.', 'C');`,
    `INSERT INTO "preguntas" VALUES (42, '¿Qué tiene por objeto la Ley de Ordenación Sanitaria de Canarias?', 'La creación y organización del Servicio Canario de la Salud, su establecimiento y ordenación.', 'La creación del Sistema Canario de Salud.', 'La regulación general de cuantas actividades, servicios y prestaciones, públicas o privadas, determinan la efectividad del derecho constitucional a la protección de la salud.', 'Nombrar al personal sanitario.', 'A');`,
    `INSERT INTO "preguntas" VALUES (43, 'Según la Ley de Ordenación Sanitaria de Canarias, ¿Qué se entiende por Sistema Canario de la Salud?', 'El conjunto de las actividades, de los servicios y de las prestaciones desarrolladas por organizaciones públicas o privadas, en el territorio del Archipiélago Canario.', 'El conjunto de las actividades, de los servicios y de las prestaciones desarrolladas por organizaciones y personas, públicas o privadas, en el territorio del Archipiélago Canario.', 'El conjunto de las prestaciones que recibe cada usuario.', 'El conjunto de las actividades, de los servicios y de las prestaciones desarrolladas por organizaciones y personas, públicas o privadas, en el territorio del Archipiélago Canario, que funciona de una manera cooperativa y ordenada, conforme al Plan de Salud de Canarias.', 'D');`,
    `INSERT INTO "preguntas" VALUES (44, 'Según la Ley de Ordenación Sanitaria de Canarias, ¿sobre qué descansa el Sistema Canario de Salud?', 'Sobre la protección universal de la salud.', 'Sobre la protección integral y universal de la salud.', 'Sobre la protección integral de la salud.', 'Sobre la prevención y rehabilitación de la salud.', 'B');`,
    `INSERT INTO "preguntas" VALUES (45, 'Según la Ley de Ordenación Sanitaria de Canarias, el Plan de Salud es definido como:', 'El conjunto de las actividades, de los servicios y de las prestaciones desarrolladas por organizaciones y personas, públicas o privadas en el territorio del Archipiélago Canario.', 'Es un sistema de acciones, actividades y prestaciones desarrolladas por personas y organizaciones públicas y privadas que funcionan de manera cooperativa y coordinada.', 'Es el comité que evalúa el estado de las personas de la comunidad autónoma.', 'El instrumento estratégico para la planificación y coordinación y de articulación funcional de las actividades de promoción y protección de la salud, de prevención de la enfermedad y asistencia sanitaria.', 'D');`,
    `INSERT INTO "preguntas" VALUES (46, '¿Dónde se regula el Plan de Salud de Canarias?', 'Capítulo I del Título I de la Ley 11/1994, de Ordenación Sanitaria de Canarias.', 'Capítulo II del Título I de la Ley 11/1994, de Ordenación Sanitaria de Canarias.', 'Capítulo IV del Título I de la Ley 11/1994, de Ordenación Sanitaria de Canarias.', 'Capítulo III del Título I de la Ley 11/1994, de Ordenación Sanitaria de Canarias.', 'D');`,
    `INSERT INTO "preguntas" VALUES (47, 'Según la Ley de Ordenación Sanitaria de Canarias, el Servicio Canario de Salud se crea como:', 'Un organismo autónomo de carácter administrativo, dotado de personalidad jurídica propia y plena capacidad de obrar para el cumplimiento de sus fines.', 'Un organismo dependiente de la Consejería de Sanidad dotado con plena capacidad de obrar para el cumplimiento de sus fines.', 'Un organismo autónomo de carácter jurídico, dotado con plena capacidad de obrar para el cumplimiento de sus fines.', 'El órgano gestor de la salud de las personas más vulnerables.', 'A');`,
    `INSERT INTO "preguntas" VALUES (48, 'Según la Ley de Ordenación Sanitaria de Canarias, el Consejo de Dirección del Servicio Canario de Salud es:', 'El órgano autónomo adscrito al gobierno y administración del Servicio Canario de la Salud.', 'El órgano coordinador del gobierno y administración del Servicio Canario de la Salud.', 'El órgano superior de gobierno y administración del Servicio Canario de la Salud.', 'El órgano que coordina los diferentes servicios de salud.', 'C');`,
    `INSERT INTO "preguntas" VALUES (49, 'Según la Ley de Ordenación Sanitaria de Canarias, ¿cómo se organiza territorialmente el Servicio Canario de Salud?', 'Áreas de Salud.', 'Zonas de Salud Básicas.', 'Zonas de Salud Especializadas.', 'Áreas Hospitalarias.', 'A');`,
    `INSERT INTO "preguntas" VALUES (50, 'Según la Ley de Ordenación Sanitaria de Canarias, ¿qué se entiende por Consejo de Salud?', 'El órgano de asesoramiento, consulta y participación comunitaria en la gestión de la Zona Básica de Salud.', 'El órgano unipersonal de dirección del equipo de atención primaria del Servicio Canario de la Salud.', 'El órgano de consulta y participación comunitaria en la gestión de la Zona Básica de Salud.', 'El órgano del Servicio Canario de la Salud integrado por todos los profesionales del equipo de atención primaria.', 'A');`,
    `INSERT INTO "preguntas" VALUES (51, 'Según la Ley de Ordenación Sanitaria de Canarias, ¿en cuántas Áreas se organiza el Servicio Canario de Salud?', 'Cinco.', 'Ocho.', 'Siete.', 'Dos.', 'C');`,
    `INSERT INTO "preguntas" VALUES (52, 'Según la Ley de Ordenación Sanitaria de Canarias, ¿cuáles de los siguientes son órganos de gobierno y participación de las Áreas de Salud?', 'Consejo de Dirección y el Consejo de Salud.', 'Solo el Consejo de Salud.', 'La dirección de programas Asistenciales.', 'Consejo de Dirección, la Dirección y el Consejo de Salud.', 'D');`,
    `INSERT INTO "preguntas" VALUES (53, 'Según la Ley de Ordenación Sanitaria de Canarias, ¿Cuáles de los siguientes son órganos de administración y participación de los hospitales?', 'Dirección Gerencia y la Comisión de Participación Hospitalaria.', 'Dirección de la Red Hospitalaria de Utilización Pública.', 'Dirección de las Áreas de Salud.', 'Dirección de las Zonas Básicas de Salud.', 'A');`,
    `INSERT INTO "preguntas" VALUES (54, 'El Real Decreto 521/1987, de 15 de abril, sobre los Servicios de Atención al Paciente, ¿para qué tipo de asistencia sanitaria del INSALUD se creó?', 'Áreas de Salud.', 'Zonas Básicas de Salud.', 'Centros de Salud.', 'Hospitales.', 'D');`,
    `INSERT INTO "preguntas" VALUES (55, 'De las siguientes afirmaciones señala la que NO constituye un principio rector recogido en el Decreto 94/1999 de 25 de mayo:', 'Confidencialidad de la identidad y de los restantes datos del usuario sanitario', 'Gestionar y custodiar el Registro de Reclamaciones, Solicitudes, Sugerencias e Iniciativas de los Usuarios.', 'Respeto a la autonomía profesional.', 'Transparencia en sus actuaciones y participación.', 'B');`,
    `INSERT INTO "preguntas" VALUES (56, 'Comunicar al órgano competente del SCS la posible comisión de hechos o conductas que supongan incumplimiento de los derechos de los usuarios sanitarios reconocidos por la normativa sanitaria, a los efectos de la iniciación de los procedimientos disciplinarios, es una función de la ODDUSS, ¿dentro de qué área funcional?', 'Reclamaciones.', 'Sanciones.', 'Sugerencias.', 'Iniciativas.', 'A');`,
    `INSERT INTO "preguntas" VALUES (57, 'Los órganos administrativos del departamento competente en materia de sanidad, sus organismos autónomos de carácter sanitario, así como los centros, establecimientos y servicios sanitarios, públicos y privados, ¿ qué deber tienen para con la ODDUS?', 'Colaboración.', 'Subrogación.', 'Participación.', 'Todas las respuestas son correctas.', 'A');`,
    `INSERT INTO "preguntas" VALUES (58, 'Cuando la complejidad técnica del asunto sometido a la ODDUS así lo aconseje ¿Quién podrá requerir la asistencia técnica o el asesoramiento específico de cualquier órgano o unidad del departamento o del Servicio Canario de la Salud?', 'El Consejero competente en materia de sanidad.', 'El Jefe de la Oficina.', 'El consejero competente en materia de sanidad, directamente o a propuesta del Jefe de la Oficina.', 'Secretaría General Técnica de la Consejería de Sanidad.', 'C');`,
    `INSERT INTO "preguntas" VALUES (59, 'Respecto a la figura del Jefe de la Oficina (ODDUS), señala la respuesta que NO se corresponde:', 'Elaborará las propuestas de contestación de las reclamaciones y solicitudes y de las respuestas a las iniciativas y sugerencias que correspondan al titular del órgano del que dependa la Oficina.', 'Elaborará las propuestas de contestación de las reclamaciones y solicitudes y de las respuestas a las iniciativas y sugerencias que correspondan al titular del órgano del que dependa la Oficina.', 'Podrá dictar las instrucciones y órdenes de servicio que resulten necesarias.', 'Podrá ejercer todas las tareas/funciones recogidas en el artículo 7 del Decreto 94/1999, de 25 de mayo.', 'D');`,
    `INSERT INTO "preguntas" VALUES (60, '¿Dónde se presentan las reclamaciones, solicitudes, iniciativas y sugerencias de los usuarios sanitarios dirigidas al titular del departamento competente en materia de sanidad?', 'En los lugares y formas que, con carácter general, establezca en cada momento la legislación reguladora del Procedimiento Administrativo Común.', 'Todos los lugares descritos en el artículo 11 del Decreto 94/1999, de 25 de mayo.', 'En los centros o dependencias donde se ubique la oficina de registro de entrada de los órganos que integran el departamento, y el Servicio Canario de la Salud.', 'En los centros de atención primaria y en los servicios de atención al paciente o unidades similares habilitadas para atender al público de los centros, establecimientos y servicios hospitalarios integrados o vinculados a la Red Hospitalaria de Utilidad Pública.', 'B');`,
    `INSERT INTO "preguntas" VALUES (61, 'Contra las resoluciones dictadas en los expedientes tramitados, ¿ qué acciones se pueden emprender según el Decreto 94/1999, de 25 de mayo?', 'Recurso de alzada.', 'Recurso contencioso administrativo.', 'Recurso de reposición.', 'Ninguna respuesta es correcta.', 'D');`,
    `INSERT INTO "preguntas" VALUES (62, 'En la tramitación de las reclamaciones, a su solicitud, el centro, establecimiento o servicio sanitario afectado, ¿ qué plazo máximo tendrá para emitir un informe?', 'Diez días.', 'Treinta días.', 'Veinte días.', 'El plazo es mínimo no máximo.', 'C');`,
    `INSERT INTO "preguntas" VALUES (63, 'Qué contenidos puede tener la contestación a una reclamación a la vista de los resultados de la instrucción?', 'Todas las respuestas son correctas, incluyendo la remisión de lo actuado al órgano competente, por si se aprecian indicios de posibles infracciones del ordenamiento jurídico.', 'Una orden de adopción de medidas tendentes a la subsanación de la anomalía o anomalías detectadas.', 'Una recomendación tendente a la subsanación, en cuyo caso se fijará un plazo, en función de la naturaleza de aquella.', 'La incoación de expediente sancionador por posible violación de derechos o incumplimiento de deberes de los usuarios, sin perjuicio de la responsabilidad disciplinaria del personal autor.', 'A');`,
    `INSERT INTO "preguntas" VALUES (64, 'Las unidades de tramitación notificarán la contestación a los interesados, así como a los órganos del SCS que ejerzan competencia directa en el asunto objeto de reclamación. ¿Cuándo podrá ser sustituida la notificación por una comunicación verbal?', 'Cuando se haya aceptado por el usuario reclamante la fórmula de la conciliación.', 'Nunca podrá ser sustituida por una comunicación verbal.', 'Cuando se haya aceptado por el usuario reclamante la fórmula de la mediación.', 'Cuando se haya aceptado por el usuario reclamante la fórmula del arbitraje.', 'C');`,
    `INSERT INTO "preguntas" VALUES (65, 'La Memoria Anual de Actividades de la Unidad corresponde a la Oficina Central, ¿con qué antecedentes será necesario contar para su elaboración?', 'Los resúmenes trimestrales de actividad elaborados por las unidades funcionalmente dependiente de la oficina.', 'Los resúmenes mensuales de actividad elaborados por la Secretaria General Técnica.', 'Los resúmenes mensuales de actividad elaborados por las unidades funcionalmente dependientes de la Oficina.', 'Los resúmenes trimestrales de actividad elaborados por la Secretaría General Técnica.', 'C');`,
    `INSERT INTO "preguntas" VALUES (66, 'En España, la sanidad pública constituía desde el antiguo régimen una función pública atribuida a los entes municipales, ¿cómo se denominaba?', 'Policía de control y salubridad.', 'Policía de comodidad y salud.', 'Policía de comodidad y salubridad.', 'Policía de comorbilidad y salud.', 'C');`,
    `INSERT INTO "preguntas" VALUES (67, 'Con antelación a la Constitución Española, se produce una considerable dispersión del sistema público sanitario asistencial ante la multiplicidad de servicios. En el aspecto organizativo, ¿en qué año se crea el Ministerio de Sanidad?', '1977.', '1976.', '1975.', '1978.', 'A');`,
    `INSERT INTO "preguntas" VALUES (68, 'La distribución de competencias entre el Estado y las comunidades autónomas en lo que se refiere a la materia sanitaria está determinada por el "bloque de constitucionalidad". ¿Preferentemente por qué normativa está conformada?', 'Constitución Española y Ley General de Sanidad.', 'Constitución Española y los estatutos de autonomía.', 'Constitución Española y Ley de Ordenación Sanitaria.', 'Constitución Española y Real Decreto sobre traspaso a la Comunidad Autónoma de Canarias de las funciones y servicios del Instituto Nacional de la Salud.', 'B');`,
    `INSERT INTO "preguntas" VALUES (69, 'La Ley General de Sanidad en el artículo 38 establece lo que se entiende por sanidad exterior. De las siguientes afirmaciones al respecto, señala la incorrecta:', 'Son actividades de sanidad exterior todas aquellas que se realicen en materia de vigilancia y control de los posibles riesgos para la salud derivados de la importación, exportación o tránsito de mercancías y del tráfico internacional de viajeros.', 'El Ministerio de Sanidad y Consumo colaborará con otros departamentos para facilitar que las actividades de inspección o control de sanidad exterior sean coordinadas.', 'Las actividades y funciones de sanidad exterior se regularán por real decreto, a propuesta de los departamentos competentes.', 'No son competencia exclusiva del Estado la sanidad exterior ni las relaciones y acuerdos sanitarios internacionales.', 'D');`,
    `INSERT INTO "preguntas" VALUES (70, 'La sanidad exterior ha generado un importante fenómeno de cooperación sanitaria internacional, un ejemplo fue la creación de diversas instituciones intergubernamentales. Señala qué organizaciones o instituciones se crearon por esta razón:', 'Organización de las Naciones Unidas y el Reglamento Sanitario Internacional.', 'Centro de Control y Prevención de Enfermedades y la Organización Mundial de la Salud.', 'Organización Mundial de la Salud y la Agencia Europea para la Seguridad y la Salud en el Trabajo.', 'Organización Mundial de la Salud y el Reglamento Sanitario Internacional.', 'D');`,
    `INSERT INTO "preguntas" VALUES (71, 'Según el artículo 40 de la Ley General de Sanidad, ¿de quién es competencia la determinación, con carácter general, de los métodos de análisis y medición y de los requisitos técnicos y condiciones mínimas en materia de control sanitario del medio ambiente?', 'Comunidades autónomas.', 'Entidades locales.', 'Cabildos.', 'Estado.', 'D');`,
    `INSERT INTO "preguntas" VALUES (72, 'Según el artículo 73.1 de la Ley General de Sanidad, ¿Quién ejercerá la coordinación general sanitaria?', 'Cabildos.', 'Entidades locales.', 'Comunidades autónomas.', 'Consejo Interterritorial del Sistema Nacional de Salud.', 'D');`,
    `INSERT INTO "preguntas" VALUES (73, 'Supervisar que el ejercicio de las competencias en materia de sanidad se ajusta a criterios de participación democrática de todos los interesados es función del Estado, ¿en el ejercicio de qué competencia?', 'Alta inspección.', 'Coordinación sanitaria.', 'Planificación sanitaria.', 'Sanidad Exterior.', 'A');`,
    `INSERT INTO "preguntas" VALUES (74, 'Señala los títulos competenciales conexos atribuidos al Estado en materia sanitaria:', 'Sanidad y protección de los consumidores. Sanidad y las competencias sobre agricultura y ganadería. Sanidad y protección del medio ambiente.', 'Sanidad y seguridad social. Sanidad y asistencia social. Sanidad y orden público. Sanidad y protección de los consumidores. Sanidad y las competencias sobre agricultura y ganadería. Sanidad y protección del medio ambiente.', 'Sanidad y las competencias sobre agricultura y ganadería. Sanidad y protección del medio ambiente. Sanidad y seguridad social.', 'Sanidad y seguridad social. Sanidad y asistencia social. Sanidad y orden público. Sanidad y protección de los consumidores.', 'B');`,
    `INSERT INTO "preguntas" VALUES (75, '¿A quién corresponde la actividad de coordinación del sistema sanitario?', 'Al Gobierno de España.', 'A las comunidades autónomas con la colaboración de otras entidades públicas en materia de salud.', 'Al Gobierno de España con la colaboración de las comunidades autónomas.', 'Al Gobierno de España con la colaboración de los gobiernos europeos.', 'C');`,
    `INSERT INTO "preguntas" VALUES (76, 'La coordinación sanitaria tiene su manifestación orgánica en el Consejo Interterritorial del Sistema Nacional de Salud, previsto en el artículo 47 de la Ley General de Sanidad, integrado por:', 'Dos representantes de cada una de las comunidades autónomas y por igual número de miembros de la Administración del Estado.', 'Dos representantes de cada una de las comunidades autónomas y por un miembro de la Administración del Estado.', 'Un representante de cada una de las comunidades autónomas y por doble número de miembros de la Administración del Estado.', 'Un representante de cada una de las comunidades autónomas y por igual número de miembros de la Administración del Estado.', 'D');`,
    `INSERT INTO "preguntas" VALUES (77, 'La LGS prevé, en su artículo 49, que las comunidades autónomas organicen sus servicios de salud, uno por cada comunidad, que conforme un "sistema sanitario coordinado e integral" a través de demarcaciones territoriales, denominadas:', 'Áreas Especializadas.', 'Áreas Hospitalarias.', 'Áreas Sociosanitarias.', 'Áreas de Salud.', 'D');`,
    `INSERT INTO "preguntas" VALUES (78, '¿Por qué Ley se efectúa la concreción del alcance y contenido de la garantía institucional de la autonomía local (artículo 140 de la Constitución Española)?', 'Ley General de Sanidad.', 'Ley Reguladora de las Bases de Régimen Local de 1985.', 'Ley de Ordenación Sanitaria.', 'Ley 8/2015, de Cabildos Insulares.', 'B');`,
    `INSERT INTO "preguntas" VALUES (79, '¿Cuál de las siguientes es la Ley Canaria de Igualdad entre Mujeres y Hombres?', 'Ley Orgánica 3/2007, de 22 de marzo.', 'La Ley 16/2003, de 8 de abril.', 'Ley 1/2010, de 26 de febrero.', 'Ley 7/2019, de 9 de abril.', 'C');`,
    `INSERT INTO "preguntas" VALUES (80, 'De las siguientes leyes, identifica la que es de modificación de la Ley 1/2010, de 26 de febrero, Canaria de Igualdad entre Mujeres y Hombres, y de modificación de las leyes reguladoras de los órganos de relevancia estatutaria para garantizar la representación equilibrada entre mujeres y hombres en su composición.', 'Ley 7/2019, de 9 de abril.', 'Ley Orgánica 1/2018, de 5 de noviembre.', 'Ley Orgánica 3/2007, de 22 de marzo.', 'La Ley 16/2003, de 8 de abril.', 'A');`,
    `INSERT INTO "preguntas" VALUES (81, '¿Cuál es el objeto de la Ley 1/2010, de 26 de febrero?', 'El establecimiento y ordenación del sistema canario de prevención y protección integral de las mujeres contra la violencia de género.', 'Facilitar la mayor presencia de mujeres en los órganos de relevancia estatutaria, partiendo del mismo reconocimiento que hacen las vigentes leyes de igualdad sobre las limitaciones que aún persisten en el acceso de las mujeres en el ámbito laboral.', 'Hacer efectivo el derecho de igualdad de trato y de oportunidades entre mujeres y hombres, en particular, mediante la eliminación de la discriminación de la mujer, sea cual fuere su circunstancia o condición, en cualesquiera de los ámbitos de la vida y, singularmente, en las esferas política, civil, laboral, económica, social y cultural para, en desarrollo de los artículos 9.2 y 14 de la Constitución, alcanzar una sociedad más democrática, más justa, y más solidaria.', 'Hacer real y efectivo el derecho de igualdad de trato y oportunidades para, en el desarrollo de determinados artículos de la Constitución y del Estatuto de Autonomía para Canarias, seguir avanzando para lograr una sociedad más democrática, justa, solidaria e igualitaria, tanto en el ámbito público como privado.', 'D');`,
    `INSERT INTO "preguntas" VALUES (82, 'El artículo 2 de la Ley 1/2010, de 26 de febrero, Canaria de Igualdad entre Mujeres y Hombres, expone dónde y a quién se le aplica la ley. Cuando comenta su aplicación en las universidades canarias públicas y privadas, ¿en qué tipo de aplicación lo hace?', 'Aplicación en el ámbito territorial del archipiélago canario, así como a quien tenga la condición política de canario o canaria y resida en el exterior.', 'Aplicación particular y en los términos establecidos en la propia ley.', 'Aplicación a las personas físicas y jurídicas.', 'Aplicación al resto de los poderes públicos, a los órganos de relevancia estatutaria y al Consejo Económico y Social, en los términos establecidos en la presente ley y en sus leyes reguladoras.', 'B');`,
    `INSERT INTO "preguntas" VALUES (83, '¿En qué artículo de la Ley 1/2010, de 26 de febrero, Canaria de Igualdad entre Mujeres y Hombres se exponen los principios generales que informan la actuación de la Administración Pública?', 'Artículo 3.', 'Artículo 44.', 'Artículo 5.', 'Artículo 4.', 'D');`,
    `INSERT INTO "preguntas" VALUES (84, 'De los principios generales que informan la actuación de la Administración Pública en la Ley 1/2010, de 26 de febrero, ¿cuál es el que comporta aplicar la perspectiva de género en las fases de planificación, ejecución y evaluación de todas las políticas llevadas a cabo por las administraciones públicas, con la finalidad de eliminar las desigualdades entre mujeres y hombres?', 'La igualdad de trato entre mujeres y hombres, que supone la ausencia de toda discriminación, directa o indirecta, por razón de sexo.', 'El fomento de la corresponsabilidad, a través del reparto equilibrado entre mujeres y hombres de las responsabilidades familiares.', 'La adopción de las medidas necesarias para eliminar el uso sexista del lenguaje.', 'La transversalidad.', 'D');`,
    `INSERT INTO "preguntas" VALUES (85, 'Entre los principios generales que informan la actuación de la Administración Pública en la Ley 1/2010, de 26 de febrero, se encuentra la coordinación, entendida como la ordenada gestión de competencias entre las administraciones públicas en materia de igualdad entre mujeres y hombres. ¿Qué fin persigue?', 'Facilitar la coordinación de los distintos cuerpos y fuerzas policiales que operan en Canarias en el ejercicio de las funciones que legalmente les corresponden.', 'Garantizar y promover la utilización de una imagen de las mujeres y los hombres fundamentada en la igualdad de sexos, en todos los ámbitos de la vida pública y privada.', 'Evitar los efectos negativos en los derechos de las mujeres, y la consideración de la paternidad en un contexto familiar y social de corresponsabilidad, de acuerdo con los nuevos modelos de familia.', 'Lograr una mayor eficacia tanto en las políticas a aplicar como en la ejecución del presupuesto, donde se debe tender a su optimización y evitar la duplicidad de gastos para los mismos fines.', 'D');`,
    `INSERT INTO "preguntas" VALUES (86, 'En la Ley 1/2010 de 26 de febrero, y en concreto en el artículo 5.2 del Capítulo I, en referencia a los instrumentos necesarios para la integración de la perspectiva de género en las políticas públicas, se informa sobre dónde se debe aplicar la transversalidad de género. Señala la respuesta correcta sobre los aspectos a tener en cuenta para esta aplicación.', 'El fomento de la colaboración de todas las administraciones y sectores implicados y la consecución de la igualdad de oportunidades en la política económica, laboral y social, sin que se permitan diferencias salariales por razón de sexo.', 'Todos los proyectos de ley, disposiciones reglamentarias y planes que apruebe el Gobierno de Canarias incorporarán, de forma efectiva, el objetivo de la igualdad por razón de género.', 'El Gobierno de Canarias, a propuesta del organismo de igualdad autonómico, podrá incorporar criterios complementarios a seguir para la realización del informe de evaluación de género, así como aquellos otros derivados de la realidad canaria.', 'El Presupuesto de la Comunidad Autónoma de Canarias será un elemento activo y dinamizador en la consecución de forma efectiva del objetivo de la igualdad entre mujeres y hombres.', 'A');`,
    `INSERT INTO "preguntas" VALUES (87, '¿Qué documento realizado por el Instituto Canario de Igualdad se define como "instrumento director que articula y define las prioridades del Gobierno de Canarias para la implantación de la transversalidad de género y los métodos de trabajo para llevarla a cabo"?', 'El Presupuesto de la Comunidad Autónoma.', 'El informe de la Comisión de Impacto de Género', 'La Estrategia de Igualdad de Género.', 'Las recomendaciones de uso no sexista del lenguaje administrativo.', 'C');`,
    `INSERT INTO "preguntas" VALUES (88, 'Señala los instrumentos necesarios para la integración de la perspectiva de género en las políticas públicas según la Ley 1/2010 de 26 de febrero.', 'Transversalidad de género, evaluación del impacto de género, plan estratégico, programas operativos departamentales, enfoque de género en el presupuesto, lenguaje no sexista e imagen pública en medios de comunicación y publicidad, y estadísticas e investigaciones con perspectiva de género.', 'Transversalidad de género, evaluación del impacto de género, plan estratégico, programas operativos departamentales y enfoque de género en el presupuesto.', 'Plan estratégico, programas operativos departamentales, enfoque de género en el presupuesto.', 'Lenguaje no sexista e imagen pública en medios de comunicación y publicidad y estadísticas e investigaciones con perspectiva de género.', 'A');`,
    `INSERT INTO "preguntas" VALUES (89, 'La promoción de la igualdad de género por parte del Gobierno de Canarias que se desarrolla del artículo 12 al 14 de la Ley 1/2010 de 26 de febrero, nombra la contratación pública. ¿En qué términos se hace esta?', 'Mediante la representación equilibrada de hombres y mujeres en el nombramiento de titulares de órganos directivos de la Administración.', 'Mediante normas reguladoras de las subvenciones públicas indicadores para la valoración de actuaciones de efectiva consecución de la igualdad de género, salvo en aquellos casos en que esté justificada su no incorporación.', 'Mediante condiciones especiales en relación con la ejecución de los contratos que celebren, con el fin de promover la igualdad entre mujeres y hombres en el mercado de trabajo, siempre dentro del marco proporcionado por la normativa vigente.', 'Mediante la facilitación de propuesta de designación de la composición de género de cada organización, institución o entidad.', 'C');`,
    `INSERT INTO "preguntas" VALUES (90, '¿Cuál es la normativa legal de Medidas de Protección Integral contra la Violencia de Género que, mediante una respuesta global a la violencia que se ejerce sobre las mujeres, pretende atender a las recomendaciones de los organismos internacionales?', 'La Ley 16/2003, de 8 de abril.', 'Ley 1/2010, de 26 de febrero.', 'Ley Orgánica 3/2007, de 22 de marzo.', 'Ley Orgánica 1/2004, de 28 de diciembre.', 'D');`,
    `INSERT INTO "preguntas" VALUES (91, '¿Qué artículo de la Constitución incorpora el derecho de todos a la vida y a la integridad física y moral?', 'Artículo 15.', 'Artículo 51.', 'Artículo 43.', 'Articulo 34.', 'A');`,
    `INSERT INTO "preguntas" VALUES (92, '¿Qué parte de la Ley 16/2003, de 8 de abril, de prevención y protección integral de las mujeres contra la violencia de género, se centra en el ámbito sanitario?', 'Artículo 10, sobre obligaciones de los centros y servicios sanitarios y de servicios sociales y artículo 16. 1 sobre formación.', 'Artículo 45, sobre perspectiva de género en el Sistema Sanitario Público de Canarias y artículo 46 sobre investigación biomédica.', 'Artículo 47, sobre igualdad y perspectiva de género en la protección y promoción de la salud.', 'Sección 1. ª, sobre la promoción y protección de la salud.', 'A');`,
    `INSERT INTO "preguntas" VALUES (93, 'La Ley 1/2010, de 26 de febrero de Igualdad entre Mujeres y Hombres, hace referencia al ámbito sanitario en su Título II (la protección de la salud), Capítulo IV (medidas para promover la igualdad de género) Sección 1. ª, en donde se desarrollan las políticas de promoción y protección de la salud y de bienestar social. Señala en qué artículos:', 'Artículo 45 sobre perspectiva de género en el Sistema Sanitario Público de Canarias y artículo 46 sobre investigación biomédica.', 'Artículo 47 sobre igualdad y perspectiva de género en la protección y promoción de la salud.', 'Artículo 46. Investigación biomédica.', 'Articulado del 45 al 47.', 'D');`,
    `INSERT INTO "preguntas" VALUES (94, 'La Ley Orgánica 3/2018, de 5 de diciembre, de Protección de Datos Personales y garantía de los derechos digitales consta de, 97 artículos, 22 disposiciones adicionales, 6 disposiciones transitorias, 1 disposición derogatoria y 16 disposiciones finales. ¿Cuántos títulos contiene?', '15.', '20.', '10.', '25.', 'C');`,
    `INSERT INTO "preguntas" VALUES (95, '¿Qué artículo de la Constitución Española de 1978, garantiza el derecho al honor, a la intimidad personal y familiar y a la propia imagen?', 'Artículo 18.', 'Artículo 28.', 'Artículo 8.', 'Artículo 38.', 'A');`,
    `INSERT INTO "preguntas" VALUES (96, 'Después de la Constitución, ¿qué ley orgánica reguló la protección civil del derecho al honor, a la intimidad personal y familiar y a la propia imagen?', 'Ley Orgánica 3/2018, de 5 de diciembre.', 'Ley Orgánica 1/1996, de 15 de enero.', 'Ley Orgánica 5/2002, de 18 de noviembre.', 'Ley Orgánica 1/1982, de 5 de mayo.', 'D');`,
    `INSERT INTO "preguntas" VALUES (97, 'El objeto de la Ley Orgánica 3/2018, de 5 de diciembre, de Protección de Datos Personales y garantía de los Derechos Digitales, es adaptar el ordenamiento jurídico español en estos aspectos a la normativa europea, ¿a qué normativa europea en concreto?', 'Reglamento 1347/2000.', 'Reglamento 2016/679.', 'Reglamento 805/2004.', 'Reglamento 861/2007.', 'B');`,
    `INSERT INTO "preguntas" VALUES (98, 'En caso de contradicción entre el Reglamento Europeo de Protección de Datos y la normativa española, ¿Qué prevalece?', 'Ninguna respuesta es correcta.', 'La Ley Orgánica 3/2018, de 5 de diciembre.', 'La Ley Orgánica 1/1982, de 5 de mayo.', 'El Reglamento Europeo.', 'D');`,
    `INSERT INTO "preguntas" VALUES (99, 'La consideración de dato personal la va a tener cualquier información que nos permita relacionarla con una persona física concreta. ¿Qué aplicación material NO hace el Reglamento General de Protección de Datos respecto al tratamiento de ficheros y datos?', 'A los ficheros de datos informatizados.', 'A los datos que afectan a actividades no comprendidas en el ámbito de aplicación del Derecho de la Unión Europea.', 'A los datos personales contenidos en ficheros manuales', 'A los datos personales contenidos en ficheros públicos o privados, automatizados o no.', 'B');`,
    `INSERT INTO "preguntas" VALUES (100, '¿Qué define el Reglamento General de Protección de Datos como "todo conjunto estructurado de datos personales, accesibles con arreglo a criterios determinados, ya sea centralizado, descentralizado o repartido de forma funcional o geográfica"?', 'Elaboración de perfiles.', 'Tratamiento.', 'Fichero.', 'Datos personales.', 'C');`,
    `INSERT INTO "preguntas" VALUES (101, '¿Qué define el Reglamento General de Protección de Datos como "toda persona cuya identidad pueda determinarse, directa o indirectamente"?', 'Persona física.', 'Datos personales.', 'Datos del perfil.', 'Responsable del tratamiento.', 'A');`,
    `INSERT INTO "preguntas" VALUES (102, 'Qué principio del Reglamento (U.E.) 2016/679 se desarrolla como "toda manifestación de voluntad libre, específica, informada e inequívoca por la que se acepta, ya sea mediante una declaración o una clara acción afirmativa, el tratamiento de datos personales que le conciernen"?', 'Tratamiento basado en el consentimiento del afectado.', 'Deber de confidencialidad.', 'Exactitud de los datos.', 'Consentimiento de los menores de edad.', 'A');`,
    `INSERT INTO "preguntas" VALUES (103, '¿Cómo define el Reglamento (U.E.) 2016/679, el principio de tratamiento de datos por obligación legal, interés público o ejercicio de poderes públicos?', 'El fundado en el cumplimiento de una misión realizada en interés público o en el ejercicio de poderes públicos conferidos al responsable aunque no deriva de una competencia atribuida por una norma con rango de ley.', 'El fundado en la capacidad de garantizar la confidencialidad, integridad, disponibilidad y resiliencia permanentes de los sistemas y servicios de tratamiento.', 'El fundado en el cumplimiento de una misión realizada en interés público o en el ejercicio de poderes públicos conferidos al responsable, cuando derive de una competencia atribuida por una norma con rango de ley.', 'El fundado en el consentimiento y solo será lícito si consta el del titular de la patria potestad o tutela.', 'C');`,
    `INSERT INTO "preguntas" VALUES (104, 'El tratamiento de datos referidos a condenas e infracciones penales, procedimientos y medidas cautelares y de seguridad conexas, según el principio de tratamiento de datos de naturaleza penal que hace el RGPD, ¿Quién los podría llevar a cabo?', 'Procuradores que tengan por objeto recoger la información facilitada por los familiares y allegados de clientes para el ejercicio de sus funciones.', 'Abogados que tengan por objeto recoger la información facilitada por los familiares y allegados de clientes para el ejercicio de sus funciones.', 'Quienes actúen con fines de prevención, investigación, detección o enjuiciamiento de infracciones penales o de ejecución de sanciones penales.', 'Abogados y procuradores que tengan por objeto recoger la información facilitada por sus clientes para el ejercicio de sus funciones.', 'D');`,
    `INSERT INTO "preguntas" VALUES (105, '¿Qué normativa de aplicación al tratamiento de datos recoge el principio de licitud, lealtad y transparencia?', 'La Ley Orgánica 3/2018, de 5 de diciembre y el Reglamento General de Protección de Datos.', 'La Constitución Española de 1978 y artículo 8 de la Carta de Derechos Fundamentales de la Unión Europea.', 'El Convenio 108 del Consejo de Europa y el artículo 8 del Convenio Europeo para la Protección de los Derechos Humanos y de las Libertades Fundamentales.', 'La Constitución Española de 1978 y el Reglamento General de Protección de Datos.', 'A');`,
    `INSERT INTO "preguntas" VALUES (106, 'La afirmación siguiente "la información se debe poner a disposición de los interesados en el momento en que se soliciten los datos, previamente a la recogida o registro, si es que los datos se obtienen directamente del interesado", ¿a qué responde?', 'Al derecho de las personas a la transparencia e información al afectado.', 'Al principio de responsabilidad proactiva.', 'Al principio de licitud, lealtad y transparencia.', 'Al derecho de las personas a la transparencia y el derecho a ser informado.', 'D');`,
    `INSERT INTO "preguntas" VALUES (107, '¿A qué derecho recogido junto con el derecho de rectificación en el contenido del artículo 8 de la Carta de Derechos Fundamentales de la Unión Europea, se refiere cuando dice "es un elemento central del derecho a la protección de datos personales"?', 'El derecho de supresión.', 'El derecho a la limitación del tratamiento.', 'El derecho de acceso.', 'El derecho al olvido.', 'C');`,
    `INSERT INTO "preguntas" VALUES (108, '¿Cuál es el rango de jerarquía en el Ordenamiento Jurídico Español, de la Ley 16/2003, de 28 de mayo de Cohesión y Calidad del Sistema Nacional de Salud?', 'Ley ordinaria o básica.', 'Ley orgánica.', 'Segundo escalón, por debajo de la Constitución.', 'Decreto ley.', 'A');`,
    `INSERT INTO "preguntas" VALUES (109, 'La ley 16/2003 se estructura en: 79 artículos, 11 capítulos, 1 d. transitoria, 2 d. derogatorias y 6 d. finales. ¿Cuántas disposiciones adicionales tiene tras su modificación por el Real Decreto?', '11.', '12.', '9.', '10.', 'D');`,
    `INSERT INTO "preguntas" VALUES (110, 'De las siguientes afirmaciones sobre la Ley 16/2003, de 28 de mayo de Cohesión y Calidad del Sistema Nacional de Salud, señala la incorrecta:', 'Da un marco legal unificado a todo el Sistema Nacional de Salud que debe ser cumplido por todas las comunidades autónomas en sus respectivos servicios de la salud.', 'Define el SNS en los términos que lo hace el artículo 44, como el conjunto de los servicios de salud de la Administración del Estado y de los servicios de salud de las comunidades autónomas.', 'Profundiza en los sistemas de cooperación establecidos en la Ley General de Sanidad de 1986, los pormenoriza y los mejora, los aclara y los desarrolla.', 'Pretende que todos los ciudadanos españoles tengan los mismos derechos a la asistencia y a la protección de la salud en todas las partes del territorio nacional.', 'B');`,
    `INSERT INTO "preguntas" VALUES (111, 'Según el art. 3 de la Ley 16/2003, de 28 de mayo de Cohesión y Calidad del Sistema Nacional de Salud, la asistencia sanitaria en España, con cargo a fondos públicos, a través del Sistema Nacional de Salud, se garantizará a aquellas personas que ostenten la condición de:', 'Beneficiario.', 'Titular.', 'Perceptor de prestaciones.', 'Asegurado.', 'D');`,
    `INSERT INTO "preguntas" VALUES (112, 'Según el art. 1 de la Ley de Cohesión se debe establecer un marco legal para las acciones de coordinación y cooperación de las administraciones públicas sanitarias, en el ejercicio de sus respectivas competencias, de modo que se garantice:', 'La equidad, la calidad y la participación social en el Sistema Nacional de Salud, así como la colaboración activa de este en la reducción de las desigualdades en salud.', 'La prestación de los servicios a los usuarios del Sistema Nacional de Salud en condiciones de igualdad efectiva y calidad, evitando especialmente toda discriminación entre mujeres y hombres en las actuaciones sanitarias.', 'La igualdad de oportunidades y la libre circulación de los profesionales en el conjunto del Sistema Nacional de Salud.', 'La colaboración entre los servicios sanitarios públicos y privados en la prestación de servicios a los usuarios del Sistema Nacional de Salud.', 'A');`,
    `INSERT INTO "preguntas" VALUES (113, 'Según el art. 3 de la Ley 16/2003, de 28 de mayo de Cohesión y Calidad del Sistema Nacional de Salud, las personas que no tengan la condición de asegurado o de beneficiario del mismo podrán obtener la prestación de asistencia sanitaria mediante:', 'Pago de la correspondiente contraprestación.', 'Cuota derivada de la suscripción de un convenio especial.', 'Pago de la correspondiente contraprestación o cuota derivada de la suscripción de un convenio especial.', 'Pago subrogado del INSS.', 'C');`,
    `INSERT INTO "preguntas" VALUES (114, 'Según el art. 3 de la Ley 16/2003, de 28 de mayo de Cohesión y Calidad del Sistema Nacional de Salud, ¿a quién corresponde el reconocimiento y control de la condición de asegurado?', 'Consejo Interterritorial del Sistema Nacional de Salud.', 'Servicios de Salud de las comunidades autónomas.', 'Instituto Nacional de la Seguridad Social (INSS) o, en su caso, el Instituto Social de la Marina a través de sus direcciones provinciales.', 'Servicios de Salud de la Administración del Estado.', 'C');`,
    `INSERT INTO "preguntas" VALUES (115, 'Según la Ley 16/2003, de 28 de mayo de Cohesión y Calidad del Sistema Nacional de Salud, el derecho a la asistencia sanitaria se hará efectivo por las administraciones sanitarias competentes, que facilitarán el acceso de los ciudadanos a las prestaciones de asistencia sanitaria mediante la expedición de:', 'Cualquier medio que pueda acreditar los datos personales.', 'El pasaporte.', 'El documento nacional de identidad.', 'La tarjeta sanitaria individual.', 'D');`,
    `INSERT INTO "preguntas" VALUES (116, 'Según la Ley 16/2003, de 28 de mayo de Cohesión y Calidad del Sistema Nacional de Salud, los extranjeros no registrados ni autorizados como residentes en España, recibirán asistencia sanitaria en las siguientes modalidades:', 'De urgencia por enfermedad grave o accidente, cualquiera que sea su causa, hasta la situación de alta médica o de asistencia al embarazo, parto y postparto.', 'Solo en casos de urgencia por enfermedad grave o accidente, cualquiera que sea su causa, hasta la situación de alta médica.', 'Solo en casos de asistencia al embarazo, parto y postparto.', 'Según decisión del Médico de Familia.', 'A');`,
    `INSERT INTO "preguntas" VALUES (117, 'Según la Ley 16/2003, de 28 de mayo de Cohesión y Calidad del Sistema Nacional de Salud, ¿los extranjeros menores de dieciocho años recibirán asistencia sanitaria en las mismas condiciones que los españoles?', 'Sí, solo en caso de urgencia por enfermedad grave o accidente, cualquiera que sea su causa, hasta la situación de alta médica.', 'Sí, solo en casos de asistencia al embarazo, parto y postparto.', 'Sí, cualquiera que sea su causa.', 'No, en ningún caso.', 'C');`,
    `INSERT INTO "preguntas" VALUES (118, 'Las prestaciones sanitarias, la farmacia, los profesionales y la investigación, entre otras, son acciones a las que se refiere el artículo 1 de la Ley 16/2003 de 28 de mayo, de Cohesión y Calidad del Sistema Nacional de Salud. ¿A quién le corresponde su seguimiento?', 'Instituto Nacional de la Seguridad Social (INSS) o, en su caso, el Instituto Social de la Marina a través de sus direcciones provinciales.', 'Administración del Estado.', 'Servicio Canario de Salud.', 'Consejo Interterritorial y Alta Inspección.', 'D');`,
    `INSERT INTO "preguntas" VALUES (119, 'Según la Ley 16/2003, de 28 de mayo de Cohesión y Calidad del Sistema Nacional de Salud, el reconocimiento y control de la condición de asegurado o beneficiario del mismo corresponde a:', 'El Instituto Nacional de la Seguridad Social.', 'El Ministerio de Salud.', 'Las unidades administrativas de salud.', 'Los servicios públicos de salud.', 'A');`,
    `INSERT INTO "preguntas" VALUES (120, 'Según la Ley 16/2003, de 28 de mayo de Cohesión y Calidad del Sistema Nacional de Salud, la expedición del documento certificativo que acredite para recibir asistencia sanitaria con cargo a fondos públicos a una persona extranjera que, encontrándose en España, no tenga su residencia legal en el territorio español, corresponde a:', 'La Administración del Estado.', 'Los servicios públicos de salud.', 'Al Servicio Canario de Salud.', 'El Instituto Nacional de la Seguridad Social.', 'C');`,
    `INSERT INTO "preguntas" VALUES (121, 'Según la Ley 16/2003, de 28 de mayo de Cohesión y Calidad del Sistema Nacional de Salud, el reconocimiento y control del derecho a la atención sanitaria con cargo a fondos públicos de una persona española residente en España o de una persona extranjera, con residencia legal y habitual en el territorio español, corresponde a:', 'Los servicios públicos de salud.', 'La Administración del Estado.', 'El Instituto Nacional de la Seguridad Social.', 'El Servicio Canario de Salud.', 'B');`,
    `INSERT INTO "preguntas" VALUES (122, '¿Cuántos títulos tiene la Ley 44/2003, de 21 de noviembre, de Ordenación de las Profesiones Sanitarias?', '4.', '5.', '6.', '7.', 'C');`,
    `INSERT INTO "preguntas" VALUES (123, 'Señala la respuesta incorrecta sobre la Ley 44/2003, de 21 de noviembre, de Ordenación de las Profesiones Sanitarias:', 'Sus disposiciones son aplicables si la profesión se ejerce en los servicios sanitarios públicos, no así en el ámbito de la sanidad privada.', 'Regula los aspectos básicos de las profesiones sanitarias tituladas en lo que se refiere a su ejercicio por cuenta propia o ajena.', 'Describe la estructura general de la formación de los profesionales, el desarrollo profesional de estos y su participación en la planificación y ordenación de las profesiones sanitarias.', 'Establece los registros de profesionales que permitan hacer efectivo los derechos de los ciudadanos respecto a las prestaciones sanitarias y la adecuada planificación de los recursos humanos del sistema de salud.', 'A');`,
    `INSERT INTO "preguntas" VALUES (124, 'Según el art. 3 de la Ley 44/2003, de 21 de noviembre, de Ordenación de las Profesiones Sanitarias, ¿en qué grupos se estructuran las profesionales del área sanitaria de formación profesional?', 'De formación sanitaria y formación no sanitaria.', 'De grado superior y grado medio.', 'De grado sanitario y de grado no sanitario.', 'De grado superior y de grado inferior.', 'B');`,
    `INSERT INTO "preguntas" VALUES (125, 'Según el art. 4 de la Ley 44/2003, de 21 de noviembre, de Ordenación de las Profesiones Sanitarias, los profesionales sanitarios desarrollan, entre otras, funciones en los ámbitos:', 'Preferentemente asistencial, investigador y docente.', 'Preferentemente en la asistencial y en la prevención, información y educación sanitarias.', 'De gestión clínica, de prevención y de información y educación sanitarias.', 'Asistencial, investigador, docente, de gestión clínica, de prevención y de información y educación sanitarias.', 'D');`,
    `INSERT INTO "preguntas" VALUES (126, '¿Cómo se denomina el título primero de la Ley 44/2003, de 21 de noviembre, de Ordenación de las Profesiones Sanitarias?', 'Profesiones sanitarias tuteladas.', 'Principios generales.', 'Normas generales.', 'Del ejercicio de las profesiones sanitarias.', 'D');`,
    `INSERT INTO "preguntas" VALUES (127, 'Según el art. 6 de la Ley 44/2003, de 21 de noviembre, de Ordenación de las Profesiones Sanitarias, ¿A qué profesional sanitario le corresponde la indicación y realización de las actividades dirigidas a la promoción y mantenimiento de la salud, a la prevención de las enfermedades y al diagnóstico, tratamiento, terapéutica y rehabilitación de los pacientes, así como al enjuiciamiento y pronóstico de los procesos objeto de atención?', 'Los farmacéuticos.', 'Los dentistas.', 'Los médicos.', 'Los veterinarios.', 'C');`,
    `INSERT INTO "preguntas" VALUES (128, 'Según el art. 6 de la Ley 44/2003, de 21 de noviembre, de Ordenación de las Profesiones Sanitarias, ¿a qué profesional sanitario le corresponde la realización de las actividades dirigidas a la producción, conservación y dispensación de los medicamentos, así como la colaboración en los procesos analíticos, farmacoterapéuticos y de vigilancia de la salud pública?', 'Los farmacéuticos.', 'Los dentistas.', 'Los médicos.', 'Los veterinarios.', 'A');`,
    `INSERT INTO "preguntas" VALUES (129, 'Según el art. 6 de la Ley 44/2003, de 21 de noviembre, de Ordenación de las Profesiones Sanitarias, ¿a qué profesional sanitario le corresponde el control de la higiene y de la tecnología en la producción y elaboración de alimentos de origen animal, así como la prevención y lucha contra las enfermedades animales, particularmente las zoonosis, y el desarrollo de las técnicas necesarias para evitar los riesgos que en el hombre pueden producir la vida animal y sus enfermedades?', 'Los farmacéuticos.', 'Los dentistas.', 'Los médicos.', 'Los veterinarios.', 'D');`,
    `INSERT INTO "preguntas" VALUES (130, 'Según la Ley 44/2003, de 21 de noviembre, de Ordenación de las Profesiones Sanitarias, las profesiones correspondientes al nivel de Licenciado son:', 'Titulados en Enfermería y Fisioterapia.', 'Titulados en Medicina, Farmacia, Veterinaria y Odontología.', 'Titulados en Podología y Logopedia.', 'Titulados Oficiales de Especialista en Ciencias de la Salud.', 'B');`,
    `INSERT INTO "preguntas" VALUES (131, 'Según el art. 8 de la Ley 44/2003, de 21 de noviembre, de Ordenación de las Profesiones Sanitarias, los centros sanitarios deberán revisar que los profesionales sanitarios de su plantilla cumplen los requisitos necesarios para ejercer la profesión, ¿cada cuánto tiempo?', 'Cada cinco años como máximo.', 'Anualmente.', 'Cada 3 años como mínimo.', 'Bianual.', 'C');`,
    `INSERT INTO "preguntas" VALUES (132, 'Según el art. 11 de la Ley 44/2003, de 21 de noviembre, de Ordenación de las Profesiones Sanitarias, toda la estructura asistencial del sistema sanitario estará en disposición de ser utilizada para:', 'La docencia de los profesionales de las universidades públicas.', 'La investigación sanitaria exclusivamente.', 'La investigación sanitaria de la asociación de farmacéuticos.', 'La investigación sanitaria y para la docencia de los profesionales.', 'd');`,
    `INSERT INTO "preguntas" VALUES (133, 'Según el art. 17 de la Ley 44/2003, de 21 de noviembre, de Ordenación de las Profesiones Sanitarias, los títulos de especialista en Ciencias de la Salud serán expedidos por:', 'El Ministerio de Educación, Cultura y Deporte.', 'El Ministerio de Sanidad y Consumo.', 'El Ministerio de Sanidad, Servicios Sociales e Igualdad.', 'La Comisión Nacional de la Especialidad.', 'a');`,
    `INSERT INTO "preguntas" VALUES (134, 'Según el art. 12 de la Ley 44/2003, de 21 de noviembre, de Ordenación de las Profesiones Sanitarias, la actualización permanente de conocimientos, mediante la formación continuada, de los profesionales sanitarios, como un derecho y un deber de estos, es un:', 'Principio general sobre el ejercicio de las profesiones sanitarias.', 'Principio general sobre formación continuada.', 'Principio rector sobre la formación de los profesionales sanitarios.', 'Normal general del desarrollo profesional y su conocimiento.', 'c');`,
    `INSERT INTO "preguntas" VALUES (135, 'Al proceso de enseñanza y aprendizaje activo y permanente al que tienen derecho y obligación los profesionales sanitarios, que se inicia al finalizar los estudios de pregrado o de especialización y que está destinado a actualizar y mejorar los conocimientos, habilidades y actitudes de los profesionales sanitarios ante la evolución científica y tecnológica y las demandas y necesidades, tanto sociales como del propio sistema sanitario, se denomina:', 'Formación especializada.', 'Formación bonificada.', 'Formación profesional.', 'Formación continuada.', 'd');`,
    `INSERT INTO "preguntas" VALUES (136, 'Respecto al derecho a la protección frente a los riesgos laborales, señala la respuesta correcta:', 'Los empresarios tienen derecho a una protección eficaz en materia de seguridad y salud en el trabajo.', 'El derecho de paralización de la actividad en caso de riesgo probable no forma parte del derecho de los trabajadores a una protección eficaz en materia de seguridad y salud en el trabajo.', 'El empresario deberá cumplir las obligaciones establecidas en la normativa sobre prevención de riesgos laborales.', 'El coste de las medidas relativas a la seguridad y la salud en el trabajo podrá recaer sobre los trabajadores.', 'c');`,
    `INSERT INTO "preguntas" VALUES (137, 'Los principios de la acción preventiva por parte del empresario NO incluyen:', 'Adoptar medidas que antepongan la protección individual a la colectiva.', 'Tener en cuenta la evolución de la técnica.', 'Adaptar el trabajo a la persona.', 'Evaluar los riesgos que no se puedan evitar.', 'a');`,
    `INSERT INTO "preguntas" VALUES (138, 'En el caso de que se haya producido un daño para la salud de los trabajadores, ¿Quién se debe encargar de llevar a cabo una investigación al respecto, a fin de detectar las causas de estos hechos?', 'El Comité de Seguridad y Salud.', 'El empresario.', 'El trabajador o los trabajadores afectados.', 'Los delegados de personal o los representantes de los trabajadores.', 'b');`,
    `INSERT INTO "preguntas" VALUES (139, 'El empresario deberá analizar las posibles situaciones de emergencia y adoptar las medidas necesarias en determinadas materias. ¿En qué materia carece de competencia?', 'Lucha contra incendios.', 'Evacuación de los trabajadores.', 'Primeros auxilios.', 'Riesgo sanitario.', 'd');`,
    `INSERT INTO "preguntas" VALUES (140, 'Respecto a la información médica de carácter personal, señala la respuesta que NO es correcta:', 'Se limitará al personal médico y a las autoridades sanitarias que lleven a cabo la vigilancia de la salud de los trabajadores.', 'En ningún caso podrá facilitarse al empresario.', 'El empresario y las personas u órganos con responsabilidades en materia de prevención serán informados de las conclusiones que se deriven de los reconocimientos efectuados.', 'Las medidas de vigilancia y control de la salud de los trabajadores se llevarán a cabo por personal sanitario con competencia técnica, formación y capacidad acreditada.', 'b');`,
    `INSERT INTO "preguntas" VALUES (141, 'Indica qué NO se incluye en el plan de prevención elaborado por el empresario:', 'Recursos necesarios para realizar la acción de prevención.', 'Las responsabilidades en materia de prevención.', 'Evaluación de los riesgos para la seguridad y la salud en el trabajo.', 'Estructura organizativa de la empresa.', 'c');`,
    `INSERT INTO "preguntas" VALUES (142, '¿Qué deberá hacer el empresario respecto a los daños para la salud de sus trabajadores producidos con motivo del desarrollo de su trabajo?', 'Deberá comunicarlo por escrito a la autoridad laboral.', 'Deberá comunicarlo verbalmente a la autoridad laboral.', 'Deberá comunicarlo a la autoridad laboral si así se establece en el plan de prevención de la empresa.', 'No está obligado a comunicarlo.', 'a');`,
    `INSERT INTO "preguntas" VALUES (143, 'En las empresas de trabajo temporal, ¿Quién será responsable del cumplimiento de las obligaciones en materia de formación y vigilancia de la salud?', 'La empresa usuaria.', 'La empresa de trabajo temporal.', 'La empresa usuaria junto con la empresa de trabajo temporal.', 'El trabajador.', 'b');`,
    `INSERT INTO "preguntas" VALUES (144, '¿Por quién está formado el Comité de Seguridad y Salud?', 'Por los trabajadores y el empresario.', 'Por los delegados de prevención, de una parte, y los delegados sindicales en número igual, de la otra.', 'Por los delegados de prevención, de una parte, y por el empresario o sus representantes en número igual al de los delegados de prevención, de la otra.', 'Por los delegados de prevención, de una parte, y los representantes del empresario en menor proporción, de la otra.', 'c');`,
    `INSERT INTO "preguntas" VALUES (145, '¿De quién es competencia ejercer una labor de vigilancia y control sobre el cumplimiento de la normativa de prevención de riesgos laborales?', 'El Comité de Seguridad y Salud.', 'La Inspección de Trabajo y Seguridad Social.', 'Los delegados de prevención.', 'El empresario.', 'c');`,
    `INSERT INTO "preguntas" VALUES (146, '¿Quién establecerá las limitaciones a la contratación de jóvenes menores de 18 años en trabajos que presenten riesgos específicos?', 'El Ministerio de Trabajo.', 'El Gobierno.', 'La Comisión Nacional de Seguridad y Salud en el Trabajo.', 'La autoridad laboral.', 'b');`,
    `INSERT INTO "preguntas" VALUES (147, '¿Quién adoptará las normas de funcionamiento del Comité de Seguridad y Salud?', 'Los delegados de prevención.', 'El propio Comité.', 'El empresario o sus representantes.', 'Los servicios de prevención.', 'b');`,
    `INSERT INTO "preguntas" VALUES (148, '¿Qué Decreto regula la adaptación de la normativa estatal sobre prevención de riesgos laborales en el ámbito de la Administración Pública de la Comunidad Autónoma de Canarias y sus organismos autónomos?', 'Decreto 304/2011, de 11 de octubre.', 'Decreto 168/2009, de 29 de diciembre.', 'Decreto 201/2018, de 21 de noviembre.', 'Decreto 17/2009, de 21 de enero.', 'b');`,
    `INSERT INTO "preguntas" VALUES (149, 'Las especialidades o disciplinas preventivas con que cuenten los servicios de prevención de riesgos laborales serán desarrolladas por un número suficiente de expertos. ¿Qué condiciones reúnen estos?', 'No se hace uso de expertos.', 'La capacitación requerida para el desempeño de tales funciones, y con los recursos necesarios, según la normativa vigente en materia de riesgos laborales.', 'La necesaria coordinación, cooperación y colaboración con los órganos directivos, organismos y entidades locales.', 'El apoyo, colaboración y cooperación en orden a la realización de las funciones encomendadas a los servicios de prevención de riesgos laborales.', 'b');`,
    `INSERT INTO "preguntas" VALUES (150, 'Ley 55/2003, de 16 de diciembre, Estatuto Marco del Personal Estatutario de los Servicios de Salud cuenta con 14 capítulos, ¿Cuántos artículos se distribuyen en ellos?', '70.', '60.', '80.', '90.', 'c');`,
    `INSERT INTO "preguntas" VALUES (151, 'El Estatuto Marco del Personal Estatutario deroga el régimen estatutario configurado preconstitucionalmente por los tres estatutos de personal que existían, ¿Cuáles eran?', 'Todas las respuestas son correctas.', 'Estatuto de personal médico.', 'Estatuto de personal sanitario no facultativo.', 'Estatuto de personal no sanitario.', 'a');`,
    `INSERT INTO "preguntas" VALUES (152, 'Señala de entre las respuestas la que No corresponde con un criterio de clasificación del personal estatutario de los servicios de salud:', 'A la función desarrollada.', 'Al nivel del título exigido para el ingreso.', 'Al tipo de su nombramiento.', 'A la durabilidad de la permanencia.', 'd');`,
    `INSERT INTO "preguntas" VALUES (153, 'La Ley 55/2003, de 16 de diciembre, Estatuto Marco del Personal Estatutario de los Servicios de Salud, tiene por objeto:', 'Establecer las bases reguladoras de la relación funcionarial general del personal estatutario de los servicios de salud que conforman el Sistema Nacional de Salud.', 'Establecer las bases reguladoras de la relación funcionarial especial del personal estatutario de los servicios de salud que conforman el Sistema Nacional de Salud.', 'Normalizar las bases reguladoras de la relación funcionarial especial del personal estatutario de los servicios de salud que conforman el Sistema Nacional de Salud.', 'Normalizar las bases reguladoras de la relación funcionarial general del personal estatutario de los servicios de salud que conforman el Sistema Nacional de Salud.', 'b');`,
    `INSERT INTO "preguntas" VALUES (154, 'Según el art. 2 de la Ley 55/2003, de 16 de diciembre, Estatuto Marco del Personal Estatutario de los Servicios de Salud, es aplicable al personal estatutario que desempeña su función en:', 'Centros sanitarios de los Servicios de Salud de las comunidades autónomas.', 'Centros y servicios sanitarios de la Administración General del Estado.', 'Centros y servicios sanitarios de la Administración Exterior del Estado', 'Centros e instituciones sanitarias de los servicios de salud de las comunidades autónomas o centros y servicios sanitarios de la Administración General del Estado.', 'd');`,
    `INSERT INTO "preguntas" VALUES (155, 'Lo previsto en la Ley 55/2003, de 16 de diciembre, Estatuto Marco del Personal Estatutario de los Servicios de Salud, será de aplicación:', 'Al personal sanitario funcionario que preste servicios en los centros del Sistema Nacional de Salud.', 'Al personal sanitario laboral que preste servicios en los centros del Sistema Nacional de Salud.', 'Al personal sanitario laboral que preste servicios en los centros sanitarios de titularidad privada.', 'Al personal sanitario funcionario y al personal sanitario laboral que preste servicios en los centros del Sistema Nacional de Salud.', 'd');`,
    `INSERT INTO "preguntas" VALUES (156, 'En desarrollo de la normativa básica contenida en la Ley 55/2003, de 16 de diciembre, Estatuto Marco del Personal Estatutario de los Servicios de Salud, aprobarán los estatutos y las demás normas aplicables al personal estatutario de cada servicio de salud:', 'Las comunidades autónomas en el ámbito de sus respectivas competencias.', 'El Estado, las comunidades autónomas y las entidades locales, en el ámbito de sus respectivas competencias.', 'Las comunidades autónomas y las entidades locales, en el ámbito de sus respectivas competencias.', 'El Estado y las comunidades autónomas, en el ámbito de sus respectivas competencias.', 'd');`,
    `INSERT INTO "preguntas" VALUES (157, 'En el Capítulo I de la Ley 55/2003, de 16 de diciembre, del Estatuto Marco del personal estatutario de los servicios de salud, se regula:', 'Clasificación del personal estatutario.', 'Planificación y ordenación del personal.', 'Normas generales.', 'Derechos y deberes.', 'c');`,
    `INSERT INTO "preguntas" VALUES (158, 'En el Capítulo II de la Ley 55/2003, de 16 de diciembre, del Estatuto Marco del Personal Estatutario de los Servicios de Salud, es personal estatutario de gestión y servicios quien ostenta tal condición en virtud de:', 'Nombramiento expedido para el desempeño de funciones de gestión o para el desarrollo de profesiones u oficios que no tengan carácter sanitario.', 'Nombramiento expedido para el ejercicio de una profesión o especialidad sanitaria.', 'Nombramiento por razones de necesidad, de urgencia o para el desarrollo de programas de carácter temporal, coyuntural o extraordinario.', 'Nombramientos para el personal transferido de la administración Local.', 'a');`,
    `INSERT INTO "preguntas" VALUES (159, 'Según el Capítulo VI, de la Ley 55/2003, de 16 de diciembre, del Estatuto Marco del Personal Estatutario de los Servicios de Salud, la provisión de plazas del personal estatutario en la promoción y movilidad del personal de los servicios de salud se regirá por los principios básicos de:', 'Igualdad, mérito, capacidad y publicidad.', 'Igualdad, mérito y capacidad.', 'Igualdad y publicidad.', 'Mérito, capacidad y publicidad.', 'a');`,
    `INSERT INTO "preguntas" VALUES (160, 'Según el Capítulo VI, de la Ley 55/2003, de 16 de diciembre, del Estatuto Marco del Personal Estatutario de los Servicios de Salud, la selección del personal estatutario fijo se efectuará, con carácter periódico, en el ámbito que en cada servicio de salud se determine, a través de convocatoria pública y mediante procedimientos que garanticen los principios constitucionales de:', 'Igualdad, mérito y capacidad, así como el de publicidad.', 'Colaboración, cooperación y coordinación, así como el de competencia.', 'Igualdad, mérito y capacidad, así como el de competencia.', 'Colaboración, cooperación y coordinación, así como el de publicidad.', 'c');`,
    `INSERT INTO "preguntas" VALUES (161, 'Según el Capítulo VI, de la Ley 55/2003, de 16 de diciembre, del Estatuto Marco del Personal Estatutario de los Servicios de Salud, la selección del personal estatutario fijo se efectuará con carácter general a través del sistema de:', 'Oposición.', 'Concurso.', 'Libre designación.', 'Concurso-oposición.', 'd');`,
    `INSERT INTO "preguntas" VALUES (162, 'Según el Capítulo VI, de la Ley 55/2003, de 16 de diciembre, del Estatuto Marco del Personal Estatutario, en el ámbito de cada servicio de salud se regulará la composición y funcionamiento de los órganos de selección, que serán de naturaleza colegiada y actuarán de acuerdo con criterios de:', 'Coordinación, cooperación, transparencia y eficacia.', 'Objetividad, imparcialidad, transparencia y eficacia.', 'Objetividad, imparcialidad, agilidad y eficacia.', 'Objetividad, cooperación, agilidad y eficacia.', 'c');`,
    `INSERT INTO "preguntas" VALUES (163, 'Según el Capítulo VII, de la Ley 55/2003, de 16 de diciembre, del Estatuto Marco del Personal Estatutario, por necesidades del servicio, y cuando una plaza o puesto de trabajo se encuentre vacante o temporalmente desatendido, podrá ser cubierto en comisión de servicios, con carácter temporal, por:', 'Personal estatutario de cualquier categoría y especialidad.', 'Personal estatutario de la correspondiente categoría y especialidad.', 'Personal estatutario de la correspondiente categoría y cualquier especialidad.', 'Personal estatutario de cualquier categoría y la correspondiente especialidad.', 'b');`,
    `INSERT INTO "preguntas" VALUES (164, 'Según la Ley de Incompatibilidades, ¿Quiénes NO quedan incluidos en lo que se considera sector público?', 'Los diputados autonómicos.', 'El personal de las entidades colaboradoras y también el personal de las entidades concertadas de la Seguridad Social.', 'Los expresidentes autonómicos.', 'Los altos cargos y restante personal al servicio de las corporaciones locales.', 'c');`,
    `INSERT INTO "preguntas" VALUES (165, 'Respecto a los principios generales de la Ley 543/1984, señala la respuesta correcta:', 'El personal comprendido en el ámbito de aplicación de la Ley 53/1984 no podrá compatibilizar sus actividades con el desempeño, por sí o mediante sustitución, de un segundo puesto de trabajo, cargo o actividad en el sector público, salvo en los supuestos previstos en la misma.', 'El personal comprendido en el ámbito de aplicación de la ley 53/1984 no podrá compatibilizar sus actividades con el desempeño, por sí o mediante sustitución, de un segundo puesto de trabajo, cargo o actividad en el sector público o privado, salvo en los supuestos previstos en la misma.', 'El personal comprendido en el ámbito de aplicación de la ley 53/1984 no podrá compatibilizar sus actividades con el desempeño, por sí o mediante sustitución, de un segundo o tercer puesto de trabajo, cargo o actividad en el sector público, salvo en los supuestos previstos en la misma.', 'El personal comprendido en el ámbito de aplicación de la ley 53/1984, no podrán compatibilizar sus actividades con el desempeño, por sí o mediante sustitución, de un segundo puesto de trabajo, cargo o actividad en el sector público, salvo en los supuestos previstos en la misma y en la normativa de desarrollo.', 'a');`,
    `INSERT INTO "preguntas" VALUES (166, 'Respecto al ámbito de aplicación de la ley 53/1984 de 26 de diciembre, señala la respuesta incorrecta:', 'El restante personal al que resulte de aplicación el régimen estatutario de los funcionarios públicos.', 'El personal al servicio del Banco de España y de las instituciones financieras públicas.', 'El personal al servicio de entidades, corporaciones de derecho público, fundaciones y consorcios cuyos presupuestos se doten ordinariamente en más de un 50 por cien con subvenciones u otros ingresos procedentes de las administraciones públicas.', 'El personal que preste servicios en empresas en que la participación del capital, directamente, de las administraciones públicas sea superior al 50 por 100.', 'd');`,
    `INSERT INTO "preguntas" VALUES (167, 'Respecto a los principios generales de la ley 53/1984 de 26 de diciembre, señala la respuesta incorrecta:', 'A los solos efectos de la Ley 53/1984 se considerará actividad en el sector público la desarrollada por los miembros electivos de las Asambleas Legislativas de las Comunidades Autónomas y de las Corporaciones Locales.', 'A los solos efectos de la Ley 53/1984 se considerará actividad en el sector público la desarrollada por los altos cargos y restante personal órganos constitucionales y de todas las administraciones públicas, incluida la Administración de Justicia.', 'Todas las respuestas son correctas.', 'A los solos efectos de la Ley 53/1984 se considerará actividad en el sector público la desarrollada por los altos cargos y restante personal de los órganos constitucionales y de todas las administraciones públicas, y de los entes, organismos y empresas de ellas dependientes, exceptuando las entidades colaboradoras y las concertadas de la Seguridad Social en la prestación sanitaria.', 'd');`,
    `INSERT INTO "preguntas" VALUES (168, 'La Ley 53/1984, de 26 de diciembre de Incompatibilidades del Personal al Servicio de las Administraciones Públicas consta de:', 'Tres disposiciones finales.', 'Cinco disposiciones adicionales.', 'Dos disposiciones derogatorias', 'Cinco disposiciones transitorias.', 'a');`,
    `INSERT INTO "preguntas" VALUES (169, 'Será requisito necesario para autorizar la compatibilidad de actividades públicas el que la cantidad total percibida por ambos puestos o actividades no supere la remuneración prevista en los Presupuestos Generales del Estado para el cargo de:', 'Secretario de Estado.', 'Ministro.', 'Director General.', 'Subsecretario.', 'c');`,
    `INSERT INTO "preguntas" VALUES (170, 'Entre las normas específicas sobre incompatibilidades que recoge el artículo 77, del Estatuto Marco, se encuentra:', 'La percepción de pensión de jubilación parcial será compatible con las retribuciones derivadas de una actividad a tiempo parcial.', 'El personal médico podrá ejercer cualquier actividad privada con independencia de su puesto de trabajo y jornada en los Servicios de Salud y sin necesidad de solicitar la incompatibilidad.', 'El personal estatutario de las instituciones sanitarias no podrá bajo ningún concepto ejercer la actividad privada.', 'El personal de gestión y servicios puede tener otro puesto de trabajo en otra administración pública, siempre y cuando solo en uno de los puestos tenga el turno de tarde.', 'a');`,
    `INSERT INTO "preguntas" VALUES (171, '¿En razón de qué o de quién, según la Ley 53/1984, se efectuará la autorización de compatibilidad?', 'Interés del solicitante.', 'Interés de la Administración.', 'Interés público.', 'Todas las respuestas son correctas', 'c');`,
    `INSERT INTO "preguntas" VALUES (172, 'Por excepción, el personal incluido en el ámbito de aplicación de la Ley 53/1984, ¿con el desempeño de qué cargos electivos podrá compatibilizar sus actividades?', 'Miembro del Consejo de Ministros.', 'Secretario de Estado.', 'Alcalde en régimen de dedicación exclusiva.', 'Diputado autonómico.', 'd');`,
    `INSERT INTO "preguntas" VALUES (173, 'El principio fundamental de la dedicación del personal al servicio de las administraciones públicas a un solo puesto de trabajo, de la Ley 53/1984, viene a cumplimentar, en esta materia, el mandato, ¿de qué artículos de la Constitución?', 'Artículos 103.4 y 149.2.18ª.', 'Artículos 102.3 y 149.1.18ª.', 'Artículos 103.3 y 149.1.18ª.', 'Artículos 103.3 y 148.1.18ª.', 'c');`,
    `INSERT INTO "preguntas" VALUES (174, 'El personal comprendido en el ámbito de aplicación de la Ley 53/1984 de 26 de diciembre, solo podrá desempeñar un segundo puesto de trabajo o actividad en el sector público en los supuestos previstos en la misma, ¿para qué funciones?', 'Docente y representativa.', 'Docente y sanitaria.', 'Sanitaria y representativa.', 'Docente, sanitaria y representativa', 'b');`,
    `INSERT INTO "preguntas" VALUES (175, 'Respecto al ámbito de aplicación de la Ley 53/1984 de 26 de diciembre, de Incompatibilidades del Personal al Servicio de las Administraciones Públicas, señala la respuesta incorrecta:', 'El personal civil y militar al servicio de la Administración del Estado y de sus organismos públicos.', 'El personal al servicio de las corporaciones locales y de los organismos de ellas dependientes.', 'El personal al servicio de entes y organismos públicos incluidos en la aplicación de la Ley de Entidades Estatales Autonómicas.', 'El personal al servicio de las administraciones de las comunidades autónomas y de los organismos de ellas dependientes, así como de sus asambleas legislativas y órganos institucionales.', 'c');`,
    `INSERT INTO "preguntas" VALUES (176, '¿Qué término utiliza la Ley respecto a la necesidad de obtener consentimiento para el desarrollo de una segunda actividad pública?', 'Reconocimiento.', 'Declaración.', 'Consentimiento.', 'Autorización.', 'd');`,
    `INSERT INTO "preguntas" VALUES (177, 'Según la ley 41/2002, de 14 de noviembre, Básica Reguladora de la Autonomía del Paciente, una de las siguientes afirmaciones es incorrecta, señálala:', 'Según la normativa vigente el paciente tiene derecho de acceso al contenido íntegro de su historia clínica.', 'Según la normativa vigente el paciente tiene derecho de acceso al contenido integro de su historia clínica, con ciertas limitaciones.', 'El acceso de un tercero a la historia clínica, motivado por un riesgo para su salud, se limitará a los datos pertinentes.', 'El derecho de acceso del paciente a la historia clínica puede ejercerse también por representación debidamente acreditada.', 'a');`,
    `INSERT INTO "preguntas" VALUES (178, 'Según la Ley 41/2002, de 14 de noviembre, ¿quién regula el procedimiento para que quede constancia del acceso a la historia clínica y su correspondiente uso?', 'Los Centros de Salud', 'El Estado.', 'La Administración y gestión de los centros sanitarios.', 'Las comunidades autónomas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (179, 'Conforme al artículo 17 de la Ley 41/2002, de 14 de noviembre, Básica Reguladora de la Autonomía del Paciente, la obligación de conservar la documentación clínica en condiciones que garanticen su correcto mantenimiento y seguridad, corresponde a:', 'Auxiliares administrativos.', 'Al personal de gestión y servicios.', 'Los/as trabajadores sociales.', 'Al centro sanitario.', 'd');`,
    `INSERT INTO "preguntas" VALUES (180, 'Según la ley 41/2002, de 14 de noviembre, Básica Reguladora de la Autonomía del Paciente, se otorgará el consentimiento por representación en determinados supuestos. Señala la respuesta que no corresponda:', 'Cuando el paciente menor de edad no sea capaz intelectual ni emocionalmente de comprender el alcance de la intervención.', 'Cuando el paciente esté incapacitado legalmente.', 'Cuando el paciente no sea capaz de tomar decisiones, a criterio del médico responsable de la asistencia, o su estado físico o psíquico no le permita hacerse cargo de su situación.', 'Cuando se trate de menores no incapaces ni incapacitados, pero emancipados o con dieciséis años cumplidos.', 'd');`,
    `INSERT INTO "preguntas" VALUES (181, 'En qué artículo de la Ley 41/2002, de 14 de noviembre, Básica Reguladora de la Autonomía del Paciente se trata el derecho a la intimidad?', 'Artículo 7.', 'Artículo 27.', 'Artículo 22.', 'Artículo 17.', 'a');`,
    `INSERT INTO "preguntas" VALUES (182, 'La Ley 41/2002, Básica Reguladora de la Autonomía del Paciente, recoge que se otorgará el consentimiento por escrito en determinados casos. Señala la respuesta incorrecta:', 'En la aplicación de procedimientos que supongan riesgos o inconvenientes de notoria y previsible repercusión negativa sobre la salud del paciente.', 'Procedimientos diagnósticos y terapéuticos invasores.', 'Siempre.', 'Intervenciones quirúrgicas.', 'c');`,
    `INSERT INTO "preguntas" VALUES (183, 'Según la Ley Básica Reguladora de la Autonomía del Paciente y de Derechos y Obligaciones en Materia de Información y Documentación Clínica, ¿cómo será la conformidad del paciente a que se refiere el consentimiento informado?', 'Libre, voluntaria y por escrito.', 'Voluntaria e inconsciente.', 'Libre y voluntaria.', 'Meticulosa, libre y voluntaria.', 'c');`,
    `INSERT INTO "preguntas" VALUES (184, '¿Cuándo se conservará la documentación clínica, según la Ley 41/2002?', 'Todas son correctas.', 'Cuando existan razones de investigación.', 'Cuando existan razones de organización y funcionamiento del Sistema Nacional de Salud.', 'Cuando existan razones epidemiológicas.', 'a');`,
    `INSERT INTO "preguntas" VALUES (185, 'La renuncia del paciente a recibir información podrá verse limitada según la Ley 41/2002, de 14 de noviembre. Señala, al respecto, la respuesta correcta:', 'Por las exigencias terapéutica del caso.', 'Todas las respuestas son correctas.', 'Por el interés de la salud de la colectividad.', 'Por el interés de la salud del propio paciente.', 'b');`,
    `INSERT INTO "preguntas" VALUES (186, 'Qué tipo de consentimiento es el que se define según la Ley 41/2002 como el que se presume de acuerdo con lo que conocemos de una persona particular?', 'Implícito.', 'Presunto.', 'Expreso.', 'Tácito.', 'b');`,
    `INSERT INTO "preguntas" VALUES (187, 'Señala la respuesta correcta sobre las instrucciones previas de cada persona:', 'No se aplicarán cuando sean contrarias a la "lex artis".', 'Todas las respuestas son correctas.', 'Podrá designar un representante para que, llegado el caso, sirva como interlocutor suyo con la familia o el equipo sanitario.', 'Podrán constar por escrito.', 'a');`,
    `INSERT INTO "preguntas" VALUES (188, 'Bajo la denominación "Instrucciones Previas", ¿Qué artículo de la Ley Básica 41/2002, de 14 de noviembre, encuadrado en su Capítulo IV, regula la manifestación anticipada de voluntad?', 'Artículo 10.', 'Artículo 11.', 'Artículo 8.', 'Artículo 12.', 'b');`,
    `INSERT INTO "preguntas" VALUES (189, 'Son principios de la ley 39/2006, de 14 de diciembre, de Autonomía Personal y Atención a las Personas en Situación de Dependencia:', 'El carácter público de las prestaciones del Sistema para la Autonomía y Atención a la Dependencia.', 'La transversalidad de las políticas de atención a las personas en situación de dependencia.', 'La atención a las personas en situación de dependencia de forma integral e integrada.', 'Todas son correctas.', 'a');`,
    `INSERT INTO "preguntas" VALUES (190, '¿Cuáles de las siguientes se consideran características fundamentales de la ley 39/2006, de 14 de diciembre, de Autonomía personal y Atención a las personas en Situación de Dependencia?', 'Establece un nuevo derecho social.', 'Homogeneiza el procedimiento y las técnicas de valoración.', 'Implanta un catálogo de servicios y prestaciones.', 'Todas son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (191, 'Los beneficios, en la financiación del Sistema:', 'No participarán en la financiación', 'No participarán directamente en la financiación, pero si indirectamente, a través de las aportaciones al régimen general o especial de seguridad social.', 'Aportarán como regla general el 10% del valor económico de la prestación, salvo que acrediten insuficiencia de recursos.', 'Participarán en su financiación en función de su capacidad económica, no quedando privados de la atención por falta de medios económicos.', 'd');`,
    `INSERT INTO "preguntas" VALUES (192, 'La Ley 39/2006, de 14 de diciembre, de Autonomía Personal y Atención a las Personas en Situación de Dependencia, establece:', 'Un nivel mínimo de protección definido y garantizado financieramente por la Administración General del Estado.', 'Un segundo nivel de protección, acordado y desarrollado mediante un régimen de cooperación y financiación entre la Administración General del Estado y las comunidades autónomas a través de convenios para el desarrollo y aplicación de las demás prestaciones y servicios que se contemplan en la Ley.', 'Un tercer nivel adicional de protección a los ciudadanos, si así lo estiman las comunidades autónomas.', 'Todas son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (193, 'Según la Ley 39/2006, de 14 de diciembre, de Autonomía Personal y Atención a las Personas en Situación de Dependencia, las ayudas económicas para facilitar la autonomía personal tendrán la condición de subvención e irán destinadas:', 'A apoyar a la persona con ayudas técnicas y a facilitar la accesibilidad y adaptaciones en el hogar.', 'A apoyar a la persona con ayudas técnicas.', 'A facilitar la accesibilidad y adaptaciones en el hogar.', 'Ninguna es correcta.', 'a');`,
    `INSERT INTO "preguntas" VALUES (194, 'Según la Ley de Dependencia, cuando la persona necesita ayuda para realizar varias actividades básicas de la vida diaria al menos una vez al día, o tiene necesidades de apoyo intermitente o limitado para su autonomía personal, nos encontramos ante una persona con:', 'Grado I. Dependencia moderada.', 'Grado II. Dependencia severa.', 'Grado III. Gran dependencia.', 'Esta persona no se encuentra en situación de dependencia.', 'a');`,
    `INSERT INTO "preguntas" VALUES (195, 'Según la Ley de Dependencia, el baremo de las actividades de la vida diaria es aplicable en:', 'Cualquier situación de discapacidad y en cualquier edad, a partir de los 3 años.', 'Cualquier situación de discapacidad y a partir de los 18 años de edad.', 'Cualquier situación de discapacidad sin distinción de sexo y edad.', 'Cualquier situación de discapacidad a partir de los 65 años de edad.', 'a');`,
    `INSERT INTO "preguntas" VALUES (196, 'La Ley de Dependencia señala en relación con la red de servicios del Sistema para la Autonomía y Atención a la Dependencia, que:', 'Las prestaciones y servicios establecidos se integran en la Red de los Centros de Referencia Estatal del Gobierno Central.', 'Ninguna es correcta.', 'Las prestaciones y servicios establecidos se integran en la Red de los Centros de Referencia Estatal del IMSERSO.', 'Las prestaciones y servicios establecidos se integran en la Red de Servicios Sociales de las respectivas comunidades autónomas en el ámbito de las competencias que las mismas tienen asumidas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (197, 'En Canarias, el Sistema de Atención a las Personas en Situación de Dependencia se lleva a cabo de forma coordinada con:', 'Las administraciones locales.', 'Las administraciones insulares que son las competentes de la red de recursos sociosanitarios de cada isla.', 'Las administraciones locales e insulares.', 'Las administraciones insulares, locales y el tercer sector.', 'b');`,
    `INSERT INTO "preguntas" VALUES (198, 'En el marco del Sistema para la Autonomía y Atención a la Dependencia, corresponden a las comunidades autónomas, sin perjuicio de las competencias que les son propias según la Constitución Española, los Estatutos de Autonomía y la legislación vigente, las siguientes funciones:', 'Establecer los procedimientos de coordinación sociosanitaria, creando, en su caso, los órganos de coordinación que procedan para garantizar una efectiva atención.', 'Crear y actualizar el Registro de Centros y Servicios, facilitando la debida acreditación que garantice el cumplimiento de los requisitos y los estándares de calidad.', 'Asegurar la elaboración de los correspondientes programas individuales de atención.', 'Todas son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (199, 'El Sistema para la Autonomía y Atención a la Dependencia tiene como uno de sus objetivos clave proporcionar y asegurar unos servicios y prestaciones de calidad en la atención a las personas en situación de dependencia. ¿Cuáles de los siguientes acuerdos se han tenido que adoptar en el ámbito del Consejo Territorial?', 'Fijación de criterios comunes de acreditación de centros y planes de calidad e indicadores de calidad para la evaluación.', 'Fijación de criterios comunes de acreditación de centros y planes de calidad, Indicadores de calidad para la evaluación, guías de buenas prácticas y cartas de servicios, adaptadas a las condiciones específicas de las personas dependientes, bajo los principios de no discriminación y accesibilidad.', 'Guías de buenas prácticas.', 'Cartas de servicios, adaptadas a las condiciones específicas de las personas dependientes, bajo los principios de no discriminación y accesibilidad.', 'b');`,
    `INSERT INTO "preguntas" VALUES (200, 'Según la Ley de Dependencia, ¿Cuándo se aplica la compensación económica por cuidados en el entorno familiar?', 'En caso de que no se disponga de la oferta pública de servicios que requiera el beneficiario en función de su grado y nivel de dependencia, se procederá al conocimiento de una prestación económica vinculada para que la persona pueda adquirir el servicio en el mercado privado.', 'Siempre que se den las circunstancias familiares y las condiciones adecuadas del hogar el beneficiario podrá optar por ser atendido en su entorno familiar, y su cuidador/a recibirá una compensación económica por ello.', 'Cuando su objetivo es contribuir a la contratación de un asistente personal, durante un número de horas, que facilite al beneficiario el acceso a la educación y al trabajo, así como una vida más autónoma en el ejercicio de las actividades básicas de la vida diaria.', 'Ninguna es correcta.', 'a');`,
    `INSERT INTO "preguntas" VALUES (201, '¿En qué siglo surge el Trabajo Social con Casos como intento de superar las medidas paliativas existentes hasta el momento?', 'XVIII.', 'XXI.', 'XX.', 'XIX.', 'd');`,
    `INSERT INTO "preguntas" VALUES (202, 'En 1917, ¿Qué autor/a, publicó Diagnóstico Social, una obra emblemática basada en sus amplios conocimientos sobre diversos aspectos sociales, estableciendo una teoría y un método, el "casework "o "trabajo social con casos"?', 'Gordon Hamilton.', 'Helen Harris Perlman.', 'Florence Hollis.', 'Mary Richmond.', 'd');`,
    `INSERT INTO "preguntas" VALUES (203, 'Señala la respuesta que define o describe "el caso social", dentro del trabajo social de caso:', 'El caso es la situación social o problema concreto no la persona o personas implicadas.', 'Todas las respuestas son correctas.', 'El caso social es un acontecimiento vital en el que siempre hay proporciones, variables de lo económico, lo físico, lo mental, lo emocional y de los factores sociales.', 'El caso social está compuesto por factores internos, externos o medioambientales.', 'b');`,
    `INSERT INTO "preguntas" VALUES (204, 'Hamilton considera como un factor importante la participación del cliente en el proceso de ayuda. El trabajo a realizar no es resolver los problemas del cliente, sino ayudarle a poner en marcha sus potencialidades y capacidades para que los resuelva por sí mismo. Según este autor ¿Cómo debe ser considerado por el trabajador/a social el caso- problema-cliente?', 'Proceso ecosocial.', 'Proceso prosocial.', 'Proceso psicosocial.', 'Proceso macrosocial.', 'c');`,
    `INSERT INTO "preguntas" VALUES (205, 'Qué autor/es define las técnicas e instrumentos utilizados en el trabajo social de casos como "el conjunto de habilidades, destrezas y recursos adquiridos mediante aprendizaje y práctica profesional, que permite al trabajador social realizar la intervención para obtener el resultado deseado".', 'Francisco Gómez Gómez.', 'Teresa Zamanillo.', 'Santa Lázaro', 'Fernández García y Ponce de León.', 'd');`,
    `INSERT INTO "preguntas" VALUES (206, 'El Consejo General de Trabajo Social recalca que el documento historia social presenta una serie de características. Señala la opción que la describe.', 'Es un instrumento vivo y por tanto abierto en el proceso de trabajo.', 'Tiene una fecha de caducidad en sus contenidos y en los documentos que incluye.', 'Es un instrumento propio del trabajador social.', 'Todas las respuestas son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (207, '¿Qué apreciaciones hace Dolors Colom sobre el diagnóstico social?', 'No es una clasificación.', 'Todas las respuestas son ciertas.', 'No es una descripción de problemas en una lista.', 'Cualquier descripción generalista e imprecisa no puede tomarse como el diagnóstico social.', 'b');`,
    `INSERT INTO "preguntas" VALUES (208, 'Los objetivos del informe social son :', 'Estudio, conocimiento y valoración de una situación social individual o grupal con el fin de tratarla para modificar o paliar dicha situación.', 'Aportar información para poder elaborar un plan de intervención.', 'Facilitar el trabajo multidisciplinar e interdisciplinar y posibilitar a los usuarios el acceso a recursos.', 'Todas las opciones son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (209, '¿Qué parte del modelo general básico del informe social hace referencia a, "las gestiones o acciones realizadas por el profesional del trabajo social y otros recursos técnicos e institucionales, las alternativas y medidas de intervención sugeridas o recomendadas, el dictamen y orientación final y el compromiso de las partes"?', 'Valoración técnico-profesional: diagnóstico, pronóstico.', 'Propuesta de intervención.', 'Evaluación, seguimiento y temporalización.', 'Derivación y contrato', 'b');`,
    `INSERT INTO "preguntas" VALUES (210, '¿Qué documento básico del trabajo social posibilita la realización de estudios e investigaciones que aportan información suficiente para establecer comparaciones sobre el tipo de demandas presentadas, los recursos y sobre las características de las personas que las realizan?', 'Ficha social.', 'Informe social.', 'Proyecto de intervención social.', 'Historia social.', 'a');`,
    `INSERT INTO "preguntas" VALUES (211, '¿Qué consigue un contrato como herramienta o documento del trabajo social?', 'La movilización del asistido y desalienta la dependencia.', 'Permite medir el camino recorrido y evaluar resultados.', 'Da seguridad y permite obtener éxitos rápidos.', 'Todas las respuestas son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (212, '¿ Cuál es el documento que muestra el diseño de intervención social y que comprende una evaluación diagnóstico de la situación y personas con quienes actuar, una determinación de objetivos operativos, actividades y tareas, utilización de recursos, temporalización y criterios de evaluación?', 'Historia social.', 'Informe social.', 'Ecomapa.', 'Proyecto de intervención social.', 'd');`,
    `INSERT INTO "preguntas" VALUES (213, 'La valoración e intervención como complemento a la valoración psicosocial medirá:', 'La percepción que tiene el afectado de su salud antes y después del trasplante.', 'La manera en que afecta la situación de salud a la dimensión social y emocional.', 'El uso de instrumentos validados que permiten hacer una medición genérica de la salud percibida.', 'Todas son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (214, 'La implantación del Protocolo de Valoración psicosocial con personas enfermas pre y post trasplantadas se encuentra regulada por:', 'Instrucción 7/99 del Director del Servicio Canario de la Salud.', 'Instrucción 03/06 del Director de Servicio Canario de la Salud.', 'Ley 30/1979, de 27/10/1979, de extracción y trasplante de órganos.', 'Ley 11/1994, de 26 de julio, de Ordenación Sanitaria de Canarias.', 'a');`,
    `INSERT INTO "preguntas" VALUES (215, '¿Cuáles de los siguientes instrumentos son preceptivos en el protocolo de intervención psicosocial con personas enfermas pre y post-trasplantadas?', 'Informe de Valoración Psicosocial y cuestionario Euroqol de salud autopercibida.', 'Consentimiento Informado y Notificación de incidencias.', 'Informe de seguimiento psicosocial e informe de seguimiento post trasplante.', 'Todas son correctas, si además se incluye el informe de alta hospitalaria.', 'd');`,
    `INSERT INTO "preguntas" VALUES (216, '¿Cuál de los siguientes criterios NO es considerado de inclusión o exclusión en la lista de espera para el trasplante?', 'La gravedad del paciente.', 'Existencia de compatibilidad del tejido u órgano.', 'La intervención psicosocial.', 'Todas son correctas.', 'c');`,
    `INSERT INTO "preguntas" VALUES (217, '¿ A qué profesional de su Zona Básica de Salud deriva el servicio de coordinación de trasplante a los pacientes que se encuentren en lista de espera para efectuarles la valoración de los factores psicosociales que inciden en su situación ?', 'Médico/a familia.', 'Enfermero/a.', 'Trabajador/a Social.', 'Nefrólogo/a.', 'c');`,
    `INSERT INTO "preguntas" VALUES (218, '¿ De cuánto tiempo dispone el trabajador/a social tras habérsele notificado el caso, para remitir su valoración psicosocial debidamente cumplimentada al Servicio de Coordinación de Trasplantes?', '1 mes como máximo.', '15 días como máximo.', '20 días como máximo.', '2 meses como mínimo.', 'a');`,
    `INSERT INTO "preguntas" VALUES (219, '¿ A qué profesional del Servicio Canario de Salud se le reclamará el Informe de seguimiento Psicosocial?', 'Al Trabajador/a Social de ZBS.', 'Al Trabajador/a Social de Atención Hospitalaria.', 'Al Trabajador/a Social del Centro de Atención Especializada.', 'Al Trabajador/a Social de Salud Pública.', 'a');`,
    `INSERT INTO "preguntas" VALUES (220, 'Tras alta hospitalaria de un paciente trasplantado y transcurrido 6 meses, ¿ Qué documento cumplimentará el Trabajador Social de Atención Primaria?', 'Informe de Alta Hospitalaria.', 'Informe de seguimiento y nuevamente el Cuestionario SF 36.', 'El Test de Gijón.', 'El Índice de Katz.', 'b');`,
    `INSERT INTO "preguntas" VALUES (221, '¿ Qué Servicio es el responsable de la difusión del protocolo de intervención psicosocial de personas enfermas pre y post-trasplantadas?', 'Servicio de Coordinación de Trasplantes.', 'Direcciones Gerencias de Hospitales.', 'Gerencias de Atención Primaria.', 'Gerencias de Servicios Sanitarios.', 'a');`,
    `INSERT INTO "preguntas" VALUES (222, 'El Protocolo de Coordinación de la Actuación con personas enfermas pre y post trasplantadas y sus familias en la Comunidad Canaria, abarca:', 'Centros de Atención Primaria de las Zonas Básicas de Salud de la Comunidad Canaria', 'Atención Especializada.', 'Asociaciones y Fundaciones de personas Enfermas.', 'Todas las respuestas son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (223, '¿A qué profesional del equipo de la zona básica de salud corresponde cumplimentar el cuestionario SF 36?', 'Médico/a.', 'Enfermero/a.', 'Trabajador/a Social.', 'Cualquiera de los profesionales mencionados.', 'd');`,
    `INSERT INTO "preguntas" VALUES (224, '¿ Qué actuación debe realizar el servicio de coordinación de trasplantes con el cuestionario SF 36?', 'Devolver el Cuestionario corregido e interpretado a la ZBS.', 'Remitirlo al Servicio de Asistencia Social del Hospital trasplantador.', 'Archivar el Cuestionario corregido e interpretarlo.', 'Devolver el Cuestionario corregido e interpretado a la ZBS y remitirlo al Servicio de Asistencia Social del Hospital trasplantador correspondiente.', 'd');`,
    `INSERT INTO "preguntas" VALUES (225, 'Cuando las y los trabajadores sociales aseguramos que distintas partes se comunican entre sí, buscando alcanzar acuerdos aceptables para todos, realizamos un tipo de intervención que se desempeña desde la atención directa al paciente. ¿Cuál es?', 'Recontextualizar.', 'Mediar/Facilitar.', 'Negociar.', 'Prestar asistencia práctica.', 'b');`,
    `INSERT INTO "preguntas" VALUES (226, 'El trabajo social es una disciplina que se dedica al estudio y transformación de una realidad social determinada. Cuando el profesional actúa desde el Área de Atención Directa utiliza y sigue como procedimiento un método. ¿Cuáles son las etapas de este método?', 'Diagnóstico social, planificación, intervención social, ejecución y evaluación.', 'Estudio de factores sociales, implementación, intervención social, ejecución y reevaluación.', 'Análisis de la demanda, estudio de factores sociales, diagnóstico social, intervención social y evaluación.', 'Análisis de la demanda, diagnóstico social, planificación, ejecución y reevaloración.', 'c');`,
    `INSERT INTO "preguntas" VALUES (227, 'Las Estaciones Clínicas de DRAGO registran la información cínica de los pacientes y permiten la consolidación y consulta de todos los datos y documentos que constituyen las Historias Clínicas en formato digital. De los sistemas DRAGO que existen ¿ cuáles se han interconectado?', 'Entre Hospitales y Atención Primaria', 'Entre hospitales.', 'Entre hospitales y CAES', 'Entre CAES y atención primaria', 'b');`,
    `INSERT INTO "preguntas" VALUES (228, 'El proceso de aprendizaje que se emplea para ayudar a los usuarios a dominar comportamientos que les hacen vulnerables al aislamiento o la exclusión, o para ampliar o desarrollar habilidades que precisan, sin olvidar las diferencias sociales o culturales entre el usuario y el profesional. Es un tipo de intervención que se desempeña dese la Atención directa al paciente. ¿ Cuál es?', 'Proporcionar asesoramiento/orientación.', 'Proporcionar apoyo emocional.', 'Formar en habilidades sociales.', 'Educar.', 'c');`,
    `INSERT INTO "preguntas" VALUES (229, '¿En qué año España asumió el Convenio Europeo de Asistencia Social y Médica, cuyo articulado expresa la importancia de la ayuda desde el trabajo social sanitario dentro del sistema de salud actual?', '1984.', '1982.', '1980.', '1983.', 'd');`,
    `INSERT INTO "preguntas" VALUES (230, '¿ Qué normativa desarrolla el Reglamento de Organización y Funcionamiento de las Zonas Básicas de Salud (ZBS) en Canarias y recoge a los profesionales de AP, las funciones de algunos de ellos y la inclusión del trabajador social como personal, entre otros?', 'Real Decreto 137/84', 'Es una ley no un decreto.', 'Es una instrucción no un decreto.', 'Decreto 117/1997.', 'd');`,
    `INSERT INTO "preguntas" VALUES (231, 'Cuando las y los trabajadores sociales creamos la posibilidad de cambio a través de adoptar un enfoque directivo; en situaciones de abandono, negligencias, maltrato a menores o personas con discapacidad; realizamos un tipo de intervención que se desempeña desde la atención directa al paciente. ¿ Cuál es ?', 'Persuadir.', 'Negociar.', 'Dar explicaciones.', 'Mediar/Facilitar.', 'a');`,
    `INSERT INTO "preguntas" VALUES (232, 'El/la trabajador/a social asume la atención del malestar psicosocial relacionado con los problemas de salud del paciente, es un miembro más del equipo, que persigue la atención integral en salud. Partiendo de lo comentado y de una valoración global de la situación del usuario ¿ Quién puede ser el profesional de referencia del paciente?', 'El médico/a, la enfermera/o o el profesional de trabajo social.', 'El médico.', 'La enfermera/o.', 'El profesional de trabajo social.', 'a');`,
    `INSERT INTO "preguntas" VALUES (233, 'El procedimiento a seguir por el profesional del trabajo social en el Área de Atención Directa cuenta con distintas fases. ¿Cuál se define como "...un proceso continuo, en el que se analiza el grado de consecución de los objetivos propuestos en el plan de intervención y el usuario participa junto con el profesional o profesionales que han intervenido"?', 'Evaluación.', 'Análisis de la demanda.', 'Intervención social.', 'Estudio de factores sociales', 'a');`,
    `INSERT INTO "preguntas" VALUES (234, '¿ Qué autor/a define la crisis como "estado de conmoción, de parálisis de una persona que sufre o ha sufrido un gran shock"?', 'De Ranquet.', 'Caplan.', 'Payne.', 'Trevithick.', 'a');`,
    `INSERT INTO "preguntas" VALUES (235, 'Desde el Área de Atención Directa , la comprensión por parte del profesional trabajador/a social de determinados fenómenos que se producen en el proceso salud-enfermedad es primordial. ¿ A qué fenómenos nos referimos?', 'Reacciones del individuo o familia; interacciones e interrelaciones en el medio y el contexto donde estas se desarrollan y actúan.', 'Emociones, vivencias, reacciones del individuo o familia; interacciones e interrelaciones en el medio y el contexto donde estas se desarrollan y actúan.', 'Emociones, vivencias, reacciones del individuo o familia; interacciones e interrelaciones en el medio.', 'Interacciones e interrelaciones en el medio y el contexto donde estas se desarrollan y actúan.', 'b');`,
    `INSERT INTO "preguntas" VALUES (236, 'Cuando los trabajadores sociales se preocupan por el bienestar de la persona; tanto dedicando atención a las necesidades que sean de su competencia, como procurando la ayuda que precisa ante la necesidad de cuidados sociales; realizan un tipo de intervención que se desempeña desde la atención directa del paciente. ¿Cuál es?', 'Prestar asistencia práctica.', 'Garantizar cuidados sociales.', 'Dar ánimo y seguridad.', 'Contener la ansiedad.', 'b');`,
    `INSERT INTO "preguntas" VALUES (237, '¿Cuándo se creó internacionalmente el primer servicio de Trabajo Social Hospitalario?', '1930.', '1984.', '1906.', '1903.', 'd');`,
    `INSERT INTO "preguntas" VALUES (238, '¿Qué normativa española refiere expresamente el trabajo social como una prestación a desarrollar por el Sistema Nacional de Salud?', 'Ley 14/ 1986, de 25 de abril, General de Sanidad', 'Ley 11/1994, de 26 de julio, de Ordenación Sanitaria de Canarias.', 'Ley 16/2003, de 28 de mayo, de Cohesión y Calidad del Sistema Nacional de Salud.', 'Ley 12/2007, de 11 de octubre, de Servicios Sociales.', 'c');`,
    `INSERT INTO "preguntas" VALUES (239, '¿Qué autor/a define el trabajo social sanitario como "la especialidad del Trabajo Social que se desarrolla y se ejerce dentro del sistema sanitario: en la atención primaria, la atención sociosanitaria o la atención especializada"?', 'Ituarte, A. (1992).', 'Milián Valle, C. (2007).', 'Hervás de la Torre. A. (2014).', 'Colom, D. (2010).', 'd');`,
    `INSERT INTO "preguntas" VALUES (240, 'Los problemas de relación enfermo-familia, que pueden incidir en la recuperación de la salud, son una de las principales problemáticas con que se encuentra el/la trabajador/a social hospitalario en relación con:', 'La enfermedad.', 'La familia.', 'La conducta.', 'La economía.', 'a');`,
    `INSERT INTO "preguntas" VALUES (241, 'La Valoración, información y orientación es una actividad incluida en una de las áreas de trabajo del profesional del Servicio de Trabajo Social Hospitalario incluida en la Cartera de Servicios. ¿ A qué área de trabajo pertenece?', 'Atención directa a la persona enferma y familiares.', 'Colaboración en programas específicos, atención a enfermos con patologías específicas y crónicas.', 'Participación Institucional.', 'Participación con la red de apoyo para conseguir la integración del individuo.', 'a');`,
    `INSERT INTO "preguntas" VALUES (242, 'La información, coordinación y contactos del Centro Hospitalario y con las demás Instituciones y servicios del Área Sanitaria, es una actividad de las distintas áreas de trabajo del profesional del Servicio de Trabajo Social Hospitalario. ¿ A qué área de trabajo pertenece?', 'Área Rehabilitadora.', 'Área de Prevención.', 'Área de Investigación y Docencia.', 'Área Asistencial.', 'b');`,
    `INSERT INTO "preguntas" VALUES (243, 'El /la TS hospitalario desarrolla múltiples tareas dentro de sus actuaciones, señala la correcta:', 'Investigan y tratan de resolver los problemas que derivan del estado de enfermedad de los pacientes en su medio familiar, social o de trabajo.', 'Todas las respuestas son correctas.', 'Procuran calmar la angustia de los familiares y, en caso de fallecimiento del enfermo, son las encargadas de auxiliarlos en los trámites para obtener la entrega del cuerpo.', 'Ayudan a que la conducta del paciente dentro del hospital sea conveniente para él y los demás enfermos, y atienden a los profesionales visitantes interesados en conocer el trabajo de la institución.', 'b');`,
    `INSERT INTO "preguntas" VALUES (244, 'Una vez el profesional en lo social realiza una valoración y un diagnóstico social, dentro del contexto hospitalario, se remite al paciente a otros servicios y recursos. ¿Cuáles son los recursos del sistema sanitario y de los servicios sociales a donde derivar al paciente / familiares?', 'De ámbito sanitario: hospitales, centros de estancias intermedias y centros sanitarios de primer nivel.', 'Centros de ámbito sociosanitario.', 'Recursos de los servicios sociales: servicios de asistencia a domicilio, estancias temporales y residenciales.', 'Todas las respuestas son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (245, '¿Qué caracteriza al Modelo Proactivo de intervención y de respuesta ante emergencias?', 'Permite la realización de análisis evaluativos de las intervenciones, diferenciar los problemas reales y potenciales y poner en marcha acciones preventivas.', 'Aporta soluciones previstas, sin atender a excepciones y, en este sentido, consiste en aplicar normativas, escalas de puntuación, criterios de acceso.', 'Sus resultados son sesgados e inútiles para la toma de decisiones.', 'Se produce una retroalimentación activa de los sistemas de información como base para la toma de futuras decisiones.', 'a');`,
    `INSERT INTO "preguntas" VALUES (246, '¿Qué modelo de intervención y de respuesta ante emergencias sociales se caracteriza por: ser rígido que, salvo casos emergentes, sigue lo establecido por comités y grupos de trabajo, y en donde los profesionales se convierten en ejecutores de órdenes con escasa participación en el diseño de intervención o normativas?', 'Modelo de Gestión de caso.', 'Modelo Administrativo.', 'Modelo Proactivo.', 'Modelo Reactivo.', 'b');`,
    `INSERT INTO "preguntas" VALUES (247, 'La canalización y contención de las reacciones de duelo y el establecimiento de la fase inicial del duelo, son actividades del TS dentro de las actuaciones en situaciones urgencia social. ¿ Dentro de qué tipo de actuaciones se enmarcan?', 'Informativa.', 'Rehabilitadora.', 'Soporte emocional.', 'Administrativa.', 'c');`,
    `INSERT INTO "preguntas" VALUES (248, '¿ Qué acontecimiento, documento o normativa supuso un impulso importante para el trabajo social sanitario dentro de los equipos multidisciplinares en España?', 'El Plan de Humanización de Asistencia Hospitalaria en 1984.', 'El Reglamento General de Estructura, Organización y Funcionamiento de los Hospitales en 1987.', 'El desarrollo de la reforma psiquiátrica de 1984.', 'La Guía de Organización del Sistema de Trabajo Social en Atención Especializada de salud en el 2000.', 'c');`,
    `INSERT INTO "preguntas" VALUES (249, 'Informar sobre trámites administrativos, fase de despedida, así como colaborar con los equipos del hospital, son actividades del TS dentro de las actuaciones en situaciones de urgencia social. ¿ Dentro de qué tipo de actuaciones se enmarcan?', 'Informativa.', 'Rehabilitadora.', 'Soporte emocional.', 'Administrativa.', 'b');`,
    `INSERT INTO "preguntas" VALUES (250, 'Indica en qué normativa se hace referencia al alta hospitalaria:', 'Decreto 5/ 1999, de 21 de enero, que regula la prestación del servicio de ayuda a domicilio y Real Decreto 521/1987, de 15 de abril, que aprueba el reglamento sobre estructura, organización y funcionamiento de los gestionados por el INS.', 'Ley 14/1986, de 25 de abril, General de Sanidad y Decreto 5/1999, de 21 de enero , que regula la prestación del servicio de ayuda a domicilio.', 'Real Decreto 521/1987, de 15 de abril, que aprueba el reglamento sobre estructura, organización y funcionamiento de los gestionados por el INS.', 'Ley 16/ 2003, de 28 de mayo, de Cohesión y Calidad del Sistema Nacional de Salud y Real Decreto 1030/2006 de 15 de septiembre, que establece la cartera de servicios comunes del Sistema Nacional de Salud.', 'd');`,
    `INSERT INTO "preguntas" VALUES (251, '¿Qué límites tiene el proceso de Alta Social Sanitaria?', 'Límite inicial y final.', 'Límite inicial, intermedio y final.', 'Los límites no quedan establecidos.', 'Límite final.', 'a');`,
    `INSERT INTO "preguntas" VALUES (252, 'Señala los objetivos del proceso de alta hospitalaria:', 'Optimizar los resultados en salud del proceso de atención sanitaria.', 'Prevenir o limitar los riesgos para la salud y la autonomía derivados de la institucionalización y las intervenciones innecesarias.', 'Todas las respuestas son correctas.', 'Optimizar las capacidades y las posibilidades de integración y funcionamiento social de pacientes y familiares.', 'c');`,
    `INSERT INTO "preguntas" VALUES (253, 'Señala qué grupos de personas son población diana del proceso de Alta Social Sanitaria en función de diferentes factores sociales:', 'Personas en situación de especial vulnerabilidad por exclusión o riesgo de exclusión social en salud.', 'Personas con enfermedades agudas.', 'Personas con enfermedades infecciosas.', 'Personas con enfermedades crónicas.', 'a');`,
    `INSERT INTO "preguntas" VALUES (254, '¿Qué define la OMS como "estructura destinada a recolectar, elaborar, analizar y transmitir la información necesaria para organizar y hacer funcionar los servicios sanitarios, siendo la investigación y la docencia objetivos complementarios"?', 'Sistemas Transaccionales.', 'Unidad básica de información (UBI).', 'Sistema de Información Hospitalario (SIH).', 'Sistema de Información Sanitaria (SIS).', 'd');`,
    `INSERT INTO "preguntas" VALUES (255, 'El Sistema de Información Hospitalario (SIH) se distingue por integrar y caracterizar los problemas de salud del paciente en cuatro niveles con el Sistema de Unidad Básica de Información (UBI) ¿qué nivel ocupa el paciente?', 'Tercer nivel.', 'Segundo nivel.', 'Cuarto nivel.', 'Primer nivel.', 'd');`,
    `INSERT INTO "preguntas" VALUES (256, 'La Atención Socio-Sanitaria viene determinada por las necesidades que cubre. Cuando aborda una demanda, ¿desde qué variables lo hace?', 'Sociodemográficas, económicas, sanitarias, motivacionales y de coste.', 'Sociodemográficas.', 'Económicas y sanitarias.', 'Sanitarias, motivacionales y de coste.', 'a');`,
    `INSERT INTO "preguntas" VALUES (257, '¿Cuáles son los elementos básicos de la Atención Socio-Sanitaria (ASS)?', 'La atención sanitaria, la rehabilitación y el apoyo social.', 'La atención sanitaria y el apoyo social.', 'La rehabilitación y el apoyo social y emocional.', 'La atención sanitaria y el apoyo social y emocional.', 'a');`,
    `INSERT INTO "preguntas" VALUES (258, 'La Atención Socio-Sanitaria es un método asistencial en red. ¿Qué nuevo estilo de intervención crea este método sobre las necesidades socio-sanitarias?', 'Atención flexible y personalizada.', 'La coordinación de los sectores sanitario y social y coordinación como trabajo interdisciplinar y multidisciplinar.', 'El paciente es el eje sobre el que giran la organización y el funcionamiento de los servicios.', 'Todas las respuestas son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (259, '¿En qué año aparece en España la Atención Socio-Sanitaria?', '1968.', '1996.', '1998.', '1986.', 'd');`,
    `INSERT INTO "preguntas" VALUES (260, 'La coordinación socio-sanitaria se debe hacer a varios niveles, ¿Cuáles?', 'Entre el servicio sanitario y los servicios sociales de las diferentes administraciones públicas.', 'Entre los diversos recursos y niveles de un mismo sistema.', 'Entre los servicios de apoyo formal, y el sistema de apoyo informal.', 'Todas las respuestas son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (261, 'Para los trabajadores/as sociales sanitarios, lo socio-sanitario es:', 'Su principal actividad.', 'Su espacio de trabajo profesional.', 'El final del proceso de trabajo con el paciente.', 'El final del proceso de trabajo con la familia del paciente.', 'b');`,
    `INSERT INTO "preguntas" VALUES (262, 'De los tipos de población que se exponen y que atiende el profesional del trabajo social en su labor de coordinación socio-sanitaria, ¿Cuál NO pertenece a grupos de especial vulnerabilidad por dependencia funcional y pérdida de autonomía?', 'Enfermos mentales y sus familias.', 'Las personas con patologías crónicas invalidantes o con estigma social.', 'Población joven y adulta en exclusión social extrema, que presenta problemas de salud.', 'Personas mayores independientes funcionalmente, con riesgo de aumento de dependencia funcional o deterioro cognitivo.', 'c');`,
    `INSERT INTO "preguntas" VALUES (263, 'En España fue pionero el modelo de hospitalización domiciliaria en 1981, ¿en qué hospital?', 'Hospital La Paz.', 'Hospital 12 de Octubre.', 'Hospital Provincial de Madrid (actual Hospital General Universitario Gregorio Marañón).', 'Hospital Universitario Ramón y Cajal.', 'c');`,
    `INSERT INTO "preguntas" VALUES (264, 'Cuando decimos que la selección de candidatos debe ser muy meticulosa, centrándose en la cantidad y complejidad de cuidados a administrar, más que en la patología en sí misma, ¿estamos hablando de?', 'Uno de los beneficios de la HD para la familia.', 'Uno de los beneficios de la HD para el paciente.', 'Un principio fundamental de la hospitalización domiciliaria (HD).', 'Uno de los beneficios de la HD para el asegurado.', 'c');`,
    `INSERT INTO "preguntas" VALUES (265, 'De las normativas jurídicas que se exponen, indica cuál NO hace ninguna referencia a la hospitalización domiciliaria:', 'Real Decreto 137/1984, de 11 de enero, sobre estructuras básicas de salud.', 'Ley 14/1986, de 25 de abril, General de Sanidad.', 'Real Decreto 521/1987, de 15 de abril, por el que se aprueba el Reglamento sobre Estructura, Organización y Funcionamiento de los Hospitales gestionados por el INS.', 'Orden de 1 de marzo de 1985 por la que se aprueba el Reglamento General de Estructura, Organización y Funcionamiento de los Hospitales de la Seguridad Social.', 'a');`,
    `INSERT INTO "preguntas" VALUES (266, 'De las siguientes afirmaciones, señala la que corresponde a una característica de la hospitalización domiciliaria:', 'La aceptación voluntaria por parte del paciente y su familia tras ser debidamente informados del tipo de asistencia.', 'El reingreso en el hospital, si surgen complicaciones.', 'Todas las respuestas son correctas.', 'La transitoriedad de los procesos atendidos.', 'c');`,
    `INSERT INTO "preguntas" VALUES (267, 'Señala qué criterio excluiría a un paciente que se puede beneficiar de la atención hospitalaria en su domicilio:', 'Que precise de una atención limitada en el tiempo.', 'Estar ingresado en el hospital y encontrarse inestable clínicamente.', 'Que no esté prevista una mala evolución de su enfermedad o sufrir alguna complicación.', 'Tras recibir la información adecuada sobre este tipo de atención, aceptar voluntariamente este tipo de atención.', 'b');`,
    `INSERT INTO "preguntas" VALUES (268, 'Campion señala los límites de la atención domiciliaria, especialmente para pacientes que viven solos, los cuales, además de los cuidados, ¿qué es imprescindible que tengan?', 'Familia.', 'Ingresos superiores al IPREM.', 'Compañía.', 'Una vivienda de un mínimo de 100 metros.', 'c');`,
    `INSERT INTO "preguntas" VALUES (269, 'La realización de las visitas domiciliarias necesarias para completar la información y seguimiento de las situaciones socio-familiares del paciente, así como el desplazamiento a otros dispositivos comunitarios en caso necesario es una de las tareas del equipo interdisciplinar de la UHD. ¿Qué profesional la desarrolla?', 'El auxiliar clínico.', 'El enfermero/a.', 'El trabajador/a social.', 'El médico/a.', 'c');`,
    `INSERT INTO "preguntas" VALUES (270, 'Desde la perspectiva integradora del Trabajo Social se deducen varios bloques de funciones dentro de la atención a pacientes con servicio de hospitalización domiciliaria, señálalos:', 'Preventivas.', 'De coordinación.', 'De enlace.', 'Asistenciales/terapéuticas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (271, 'El/la trabajador/a social evaluará la situación sociofamiliar del paciente de atención hospitalaria domiciliaria. ¿Qué tendrá que hacer para ello?', 'Todas las respuestas son correctas.', 'Identificar la problemática social que acompaña a la enfermedad proporcionando la detección de necesidades sociales del paciente.', 'Planificar de forma interdisciplinar el plan de cuidados sociales y médicos del paciente durante la hospitalización a domicilio y al alta.', 'Establecer un sistema fluido de coordinación y de comunicación con el resto de los niveles asistenciales, sociales y sanitarios.', 'a');`,
    `INSERT INTO "preguntas" VALUES (272, 'Concienciar al personal sanitario de que las situaciones sociales y sanitarias cohabitan y no deben desligarse, ¿de qué profesional del equipo es función?', 'El enfermero/a de continuidad de cuidados.', 'El enfermero/a.', 'El médico/a.', 'El trabajador/a social.', 'd');`,
    `INSERT INTO "preguntas" VALUES (273, 'El/la trabajador/a social del equipo reduce el impacto, la ansiedad y la desorientación que provoca la hospitalización al paciente. ¿Qué tipo de acción es esta?', 'Una de sus funciones dentro de la atención en HD.', 'Una de las tareas dentro de la función preventiva en el servicio de HD.', 'Una de las áreas de evaluación que trabaja.', 'No es una acción de este profesional.', 'a');`,
    `INSERT INTO "preguntas" VALUES (274, 'De las siguientes afirmaciones sobre la hospitalización domiciliaria, indica cuál constituye un principio fundamental:', 'Disminuir el gasto autónomo entendiendo por este todo aquel en que incurre un paciente o su familia con motivo de una hospitalización.', 'Mayor participación e información.', 'Trasladar al paciente tempranamente a su casa, para continuar su tratamiento, libera a la institución de estos gastos.', 'La valoración de las necesidades sociales que influyan en el mantenimiento del paciente en su domicilio.', 'd');`,
    `INSERT INTO "preguntas" VALUES (275, 'De los siguientes motivos, señala el que justificaría el rechazo a la Hospitalización Domiciliaria:', 'Todas las respuestas son correctas.', 'Negativa del paciente o familiar a dicho proceso.', 'No estar médicamente indicado el ingreso.', 'Problemas sociales que lo impidan.', 'a');`,
    `INSERT INTO "preguntas" VALUES (276, '¿Bajo qué dirección nace el Programa de Prevención y Control de la Enfermedad Cardiovascular?', 'Programas asistenciales.', 'Programas específicos.', 'Programas de atención temprana.', 'Programas sociosanitarios.', 'a');`,
    `INSERT INTO "preguntas" VALUES (277, '¿A qué población se dirige el Programa de Prevención y Control de la Enfermedad Vascular Aterosclerótica de Canarias?', '> 14 años.', '< 16 años.', 'Población adulta.', 'Población en general.', 'a');`,
    `INSERT INTO "preguntas" VALUES (278, '¿Cuál es el objetivo general que persigue el Programa de Prevención y Control de la Enfermedad Vascular Aterosclerótica de Canarias?', 'Reducir la morbimortalidad por Enfermedad Vascular Aterosclerótica, a través de medidas de prevención, detección precoz y abordaje integrado de los FRCV modificables.', 'Reducir la mortalidad por Enfermedad Vascular Aterosclerótica, a través de medidas de prevención, detección precoz y abordaje integrado de los FRCV no modificables.', 'Reducir la morbimortalidad por Enfermedad Vascular Aterosclerótica, a través de medidas de prevención, detección precoz y abordaje integrado de los FRCV no modificables.', 'Realizar controles periódicos de salud para la detección de hábitos de vida y factores de riesgo para la Enfermedad Vascular Aterosclerótica.', 'a');`,
    `INSERT INTO "preguntas" VALUES (279, '¿Qué se entiende por Factores de riesgo cardiovascular?', 'Son condicionantes endógenos o ligados a estilos de vida que incrementan la probabilidad de padecer o morir por enfermedad vascular aterosclerótica en aquellas personas en las que inciden.', 'Son condicionantes exógenos o ligados a estilos de vida que incrementan la probabilidad de padecer o morir por enfermedad vascular aterosclerótica en aquellas personas en las que inciden.', 'Son condicionantes sociales ligados a estilos de vida que incrementan la probabilidad de padecer o morir por enfermedad vascular aterosclerótica en aquellas personas en las que inciden.', 'Son condicionantes psicosociales ligados a estilos de vida que incrementan la probabilidad de padecer o morir por enfermedad vascular aterosclerótica en aquellas personas en las que inciden.', 'a');`,
    `INSERT INTO "preguntas" VALUES (280, '¿Cuáles de los siguientes factores de riesgos (FR) se consideran modificables?', 'Edad, sexo y antecedentes personales de haber padecido una enfermedad vascular aterosclerótica.', 'El tabaquismo, la hipertensión arterial, la dislipemia y la diabetes.', 'La sobrecarga asistencial producida por el seguimiento, control y tratamiento de los factores de riesgo considerados aisladamente.', 'La eficacia de las intervenciones cuanto mayor es el riesgo y la limitación de los recursos.', 'b');`,
    `INSERT INTO "preguntas" VALUES (281, 'La necesidad de establecer prioridades de actuación (en función del riesgo de padecer una enfermedad vascular) viene motivada por:', 'El mayor impacto y eficacia de las intervenciones cuanto mayor es el riesgo.', 'La sobrecarga asistencial producida por el seguimiento, control y tratamiento de los factores de riesgo considerados aisladamente.', 'La limitación en los recursos.', 'Todas las opciones son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (282, '¿Cuáles de los siguientes son considerados factores psicosociales en el Programa de Prevención y Control de la Enfermedad Vascular Aterosclerótica de Canarias?', 'Nivel socio-económico bajo, bajo nivel de estudios, aislamiento social, falta de apoyo social.', 'Estrés en el trabajo y en la vida familiar.', 'Emociones negativas: depresión, ansiedad y hostilidad.', 'Todas las opciones son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (283, '¿Qué se entiende por prevención primaria en las enfermedades cardiovasculares?', 'Medidas encaminadas a controlar los factores de riesgo cardiovascular, una vez ya presentes, para disminuir la incidencia de la enfermedad.', 'Medidas encaminadas a disminuir la progresión de la enfermedad, incluso desde los estadios iniciales asintomáticos, con el objetivo de prolongar y mejorar la calidad de vida de los individuos con patología cardiovascular.', 'Medidas encaminadas a disminuir la prevalencia de los factores de riesgo cardiovascular, y a promocionar factores protectores.', 'Medidas encaminadas a disminuir la progresión de la enfermedad, la prevalencia de los factores de riesgo cardiovascular, y a promocionar factores protectores.', 'a');`,
    `INSERT INTO "preguntas" VALUES (284, '¿Qué se entiende por prevención secundaria en las enfermedades cardiovasculares?', 'Medidas encaminadas a controlar los factores de riesgo cardiovascular, una vez ya presentes, para disminuir la incidencia de la enfermedad.', 'Medidas encaminadas a disminuir la progresión de la enfermedad, incluso desde los estadios iniciales asintomáticos, con el objetivo de prolongar y mejorar la calidad de vida de los individuos con patología cardiovascular.', 'Medidas encaminadas a disminuir la prevalencia de los factores de riesgo cardiovascular, y a promocionar factores protectores.', 'Medidas encaminadas a disminuir la progresión de la enfermedad, la prevalencia de los factores de riesgo cardiovascular, y a promocionar factores protectores.', 'b');`,
    `INSERT INTO "preguntas" VALUES (285, 'Los niveles de intervención en el Programa de enfermedades cardiovasculares serán:', 'Individual, familiar y comunitario.', 'Individual, familiar, grupal, comunitario.', 'Familiar, grupal y comunitario.', 'Grupal, comunitario e intersectorial.', 'b');`,
    `INSERT INTO "preguntas" VALUES (286, 'Las principales actuaciones para prevenir una enfermedad cardiovascular son:', 'No fumar, controlar el peso, llevar una alimentación saludable, hacer ejercicio físico.', 'Realizar un control adecuado de la diabetes, hipertensión arterial y colesterol elevado.', 'No fumar, controlar el peso, llevar una alimentación saludable, hacer ejercicio físico y realizar un control adecuado de la diabetes, hipertensión arterial y colesterol elevado.', 'Ninguna de las anteriores es correcta.', 'c');`,
    `INSERT INTO "preguntas" VALUES (287, 'El desarrollo de la enfermedad cardiovascular está íntimamente ligada a:', 'Hábitos de vida y factores de riesgo.', 'Hábitos de vida.', 'Factores de riesgo.', 'Ninguna es correcta.', 'a');`,
    `INSERT INTO "preguntas" VALUES (288, 'Los factores de riesgo se catalogan cuando cumplen unos requisitos que permiten establecer:', 'Una relación de causa con respecto a la enfermedad cardiovascular.', 'Una relación de causa-efecto con respecto a la enfermedad cardiovascular y se clasifican en modificables y no modificables.', 'Una relación coordinada según sea posible o no actuar sobre ellos y limitar su riesgo subsidiario.', 'Todas son correctas.', 'b');`,
    `INSERT INTO "preguntas" VALUES (289, 'El apoyo social tiene efectos sobre la salud. ¿Cómo se denomina el efecto que protege a las personas de los elementos patogénicos de las situaciones de estrés, siendo esencialmente un moderador del impacto de éste?', 'Efecto directo.', 'Efecto amortiguador.', 'Efecto reductor.', 'Efecto creador.', 'b');`,
    `INSERT INTO "preguntas" VALUES (290, 'El sistema de ayuda natural (familia, amigos, vecinos) y el sistema de ayuda natural organizada (asociaciones, Grupos de Ayuda Mutua), ¿a qué tipo de apoyo social pertenecen?', 'Apoyo formal.', 'Apoyo organizacional.', 'Apoyo informal.', 'Apoyo institucional.', 'c');`,
    `INSERT INTO "preguntas" VALUES (291, '¿Qué parte del proceso a seguir para el fomento de los Grupos de Ayuda Mutua (GAM) consiste en poner en relación a las personas afectadas por un mismo problema, ya sea ofreciendo contactos, recursos de forma común, canalizar hacia grupos ya creados, a través de educación sanitaria, sesiones socioeducativas, etc.?', 'Generar contactos.', 'Apoyo a los GAM.', 'Fomento de los grupos.', 'Colaboración.', 'a');`,
    `INSERT INTO "preguntas" VALUES (292, '¿Qué objetivos persigue, dentro del Área de Apoyo Social, el método a seguir por el profesional del trabajo social para el fomento del apoyo social a nivel grupal?', 'Fomentar el voluntariado como fuente de apoyo y beneficios psicológicos.', 'Fomentar la creación y mantenimiento de los grupos de ayuda mutua y del voluntariado como fuente de apoyo.', 'Fomentar la creación y mantenimiento de los grupos de ayuda mutua e Integración social.', 'Fomentar la creación y mantenimiento de los grupos de ayuda mutua, del voluntariado como fuente de apoyo y de la red de apoyo social a nivel individual y familiar.', 'b');`,
    `INSERT INTO "preguntas" VALUES (293, '¿Cuál es la ley del voluntariado en Canarias?', 'Ley 4/2001, de 19 de junio.', 'Ley 4/2018, de 8 de mayo.', 'Ley 25/2015, de 30 de julio.', 'Ley 4/1998, de 15 de mayo.', 'd');`,
    `INSERT INTO "preguntas" VALUES (294, '¿Cómo define la ley canaria en su artículo 6 al voluntariado?', 'Todas aquellas acciones de interés general que contribuyen a enriquecer la calidad de vida de la sociedad en general y preservar el medio ambiente.', 'Una forma de ayuda social que consiste en ofrecer recursos humanos y materiales para un fin determinado.', 'Es el trabajo de las personas que sirven a una comunidad o al medio ambiente por decisión propia y libre.', 'Toda persona física que realice una actividad no obligatoria, no lucrativa, responsable, continua, solidaria a través de los proyectos o programas de una entidad que ejerza el voluntariado y dentro de algunas áreas de interés social.', 'd');`,
    `INSERT INTO "preguntas" VALUES (295, 'Toda persona física que realice una actividad no obligatoria, no lucrativa, responsable, continua, solidaria a través de los proyectos o programas de una entidad que ejerza el voluntariado y dentro de algunas áreas de interés social:', 'Área de Atención Directa.', 'Área de Apoyo Social.', 'Área de Coordinación.', 'Área de Docencia.', 'a');`,
    `INSERT INTO "preguntas" VALUES (296, 'Indica las áreas de interés social para el ejercicio del voluntariado:', 'Todas las respuestas son correctas.', 'Servicios sociales y sanitarios.', 'Promoción de la igualdad de oportunidades entre el hombre y la mujer.', 'Cooperación y solidaridad internacional, sensibilidad y educación para el desarrollo, derechos humanos y pacifismo.', 'a');`,
    `INSERT INTO "preguntas" VALUES (297, 'Fomentar la puesta en marcha, por los organismos competentes, de Proyectos de Voluntariado que respondan a necesidades en salud es una de las funciones del trabajador social en salud. ¿En qué normativa/documento queda regulado?', 'Documento Marco Rol Profesional.', 'Ley 4/ 1998, de 15 de mayo.', 'Instrucción n.° 6/03.', 'Ley 4/2001, de 19 de junio.', 'a');`,
    `INSERT INTO "preguntas" VALUES (298, 'Contribuir, junto al resto del equipo, a la formación del voluntariado en temas de salud si se considera necesario, es una de las funciones del trabajador/a social en salud. ¿En qué área de actuación la desarrolla?', 'Área de Promoción.', 'Área de Coordinación.', 'Área de Atención Directa.', 'Área de Apoyo Social.', 'd');`,
    `INSERT INTO "preguntas" VALUES (299, '¿Por qué es importante que el grupo se marque una finalidad y sea quien decide qué actividades quiere hacer?', 'Las actividades marcan el sentido del grupo.', 'Las actividades fijadas en grupo dependerán de los intereses y aspiraciones de sus componentes.', 'La dimensión del grupo depende de la actividad que haga.', 'La actividad la marca el líder del grupo.', 'b');`,
    `INSERT INTO "preguntas" VALUES (300, '¿Qué grupos se caracterizan porque sus miembros intercambian experiencias comunes?', 'Grupos de ayuda mutua.', 'Grupos de interés.', 'Grupos de referencia.', 'Grupos secundarios.', 'a');`,
    `INSERT INTO "preguntas" VALUES (301, 'El papel del profesional trabajador/a social en los Grupos de Ayuda Mutua ha de ser de asesoramiento, formación y apoyo de líderes. Una vez creado y apoyado el grupo, ¿Cómo debe actuar el profesional?', 'Debe hacer un seguimiento periódico.', 'Debe realizar un seguimiento y evaluación continua.', 'Debe mantenerse al margen.', 'Debe delegar su función en otro profesional del equipo.', 'c');`,
    `INSERT INTO "preguntas" VALUES (302, '¿Dónde se define la promoción de la salud como el proceso que permite a las personas incrementar el control sobre su salud?', 'Declaración de Alma Ata.', 'Declaración de Yakarta.', 'Cartera de Servicios del Ministerio de Sanidad.', 'OMS.', 'd');`,
    `INSERT INTO "preguntas" VALUES (303, '¿Qué tipo de intervención en promoción y educación para la salud a nivel de la comunidad local se dirige a colectivos de la comunidad, desarrollando talleres, cursos o sesiones, para aumentar su conciencia sobre los factores sociales, políticos y ambientales que influyen sobre la salud?', 'Educación para la salud grupal y colectiva.', 'Consejo y asesoramiento.', 'Educación para la salud individual.', 'Información y comunicación.', 'a');`,
    `INSERT INTO "preguntas" VALUES (304, 'Tres son las áreas de intervención prioritarias de abordaje de la promoción y educación para la salud para responder de forma coherente a las diferentes necesidades de salud que se plantean en nuestras sociedades desarrolladas. Señala la que NO es:', 'Problemas de salud.', 'Estilos de vida.', 'El hábitat.', 'Transiciones vitales.', 'c');`,
    `INSERT INTO "preguntas" VALUES (305, '¿Qué define la OMS como "aquella actividad que se refiere a los esfuerzos colectivos de las comunidades para incrementar su control sobre los determinantes de la salud y en consecuencia para mejorar la salud"?', 'Determinantes comunitarios.', 'Acción comunitaria.', 'Elementos comunitarios.', 'Salud comunitaria.', 'b');`,
    `INSERT INTO "preguntas" VALUES (306, 'La promoción de la salud constituye un instrumento imprescindible y una estrategia básica en la respuesta a los problemas y situaciones de salud más relacionales. ¿Qué NO es necesario para este proceso?', 'La sensibilización y capacitación técnica correspondiente que posibilite la promoción de la salud de las personas.', 'La única implicación de los profesionales.', 'Un cambio de actitudes en los decisores y planificadores de la salud.', 'El arrastre de políticas y recursos que apuesten realmente por esta opción.', 'b');`,
    `INSERT INTO "preguntas" VALUES (307, 'El enfoque de la Atención Primaria de Salud abarca entre sus componentes la implicación y participación de la comunidad. ¿Desde cuándo ocurre?', 'Declaración de Yakarta.', 'Carta de Ottawa.', 'Declaración de Alma Ata.', 'Declaración de Helsinki.', 'c');`,
    `INSERT INTO "preguntas" VALUES (308, '¿Qué ley canaria define el Consejo de Salud como el órgano de asesoramiento, consulta y participación comunitaria en la gestión de la Zona Básica de Salud?', 'Ley 11/1994, de 26 de julio, de Ordenación Sanitaria.', 'Ley 12/2001, de 21 de diciembre, de Ordenación Sanitaria.', 'Ley 3/2003, de 6 de febrero, de Ordenación Sanitaria.', 'Ley 7/2003, de 9 de diciembre, de Ordenación Sanitaria.', 'a');`,
    `INSERT INTO "preguntas" VALUES (309, '¿Cuáles de las siguientes son funciones básicas que debe desarrollar un/a profesional del trabajo social en el ámbito de la salud comunitaria y educación grupal?', 'Todas las respuestas son correctas.', 'Desarrollar programas y proyectos específicos de formación y educación para la salud y capacitación dirigidos para toda la población, pero especialmente para aquellos/as que se encuentren en situación de riesgo.', 'Realizar evaluaciones periódicas que permitan modificaciones, según se van planteando imprevistos, en los objetivos propuestos a largo, medio y corto plazo.', 'Facilitar al resto de los miembros del equipo conocimientos técnicos sobre relación, comunicación, trabajo en grupo e intervención comunitaria.', 'a');`,
    `INSERT INTO "preguntas" VALUES (310, '¿Cómo se denomina el órgano de participación cercano y próximo a los ciudadanos donde se tratan cuestiones de salud individual y comunitaria?', 'Consejo de Salud de Área.', 'Consejos de Salud de la Zona Básica de Salud.', 'Comisión de Participación Hospitalaria.', 'Comisión de Participación de Atención primaria.', 'b');`,
    `INSERT INTO "preguntas" VALUES (311, 'El papel de la promoción y educación para la salud es fundamental para responder de forma efectiva a las nuevas necesidades de salud de la sociedad. Un elemento de referencia es la política de Salud 21 para la Región Europea de la OMS, que ha definido veintiún objetivos de salud que se distribuyen en áreas. Señala en cuáles:', 'Garantizar la equidad y mejorar la salud en las transiciones vitales.', 'Prevenir y controlar las enfermedades y accidentes, y desarrollar estrategias de salud intersectoriales.', 'Todas las respuestas son correctas.', 'Orientar el sector sanitario hacia resultados de salud y establecer políticas y mecanismos de gestión del cambio.', 'c');`,
    `INSERT INTO "preguntas" VALUES (312, 'Una función a desarrollar por el/la trabajador/a social en el ámbito de la salud comunitaria y educación grupal es vincular a la comunidad con el proyecto, haciendo que personas de la propia comunidad lideren en el proceso ¿Cuál es el perfil de las personas seleccionadas para ello?', 'Personas ajenas a la comunidad, que dispongan de tiempo, con estudios universitarios y edad y madurez similar al de la media.', 'Personas con trayectoria de liderazgo, con estudios universitarios, con control de sus tiempos y edad que entrañe madurez.', 'Personas arraigadas en la comunidad, con al menos disponibilidad de cuatro horas semanales, con formación de grado medio y sin cargas familiares.', 'Personas arraigadas en la comunidad, que dispongan de tiempo, con educación básica y edad que combine experiencia y aprendizaje.', 'd');`,
    `INSERT INTO "preguntas" VALUES (313, '¿Cómo se denomina al órgano colegiado de participación comunitaria en la planificación, control y evaluación de la asistencia que corresponde al hospital?', 'Consejo de Participación en Atención Especializada.', 'Consejo de Salud Especializada.', 'Comisión de Participación Hospitalaria.', 'Consejo de Salud de Área.', 'c');`,
    `INSERT INTO "preguntas" VALUES (314, '¿Es la misma ley canaria la que regula los Consejos de Salud de la ZBS y la Comisión de Participación Hospitalaria?', 'No, los Consejos de Salud se regulan mediante decreto.', 'No, cada órgano de participación se regula por una normativa distinta.', 'Sí, ambas se regulan en la Ley 12/2001, de 21 de diciembre, de Ordenación Sanitaria.', 'Sí, ambas se regulan en la Ley 11/1994, de 26 de julio, de Ordenación Sanitaria.', 'd');`,
    `INSERT INTO "preguntas" VALUES (315, 'El trabajo social sanitario como disciplina surge:', 'Para tratar no solo los problemas de salud que presenta un paciente, sino también los factores sociales que pueden agravar o aligerar su enfermedad.', 'Para tratar los problemas de salud que presenta un paciente.', 'Para tratar los problemas sociales que pueden agravar o aligerar su enfermedad.', 'Ninguna es correcta.', 'a');`,
    `INSERT INTO "preguntas" VALUES (316, '¿En qué año se crea el Real Decreto sobre estructuras básicas de salud?', '1984.', '1974.', '1975.', '1978.', 'a');`,
    `INSERT INTO "preguntas" VALUES (317, 'En nuestra Comunidad Autónoma, ¿cuál es la Ley que regula la atención sanitaria?', 'Ley de Ordenación Sanitaria de Canarias, 1994.', 'Decreto 117/1997, que desarrolla el Reglamento de Organización y funcionamiento de las Zonas Básicas de Salud.', 'La ley y el decreto mencionados se complementan.', 'Ninguna de las anteriores es correcta.', 'a');`,
    `INSERT INTO "preguntas" VALUES (318, 'El/la trabajador/a social es pieza clave en la atención en salud, en la medida que aporta:', 'Conocimiento de los distintos sistemas de protección social, favorece el trabajo intersectorial, hace posible la necesaria relación entre los sistemas sanitario y social, que deben coordinarse para la atención a estos grupos de población.', 'Conocimiento de los distintos sistemas de protección social operativizando el abordaje de la dimensión social y comunitaria de la salud.', 'Conocimiento en la atención de la salud favoreciendo el trabajo intersectorial.', 'Ninguna de las respuestas es correcta.', 'a');`,
    `INSERT INTO "preguntas" VALUES (319, 'Además de la instrucción nº3/06 de la Directora del Servicio Canario de la Salud por la que se establece el Marco Funcional de los Trabajadores Sociales en Atención Primaria de la Salud, ¿Qué único documento marco fue emitido en 2004?', 'El Rol del trabajador social en la AP en salud.', 'El Rol del trabajador social en la AE en salud.', 'El Plan de Salud del Servicio Canario de Salud.', 'Programas asistenciales.', 'a');`,
    `INSERT INTO "preguntas" VALUES (320, 'El Rol del trabajador social en la AP en salud se estructura por áreas de trabajo o unidades de planificación. Señala cuáles:', 'Área de Atención Directa, Área de Coordinación Sociosanitaria, Área de Apoyo Social, Área de Promoción de Salud y Participación y Área de Docencia e Investigación.', 'Área de Atención Directa, Área de Coordinación Sociosanitaria y Área de Apoyo Social.', 'Área de Atención Directa, Área de Promoción de Salud y Participación y Área de Docencia e Investigación.', 'Área de Coordinación Sociosanitaria, Área de Apoyo Social y Área de Promoción de Salud y Participación.', 'a');`,
    `INSERT INTO "preguntas" VALUES (321, 'Respecto a la formación continuada, ¿Cuáles de las siguientes son funciones del trabajador/a social?', 'Participar en la formación continuada del equipo aportando conocimientos específicos de trabajo social e integrándose en las actividades de formación.', 'Ofrecer información sobre recursos y facilitar los lazos de coordinación con los recursos o instituciones de interés para el estudio.', 'Aportar la perspectiva social y los conocimientos propios de su disciplina, fomentando la inclusión en el estudio de los determinantes sociales de la salud y los factores de riesgo social en salud.', 'Formación sanitaria sobre conceptos médico-sociales, etiología y desarrollo de las enfermedades, profundizando en aquellas enfermedades que tienen mayor impacto social (SIDA, enfermedades mentales, enfermedades infecciosas, etc.).', 'a');`,
    `INSERT INTO "preguntas" VALUES (322, 'Con respecto a la investigación, ¿Cuáles de las siguientes son funciones del trabajador/a social?', 'Aportar la perspectiva social y los conocimientos propios de su disciplina, fomentando la inclusión en el estudio de los determinantes sociales de la salud y los factores de riesgo social en salud.', 'Participar en la formación continuada del equipo aportando conocimientos específicos de trabajo social e integrándose en las actividades de formación.', 'Colaborar en la planificación de las actividades docentes, facilitar apoyo o asesoramiento al responsable de docencia en torno a los temas de la competencia de este profesional.', 'Realizar las actividades formativas a grupos de profesionales de la Zona Básica o Área de salud.', 'a');`,
    `INSERT INTO "preguntas" VALUES (323, 'La especialidad del trabajo social que se desarrolla y se ejerce en la atención primaria, la atención sociosanitaria o la atención especializada se denomina:', 'Trabajo social sanitario.', 'Trabajo social comunitario en el área de salud.', 'Trabajo social hospitalario.', 'Trabajo social clínico.', 'a');`,
    `INSERT INTO "preguntas" VALUES (324, '¿Hacia qué materias orienta el/la trabajador/a social su formación?', 'Formación sanitaria sobre conceptos médico-sociales, etiología y desarrollo de las enfermedades, profundizando en aquellas enfermedades que tienen mayor impacto social (SIDA, enfermedades mentales, enfermedades infecciosas, etc.).', 'Conocimiento de los programas y protocolos de salud vigentes en la Comunidad Autónoma.', 'Formación en investigación y planificación en el campo de salud.', 'Todas son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (325, 'En el área de docencia, el/la trabajador/a social participará junto al resto del EAP en las siguientes acciones:', 'Determinar al responsable de docencia del EAP.', 'Identificar las necesidades formativas de los profesionales o alumnos.', 'Organizar las actividades formativas y docentes.', 'Todas son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (326, '¿Cuál es el instrumento del Servicio Canario de la Salud para la formación del personal sanitario y el desarrollo de programas de investigación sanitaria?', 'La Escuela de Servicios Sanitarios y Sociales de Canarias.', 'El Instituto Canario de Administración Pública.', 'El Servicio Público de Empleo Estatal.', 'El Colegio Oficial de Trabajo Social.', 'a');`,
    `INSERT INTO "preguntas" VALUES (327, '¿En cuáles de los siguientes ámbitos la práctica del trabajo social de salud genera puntos de partida para la investigación?', 'Clínico, epidemiológico, de organización de servicios, de evaluación del impacto de determinadas intervenciones sobre el estado de salud de las personas, familiar y comunitario.', 'Clínico, epidemiológico y de organización de servicios.', 'De organización de servicios, de evaluación del impacto de determinadas intervenciones sobre el estado de salud de las personas, familiar y comunitario.', 'De evaluación del impacto de determinadas intervenciones sobre el estado de salud de las personas, familiar y comunitario.', 'a');`,
    `INSERT INTO "preguntas" VALUES (328, 'En la Conferencia Internacional de Atención Domiciliaria (AD), celebrada en Lombardía se elaboran los principios de esta acción. Según estos principios ¿Qué debe garantizar la AD?', 'La provisión de información a los pacientes y a sus familias durante el periodo de tratamiento, coordinar la trasferencia del paciente a otro nivel asistencial o servicio en caso necesario.', 'La calidad asistencial, formación de sus profesionales y la provisión de información a los pacientes y a sus familias durante el periodo de tratamiento.', 'Una situación higiénica adecuada y coordinar la trasferencia del paciente a otro nivel asistencial o servicio en caso necesario.', 'Confidencialidad, información a los pacientes y a sus familias, coordinar la trasferencia del paciente a otro nivel asistencial, calidad asistencial, formación de sus profesionales y una situación higiénica adecuada.', 'd');`,
    `INSERT INTO "preguntas" VALUES (329, '¿Cómo se denomina uno de los servicios de la Asistencia Primaria de la Salud que, por las características especiales del paciente, se ha de realizar en el domicilio de éste?', 'Cuidado domiciliario.', 'Continuidad de cuidados.', 'Atención domiciliaria.', 'Hospitalización domiciliaria.', 'c');`,
    `INSERT INTO "preguntas" VALUES (330, '¿Qué cambios de la comunidad hacen prever un futuro en donde será demandado socialmente un volumen creciente de servicios domiciliarios?', 'Demográficos.', 'Epidemiológicos.', 'Todas las respuestas son correctas.', 'Sociales y sanitarios.', 'c');`,
    `INSERT INTO "preguntas" VALUES (331, '¿Qué regula el Decreto 5/1999, de 21 de enero?', 'Servicio de continuidad de cuidados.', 'Programas de los Servicios Sociales de Base.', 'Servicio de Ayuda a Domicilio (SAD) en el ámbito municipal.', 'La autonomía del paciente.', 'c');`,
    `INSERT INTO "preguntas" VALUES (332, '¿A partir de qué año se incorpora a la cartera de servicios de atención primaria el denominado "Servicio de Continuidad de Cuidados para la Atención Domiciliaria" en la Comunidad Autónoma de Canarias?', '2002.', '1992.', '2000.', '2012.', 'a');`,
    `INSERT INTO "preguntas" VALUES (333, '¿Quiénes son los principales receptores de la Atención Domiciliaria prestada por los Equipos de Atención Primaria (EAP)?', 'Personas mayores con pluripatología y deterioro funcional.', 'Personas con dificultad para valerse por ellos mismos.', 'Todas las respuestas son correctas.', 'Personas con procesos terminales que necesitan cuidados paliativos en el hogar.', 'c');`,
    `INSERT INTO "preguntas" VALUES (334, 'Señala de los siguientes criterios de inclusión de la Atención Domiciliaria cuál NO está incluido en el protocolo del Servicio de Continuidad de Cuidados.', 'Padecer una patología física, mental y funcional incapacitante, que no puedan acudir a su Centro de Salud (CS).', 'Reciente alta hospitalaria y no puedan desplazarse al CS, que continúen en convalecencia y hasta su recuperación.', 'Padecer patología o enfermedad terminal.', 'Personas cuidadas en Centros Sociosanitarios adscritos al CS y que no reúnan algunos de los criterios de inclusión.', 'd');`,
    `INSERT INTO "preguntas" VALUES (335, 'Crear y mantener mecanismos de coordinación con los recursos y servicios sociales de la zona de salud destinados a la atención a las personas dependientes y sus cuidadores/as es uno de los objetivos específicos del Servicio de Continuidad de Cuidados para la Atención Domiciliaria. Señala cuál de las siguientes metas persigue:', 'Elaborar protocolos de coordinación entre los profesionales de los servicios sociales comunitarios, los centros sociosanitarios y el EAP de la ZBS para la atención a personas dependientes y sus cuidadores/as.', 'Valorar tras el alta a las personas incluidas en la atención domiciliaria y a sus cuidadores/as.', 'Ofertar formación grupal específica a las personas cuidadoras cada trimestre.', 'Hacer el seguimiento periódico del 100% de los pacientes incluidos en el servicio.', 'a');`,
    `INSERT INTO "preguntas" VALUES (336, 'Analizar y promover redes de apoyo social formal e informal para las personas dependientes en domicilio es una actividad del/la Trabajador Social. ¿A qué objetivo específico del Servicio de Continuidad de Cuidados para la Atención Domiciliaria pertenece esta actividad?', 'Mantener y mejorar la calidad de vida relacionada con la salud de las personas dependientes incluidas en el servicio.', 'Mantener y mejorar la calidad de vida relacionada con la salud de los/as cuidadores/as principales de las personas incluidas en el servicio.', 'Potenciar circuitos para la obtención, recogida y reutilización de material específico para la atención domiciliaria.', 'Crear y mantener mecanismos de coordinación con los recursos y servicios sociales de la zona de salud destinados a la atención a las personas dependientes y sus cuidadores/as.', 'd');`,
    `INSERT INTO "preguntas" VALUES (337, 'Coordinarse con la unidad de atención familiar (UAF) y la enfermera comunitaria de enlace (ECE) para dar coherencia y una visión integral al plan de intervención del paciente, prestando atención a las necesidades sociales, es una actividad del/la Trabajador Social. ¿A qué objetivo específico del Servicio de Continuidad de Cuidados para la Atención Domiciliaria pertenece esta actividad?', 'Mantener y mejorar la calidad de vida relacionada con la salud de las personas dependientes incluidas en el servicio.', 'Mantener y mejorar la calidad de vida relacionada con la salud de los/as cuidadores/as principales de las personas incluidas en el servicio.', 'Conocer y participar en la mejora de los mecanismos de coordinación entre los niveles asistenciales para facilitar la continuidad de cuidados.', 'No pertenece a ningún objetivo específico del Servicio de Continuidad de Cuidados para la Atención Domiciliaria.', 'a');`,
    `INSERT INTO "preguntas" VALUES (338, 'El paciente debe ser atendido en cada momento en el lugar y nivel más apropiado a sus necesidades. El hospital es un componente del continuo de servicios. ¿Qué otros servicios forman parte en ese continuo?', 'El domicilio de los pacientes.', 'Los Servicios Sociosanitarios.', 'Todas las respuestas son correctas.', 'El Centro de Salud.', 'c');`,
    `INSERT INTO "preguntas" VALUES (339, 'La coordinación entre niveles tiene significados diferentes según distintos puntos de vista. Cuando se define como el complemento de cuidados tras el alta y la actividad en consultas externas ¿desde qué punto de vista se realiza?', 'Paciente.', 'Atención Primaria.', 'Atención Especializada.', 'Domicilio.', 'c');`,
    `INSERT INTO "preguntas" VALUES (340, '¿De quién es la Historia Clínica informatizada?', 'De la Atención Primaria.', 'Del Sistema.', 'De la Atención Especializada.', 'Del paciente.', 'd');`,
    `INSERT INTO "preguntas" VALUES (341, '¿A través de qué proyectos los profesionales de la salud capacitan a la comunidad en la mejora o mantenimiento de la salud?', 'El voluntariado.', 'El voluntariado, la dinamización grupos de ayuda mutua, las campañas de sensibilización, la promoción de la salud y el diagnóstico de salud del territorio.', 'Las campañas de sensibilización, la promoción de la salud y el diagnóstico de salud del territorio.', 'Ninguna de las anteriores es correcta.', 'b');`,
    `INSERT INTO "preguntas" VALUES (342, 'Dentro de las estrategias de intervención comunitaria en salud se encuentran:', 'Divulgación y motivación.', 'Divulgación y sensibilización, promoción y motivación.', 'Divulgación y promoción.', 'Sensibilización y motivación.', 'b');`,
    `INSERT INTO "preguntas" VALUES (343, '¿Cuáles de los siguientes son objetivos específicos del Trabajo Social Comunitario?', 'Favorecer la promoción social comunitaria y fomentar la participación y el fortalecimiento de las redes sociales como factores compensatorios que proteger frente a otros factores de riesgo social.', 'Prevenir la aparición de problemáticas sociales y modificar las conductas y actitudes que contribuyen a dichas situaciones.', 'Fomentar la inclusión social desde el enfoque comunitario.', 'Todas las opciones son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (344, '¿Cuáles de las siguientes fases se contemplan en los momentos metodológicos de trabajo social en el proceso de intervención comunitaria?', 'Fase de diagnóstico, fase de planificación social y dimensión participativa, fase de ejecución o intervención, fase de evaluación y control.', 'Fase de diagnóstico, fase intervención participativa, fase de ejecución, fase de evaluación, fase de seguimiento.', 'Fase de diagnóstico, fase de planificación participativa, fase de intervención, fase de evaluación y control.', 'Fase de diagnóstico, fase de evaluación social y dimensión participativa, fase de planificación social, fase de ejecución y control.', 'a');`,
    `INSERT INTO "preguntas" VALUES (345, 'La fase que sirve de base para la realización de un proyecto o programa y para fundamentar las estrategias a llevar a cabo en el trascurso del mismo es:', 'Fase de diagnóstico.', 'Fase de planificación.', 'Fase de ejecución o intervención.', 'Fase de evaluación y control.', 'a');`,
    `INSERT INTO "preguntas" VALUES (346, '¿Qué aspectos indispensables debe aportar el diagnóstico que explique una realidad determinada con el fin de programar una acción transformadora?', 'Una correcta formulación del problema y su ubicación en un contexto global.', 'Un análisis de las situaciones que intervienen en el problema y las relaciones hipotéticas existentes entre ellas.', 'Un pronóstico del desarrollo futuro del problema.', 'Todas las opciones son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (347, 'Todo diagnóstico que se acerque a la problemática de la comunidad debe comenzar con:', 'Análisis de la comunidad.', 'Planificación participativa.', 'Investigación preliminar.', 'Evaluación de la comunidad.', 'c');`,
    `INSERT INTO "preguntas" VALUES (348, '¿A qué fase nos referimos cuando se decide con antelación lo que hay que hacer, señalando los medios con los que se cuenta y los fines que se pretende conseguir?', 'Fase de diagnóstico.', 'Fase de planificación.', 'Fase de ejecución o intervención.', 'Fase de evaluación y control.', 'b');`,
    `INSERT INTO "preguntas" VALUES (349, '¿Qué variables hay que tener en cuenta para la preparación de la comunidad en la participación de un programa?', 'Las aspiraciones y necesidades comunitarias.', 'El nivel de vida material.', 'El grado de actividad o pasividad de los individuos.', 'Todas las opciones son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (350, '¿Qué fase mide los resultados del Programa en relación con los objetivos propuestos?', 'Fase de diagnóstico.', 'Fase de planificación.', 'Fase de ejecución o intervención.', 'Fase evaluación.', 'd');`,
    `INSERT INTO "preguntas" VALUES (351, '"La participación comunitaria es el proceso en virtud del cual los individuos y las familias asumen responsabilidades en cuanto a salud y bienestar propios y los de la colectividad y mejoran la capacidad de contribuir a su propio desarrollo económico y comunitario". Esta definición se encuentra recogida en:', 'La Conferencia de Alma-Ata.', 'La Organización Mundial de la Salud.', 'La Ley General de Sanidad.', 'Los Consejos de Salud.', 'a');`,
    `INSERT INTO "preguntas" VALUES (352, 'La participación comunitaria como el conjunto de acciones que se realizan de forma individual, familiar y de la comunidad para promover la salud, prevenir las enfermedades y detener su avance, es una definición de:', 'La Conferencia de Alma-Ata.', 'La Organización Mundial de la Salud.', 'La Ley General de Sanidad.', 'Los Consejos de Salud.', 'b');`,
    `INSERT INTO "preguntas" VALUES (353, '¿Cuál de los siguientes mecanismos de participación comunitaria es el más utilizado en el ámbito de la salud?', 'Los Consejos de Salud.', 'Relaciones con otros sectores.', 'Sistemas de reclamaciones y sugerencias.', 'Máxima capacidad de elección por parte del paciente.', 'a');`,
    `INSERT INTO "preguntas" VALUES (354, '¿Qué profesional del equipo multidisciplinar conoce las carencias y necesidades sociales que inciden en el proceso salud-enfermedad, e identifica las redes familiares y sociales en las instituciones de salud?', 'Médico de familia.', 'Trabajador/a social.', 'Trabajador/a comunitario.', 'Enfermero/a de enlace.', 'b');`,
    `INSERT INTO "preguntas" VALUES (355, '¿Con qué previsión de temporalidad para su implantación se elaboró la Estrategia en Cuidados Paliativos de Canarias?', '2018-2021.', '2017-2020.', '2019-2022.', '2016-2019.', 'a');`,
    `INSERT INTO "preguntas" VALUES (356, '¿En qué razones se basa la justificación de la elaboración de la Estrategia en Cuidados Paliativos de Canarias?', 'Razones legales, éticas y económicas.', 'Razones legales, éticas, necesidad de la población muy prevalente y de alta intensidad, económicas, consenso técnico y científico y por compromiso con el SNS y con el plan de salud de canarias.', 'Necesidad de la población muy prevalente y de alta intensidad y compromiso con el SNS y con el plan de salud de canarias.', 'Consenso técnico y científico y por compromiso con el SNS y con el plan de salud de canarias.', 'b');`,
    `INSERT INTO "preguntas" VALUES (357, 'Señala de entre los siguientes epígrafes, el que corresponde a los valores de la Estrategia de Cuidados Paliativos de Canarias:', 'Atención integral bio-psicosocial de los pacientes y sus familias y cuidadores.', 'Todas las respuestas son correctas.', 'Participación de la familia y la comunidad en la atención de las personas en situación de enfermedad en fase avanzada o terminal susceptibles de recibir cuidados paliativos.', 'Respeto a los valores y autonomía del paciente.', 'b');`,
    `INSERT INTO "preguntas" VALUES (358, 'Según la Estrategia en Cuidados Paliativos del Sistema Nacional de Salud (ECP-SNS), ¿Cómo debe ser un Programa de Cuidados Paliativos?', 'Coordinado entre los niveles y servicios de atención primaria (consulta, domicilio, servicios de urgencias) y de atención hospitalaria.', 'Cerrado a la coordinación con los recursos sociales (servicios sociales, voluntariado y ONG).', 'Sin llegar a formar equipos interdisciplinares y multiprofesionales.', 'Ninguna respuesta es correcta.', 'a');`,
    `INSERT INTO "preguntas" VALUES (359, '¿Qué características debe tener el modelo de atención de Cuidados paliativos?', 'Ninguna respuesta es correcta.', 'Responsabilidad social y respeto hacia los objetivos y valores de los enfermos y sus familias.', 'Accesible, integral, integrado en la red asistencial, sectorizado por Áreas de Salud y responsabilidad social.', 'Accesible, integral. integrado en la red asistencial, sectorizado por Áreas de Salud, responsabilidad social y respeto hacia los objetivos y valores de los enfermos y sus familias.', 'd');`,
    `INSERT INTO "preguntas" VALUES (360, 'Toda la red de centros del SCS debe atender a los cuidados paliativos (CP) en su nivel correspondiente de complejidad. ¿Qué recursos son propios de la Atención Primaria para dispensar esta atención?', 'Unidad de cuidados paliativos que incluye la interconsulta con otros servicios, la hospitalización de día y la consulta externa.', 'Unidad de cuidados paliativos y la atención domiciliaria.', 'Función de gestores de casos, coordinación de los cuidados y la atención domiciliaria.', 'Atención primaria carece de recursos de atención a los CP.', 'c');`,
    `INSERT INTO "preguntas" VALUES (361, 'Dentro del equipo de atención a los pacientes beneficiarios de cuidados paliativos (CP) se desarrolla el objetivo de Informar con veracidad aportando conocimiento del medio sanitario y de los recursos socio-sanitarios existentes para ponerlos al servicio de ese paciente y de su familia, al que ya no se puede curar, pero al que se puede escuchar, cuidar, atender y entender. ¿Qué profesional lo desempeña?', 'Enfermera gestora de casos.', 'Trabajador/a social.', 'Médico.', 'Servicio de atención al paciente.', 'b');`,
    `INSERT INTO "preguntas" VALUES (362, '¿Con respecto a quién el trabajador/a social del equipo de cuidados paliativos (CP) desarrolla la acción de contribuir a la formación de una opinión pública informada?', 'La comunidad.', 'El equipo.', 'La persona.', 'La familia.', 'a');`,
    `INSERT INTO "preguntas" VALUES (363, 'El profesional trabajador/a social ha de estar capacitado para la atención de niños con enfermedades que limitan o amenazan su vida con un enfoque asistencial que tenga en cuenta al niño como persona con todas sus dimensiones. Dentro de su formación ¿con qué conocimientos debería contar?', 'Todas las respuestas son correctas.', 'Marco conceptual, principios y atención al final de la vida en cuidados paliativos pediátricos. Fundamentos del trabajo interdisciplinar. Atención y acompañamiento en el final de la vida.', 'Problemas y necesidades habituales en los pacientes en cuidados paliativos pediátricos. Acompañamiento al niño y su familia. El proceso de duelo. Enfoque paliativo como enfoque asistencial integral centrado en la persona.', 'Habilidades de comunicación con el niño y su familia. Recursos y redes asistenciales en cuidados paliativos. Aspectos básicos de la atención integral en CPP.', 'a');`,
    `INSERT INTO "preguntas" VALUES (364, '¿Cómo se definen las enfermedades raras (ER)?', 'Son aquellas enfermedades graves y habitualmente crónicas y progresivas que afectan a un número pequeño de personas y que, por su rareza, plantean cuestiones específicas.', 'Determinadas alteraciones genéticas pueden hacer que las células plasmáticas recuperen la capacidad de reproducirse y ocupar parcialmente la médula ósea.', 'Es una infección bacteriana causada por un germen llamado Mycobacterium tuberculosis. La bacteria suele atacar los pulmones, pero puede también dañar otras partes del cuerpo.', 'Se aplica a muchas enfermedades que impiden que el hígado funcione o evitan que trabaje bien. El dolor abdominal, el color amarillo de la piel o los ojos (ictericia) o los resultados anormales de las pruebas de la función hepática indican la presencia de la enfermedad.', 'a');`,
    `INSERT INTO "preguntas" VALUES (365, 'En la fase de seguimiento que hace el profesional del trabajo social en intervenciones de atención a los cuidados paliativos pediátricos. ¿Qué acciones desarrolla?', 'Todas las respuestas son correctas.', 'Establecer objetivos que previsiblemente se puedan lograr basados en la movilización de recursos de la propia situación, de recursos sociales y de los que el propio servicio puede ofrecer.', 'Determinar de qué manera se llevará a cabo el tratamiento y qué medios y recursos técnicos probablemente se tendrán que emplear.', 'Decidir cuáles son los aspectos prioritarios y cuáles los que se han de atender en segundo lugar.', 'a');`,
    `INSERT INTO "preguntas" VALUES (366, 'En Canarias las asociaciones de pacientes son el motor principal en el impulso de acciones en enfermedades raras (ER). ¿Qué otras acciones promueven?', 'Impulsan los ensayos clínicos.', 'El desarrollo de registros específicos por patologías.', 'Todas las respuestas son correctas.', 'Proyectos de investigación que permiten avanzar en el diagnóstico precoz.', 'c');`,
    `INSERT INTO "preguntas" VALUES (367, 'La actividad de aclarar al paciente las consecuencias que pueden tener las diferentes alternativas de acción que se escojan es parte una de las intervenciones directas que realiza el trabajador/a social con el paciente con enfermedad rara (ER) ¿A qué intervención pertenece?', 'Estructurar una relación de trabajo con el asistido.', 'Crear nuevas oportunidades.', 'Informar-educar.', 'Clarificar-apoyar.', 'd');`,
    `INSERT INTO "preguntas" VALUES (368, '¿Cómo ha llamado el Servicio Canario de Salud a las enfermedades raras (ER)?', 'Enfermedades menores.', 'Enfermedades minoritarias.', 'Enfermedades mínimas.', 'Enfermedades monitorizadas.', 'b');`,
    `INSERT INTO "preguntas" VALUES (369, 'La Organización Internacional para la Migraciones (OIM) define migración como:', 'Movimiento de población hacia el territorio de otro Estado o dentro del mismo que abarca todo movimiento de personas sea cual fuere su tamaño, su composición o sus causas.', 'La migración es una experiencia potencialmente traumática caracterizada por acontecimientos traumáticos parciales y configura a la vez una situación de crisis.', 'Un movimiento que atraviesa una barrera significativa que es definida y mantenida por cierto régimen político, de tal manera que cruzarla afecta la identidad del individuo.', 'La inmigración es un fenómeno reciente y complejo, que presenta múltiples facetas interrelacionadas y que requiere un abordaje integral, no solo desde el punto de vista demográfico y económico, sino contemplando también los aspectos jurídicos, educativos, de vivienda, culturales y por supuesto sanitarios.', 'a');`,
    `INSERT INTO "preguntas" VALUES (370, 'A diferencia de otras comunidades autónomas, el Gobierno de Canarias tiene la tutela a los menores inmigrantes. ¿De quién depende su guarda y custodia?', 'Cabildos.', 'Ayuntamientos.', 'Mancomunidades.', 'ONG´s.', 'a');`,
    `INSERT INTO "preguntas" VALUES (371, '¿Qué se entiende por menor inmigrante?', 'Toda persona que tenga una edad menor a 18 años procedente de otro país, con situación legalizada o no, refugiado/a o procedente de la adopción internacional.', 'Toda persona que tenga una edad mayor a 16 años procedente de otro país, con situación legalizada o no, refugiado/a o procedente de la adopción internacional.', 'A los menores de 18 años que se encuentren en territorio español, salvo que en virtud de la ley que les sea aplicable hayan alcanzado anteriormente la mayoría de edad.', 'A los mayores de 16 años que se encuentren en territorio español, salvo que en virtud de la ley que les sea aplicable hayan alcanzado anteriormente la mayoría de edad.', 'a');`,
    `INSERT INTO "preguntas" VALUES (372, '¿Qué artículo de la Ley Orgánica 4/2000 de 11 de enero sobre derechos y libertades de los extranjeros en España y su integración social establece que: "Los extranjeros que se encuentren en España inscritos en el padrón del municipio en el que residan habitualmente, tienen derecho a la asistencia sanitaria en las mismas condiciones que los españoles"?', 'Artículo 14.', 'Artículo 16.', 'Artículo 12.', 'Artículo 10.', 'c');`,
    `INSERT INTO "preguntas" VALUES (373, '¿Cuáles de las siguientes se consideran dificultades para la atención sanitaria de los menores inmigrantes no acompañados?', 'Aumento de la presión asistencial si no se dimensiona la atención sanitaria en aquellas Zonas Básicas de Salud donde existen Centros (CAI o CAME).', 'Desplazamientos o cambios de centros debido a necesidades de organización por la Institución responsable.', 'Miedos relacionados con la sospecha de enfermedades infectocontagiosas en los trabajadores y trabajadoras de los centros de acogida de estos menores (CAI, CAME).', 'Todas las opciones son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (374, 'La metodología de intervención social dependerá de la fase del proyecto migratorio en la que se encuentre la persona inmigrante. ¿Cuáles de las siguientes fases son imprescindibles para la integración social del inmigrante?', 'Fase de Llegada, Fase de Guarda, Fase Independiente.', 'Fase de Custodia, Fase de Asistencia, Fase Autónoma.', 'Fase de Recepción, Fase de Amparo, Fase Independiente.', 'Fase de Acogida, Fase Tutelada, Fase Autónoma.', 'd');`,
    `INSERT INTO "preguntas" VALUES (375, 'En el proyecto migratorio de una persona inmigrante, ¿Qué se entiende por Fase de Acogida?', 'Es la primera fase de todas y coincide con la llegada del inmigrante a su nuevo destino.', 'Esta fase está relacionada con la adaptación y el asentamiento del sujeto al nuevo medio.', 'Es la fase en la que se persiguen resultados como la inserción y la integración.', 'Todas las opciones son correctas.', 'a');`,
    `INSERT INTO "preguntas" VALUES (376, 'En el proyecto migratorio de una persona inmigrante, ¿qué se entiende por Fase de Tutelada?', 'Esta fase está relacionada con la adaptación y el asentamiento del sujeto al nuevo medio.', 'Es la primera fase de todas y coincide con la llegada del inmigrante a su nuevo destino.', 'Es la fase en la que se persiguen resultados como la inserción y la integración.', 'Todas las opciones son correctas.', 'a');`,
    `INSERT INTO "preguntas" VALUES (377, 'En el proyecto migratorio de una persona inmigrante, ¿Qué se entiende por Fase de Autonomía?', 'Es la primera fase de todas y coincide con la llegada del inmigrante a su nuevo destino.', 'Es la fase en la que se persiguen resultados como la inserción y la integración.', 'Esta fase está relacionada con la adaptación y el asentamiento del sujeto al nuevo medio.', 'Todas las opciones son correctas.', 'b');`,
    `INSERT INTO "preguntas" VALUES (378, '¿Cuáles de las siguientes se consideran funciones del voluntariado en salud?', 'Promoción de la salud, de mejora de la accesibilidad y de complemento de la labor asistencial preventiva a personas inmigrantes, minorías étnicas y grupos socialmente desfavorecidos, y de facilitar el desarrollo de los autocuidados y la ayuda mutua.', 'Mejora de la accesibilidad y de complemento de la labor asistencial preventiva a inmigrantes, minorías étnicas y grupos socialmente desfavorecidos.', 'Facilitar el desarrollo de los autocuidados y la ayuda mutua, no solo de las personas inmigrantes sino de la población en general.', 'Promoción de la salud y complemento de la labor asistencial preventiva a personas inmigrantes, minorías étnicas y grupos socialmente desfavorecidos.', 'a');`,
    `INSERT INTO "preguntas" VALUES (379, 'Los poderes públicos apoyarán a los sindicatos, organizaciones empresariales y a las organizaciones no gubernamentales que, sin ánimo de lucro, favorezcan la integración social de las personas inmigrantes e impulsarán entre ellas:', 'El Foro para la Integración Social de los Inmigrantes.', 'La capacidad de autoorganización.', 'El fortalecimiento del movimiento asociativo.', 'La Conferencia Sectorial de Inmigración.', 'c');`,
    `INSERT INTO "preguntas" VALUES (380, 'El Estado garantizará el principio de solidaridad consagrado en la Constitución Española, atendiendo a:', 'Las especiales circunstancias de aquellos territorios en los que los flujos migratorios tengan una especial incidencia.', 'La lucha contra la inmigración irregular y la persecución del tráfico ilícito de personas.', 'La persecución de la trata de seres humanos.', 'La promoción del diálogo y la colaboración con los países de origen y tránsito de inmigración.', 'a');`,
    `INSERT INTO "preguntas" VALUES (381, '¿En qué circunstancias el personal de los Equipos de Atención Primaria derivará a los menores inmigrantes acompañados al trabajador/a social de salud?', 'Cuando existan criterios de riesgo según recoge el Programa de Salud Infantil.', 'Cuando exista una demanda explícita y justificada de los padres o responsables del menor de asesoramiento, dificultad o miedo con las gestiones de la TSI, de información sobre sus derechos.', 'Cuando se trate de menores inmigrantes que estén tutelados por la Dirección General de Protección a la Infancia y Familia.', 'Todas las respuestas son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (382, '¿Cuáles de las siguientes son funciones comunes del trabajador/a social en todo el proceso de intervención con inmigrantes?', 'Poner en marcha acciones sensibilizadoras con el fin de promover una convivencia sana, libre del rechazo y la discriminación.', 'Luchar por la inclusión de los inmigrantes sensibilizando a la población receptora acerca del racismo y la xenofobia.', 'Dejar que sea el inmigrante el actor de su propio cambio y evitar así el proteccionismo.', 'Todas las respuestas son correctas.', 'd');`,
    `INSERT INTO "preguntas" VALUES (383, '¿Qué sistema es el que es definido por la OMS como "un conjunto complejo de elementos interrelacionados que contribuyen a la salud en los hogares, los lugares de trabajo, los lugares públicos y las comunidades, así como en el medio ambiente físico y psicosocial, y en el sector de la salud y otros sectores afines"?', 'Sistema ecológico', 'Sistema económico', 'Sistema social.', 'Sistema sanitario.', 'd');`,
    `INSERT INTO "preguntas" VALUES (384, '¿Qué factores de salud pueden provocar problemática social derivando en enfermedades crónicas?', 'Factores climatológicos y estacionales.', 'Los hábitos de vida.', 'Conducción en condiciones inadecuadas.', 'Adulteraciones de los productos.', 'b');`,
    `INSERT INTO "preguntas" VALUES (385, 'Los determinantes de la salud-enfermedad se han venido abordando a través de distintos modelos. ¿Qué modelo concebía la enfermedad como la confluencia de un agente patógeno, el individuo y el medio ambiente?', 'Modelo tradicional o ecológico de enfermedad.', 'Modelo biopsicosocial.', 'Modelo de promoción de la salud.', 'Modelo médico alternativo subordinado.', 'a');`,
    `INSERT INTO "preguntas" VALUES (386, 'Los principales factores sociales que pueden influir en la salud del individuo son varios. ¿Cuál de ellos constituye el primer agente socializador?', 'Educación.', 'Ingresos.', 'Familia.', 'Medios de comunicación.', 'c');`,
    `INSERT INTO "preguntas" VALUES (387, '¿A qué factores hace referencia Clayton, 1992 cuando los define como atributos individuales, condición situacional, ambiente o contexto que reduce la probabilidad de ocurrencia de un comportamiento desviado?', 'Factores de protección.', 'Factores de riesgo.', 'Factores relacionales.', 'Factores causales.', 'a');`,
    `INSERT INTO "preguntas" VALUES (388, 'Según Lalonde, el nivel de salud de una comunidad estaría influido por distintos grupos de determinantes. ¿Cuáles son?.', 'Estilos de vida y conductas de salud y medio ambiente.', 'Biología humana y sistema de asistencia sanitaria.', 'Estilos de vida y conductas de salud, medio ambiente, biología humana y sistema de asistencia sanitaria.', 'Estilos de vida y conductas de salud.', 'c');`,
    `INSERT INTO "preguntas" VALUES (389, 'Actualmente el modelo más utilizado de determinantes es el de Dahlgren y Whitehead. En este modelo los niveles de salud individual y poblacional estarían definidos por:', 'Condiciones económicas, culturales y del medio ambiente; Las condiciones de vida y de trabajo; Estilos de vidas individuales; La influencia social y de la comunidad; Factores individuales, como el sexo, edad y factores hereditarios.', 'Condiciones económicas, culturales y del medio ambiente: Las condiciones de vida y de trabajo.', 'Estilos de vidas individuales y la influencia social y de la comunidad.', 'Factores individuales, como el sexo, edad y factores hereditarios.', 'a');`,
    `INSERT INTO "preguntas" VALUES (390, 'El área de trabajo "Instituir políticas de protección social de amplio alcance para que toda la población disfrute de un nivel de ingresos suficiente para llevar una vida sana" es una de las que propone la Comisión de los Determinantes de Salud para implementar una de sus recomendaciones para trabajar colectivamente sobre los determinantes de salud, desde dentro y desde fuera de los sistemas sanitarios ¿De qué recomendación se trata?', 'Mejorar las condiciones de vida.', 'Luchar contra la distribución no equitativa del poder, el dinero y los recursos.', 'Medir la magnitud del problema, analizarlo y evaluar los efectos de las intervenciones.', 'Ninguna respuesta es correcta.', 'a');`,
    `INSERT INTO "preguntas" VALUES (391, '¿Qué define la Organización Applied Sociology como "la división más pequeña de la sociedad, o el grupo más íntimo al que puede pertenecer una persona"?', 'Amigos.', 'Familia.', 'Vecinos.', 'Socios.', 'b');`,
    `INSERT INTO "preguntas" VALUES (392, '¿Qué concepto identificas como el que "no sólo determina el estado de salud sino que también condicionan la conducta del individuo"?', 'La empatía.', 'Los genes.', 'El entorno.', 'El ambiente.', 'b');`,
    `INSERT INTO "preguntas" VALUES (393, '¿Qué concepto identificas como el que "es la interacción de un organismo vivo, genéticamente dotado, y su ambiente"?', 'La empatía.', 'Los genes.', 'El entorno.', 'La conducta.', 'd');`,
    `INSERT INTO "preguntas" VALUES (394, 'La pérdida o ausencia de apoyo especialmente por parte de la familia, asociada a las diferentes fuentes de estrés que pueden presentarse en la vida de la persona pueden interactuar, potenciándose y creando un alto nivel de vulnerabilidad individual. ¿Qué autor hace esta afirmación?', 'Sarason,1999', 'Novel, 1991', 'Gallar, 2006', 'House, 1981', 'b');`,
    `INSERT INTO "preguntas" VALUES (395, 'Existen varios tipos de Apoyo Social. La importancia de un tipo u otro dependerá en cada caso de la relevancia de la fuente de apoyo para la persona y del problema concreto que requiera tal apoyo. Señala los tipos de apoyo social existentes.', 'Evaluativo, emocional, instrumental e informativo.', 'Emocional e instrumental.', 'Evaluativo, emocional.', 'Instrumental e informativo.', 'a');`,
    `INSERT INTO "preguntas" VALUES (396, 'Según House ¿Cuáles son los efectos del apoyo social?', 'Todas las respuestas son correctas.', 'Reducen los estresores percibidos (responsabilidad respecto a personas, ambigüedad de rol, infrautilización de capacidades, habilidades).', 'Incrementan la satisfacción a todos los niveles.', 'Disminuyen la depresión y la ansiedad.', 'a');`,
    `INSERT INTO "preguntas" VALUES (397, '¿En qué nivel asistencial de los servicios de salud mental infanto-juvenil la actividad asistencial se desarrolla de forma ambulatoria en las USMIJ (en ellas, se atenderán patologías más graves)?', 'Tercer nivel.', 'Segundo nivel.', 'Primer nivel.', 'Los servicios no se distribuyen entre niveles.', 'b');`,
    `INSERT INTO "preguntas" VALUES (398, 'La asistencia ambulatoria se lleva a cabo en las Unidades de Salud Mental Comunitarias. ¿En qué modalidades?', 'Individual, grupal y domiciliaria.', 'Individual.', 'Grupal.', 'Domiciliaria.', 'a');`,
    `INSERT INTO "preguntas" VALUES (399, 'En los equipos de Salud mental ¿Quién asume la atención en su realidad bio-psico-social?', 'Psiquiatra o Psicólogo Clínico.', 'Enfermero.', 'El Equipo en su conjunto.', 'Trabajador social.', 'c');`,
    `INSERT INTO "preguntas" VALUES (400, 'Colaborar en la detección de personas, sectores y grupos sociales susceptibles de presentar problemas específicos, y determinar los principales factores de riesgo psicosocial relacionados con ellos, es una de las actividades del Trabajador social de los equipos de atención a la salud mental. ¿Dentro de qué función está integrada esta actividad?', 'Funciones de promoción y prevención.', 'Funciones docentes e investigadoras.', 'Funciones de gestión.', 'Funciones asistenciales.', 'a');`,
    `INSERT INTO "preguntas" VALUES (401, 'El paciente que acude a recibir tratamiento y cuyo problema médico fundamental es el psiquiátrico ¿a qué tipo de usuario de los dispositivos de salud mental pertenece?', 'Usuarios en situación de crisis.', 'Usuarios con trastorno mental grave.', 'Usuario de atención domiciliaria.', 'Usuarios continuos de la red.', 'd');`,
    `INSERT INTO "preguntas" VALUES (402, 'Las personas que padecen enfermedades mentales graves y crónicas presentan una mayor morbilidad física y una mortalidad superior a la de la población general. ¿Qué instrumento ha desarrollado la Dirección de Programas Asistenciales para abordar este problema?', 'Manual de atención a la salud mental en Atención Primaria.', 'Guía de cuidados psíquicos para personas con Trastorno Mental Grave.', 'Guía de cuidados físicos para personas con Trastorno Mental Grave.', 'Protocolo de atención a los Trastornos Mentales Graves.', 'c');`,
    `INSERT INTO "preguntas" VALUES (403, '¿Qué equipo de tratamiento asertivo y atención comunitaria se encarga del seguimiento, cuidados y tratamiento integrado dirigido a personas con Trastorno Mental Grave en situación de exclusión social, o en riesgo de padecerla?', 'CIRPAC.', 'USM.', 'CDRPS.', 'ETAC.', 'd');`,
    `INSERT INTO "preguntas" VALUES (404, '¿Por qué entidades se encuentra integrado el Consejo Insular de Rehabilitación Psicosocial y Acción Comunitaria?', 'Servicio Canario de la Salud, Dirección General de Bienestar Social, Dirección General de Empleo, Cabildos, Ayuntamientos y Asociaciones de familiares o de enfermos mentales.', 'Servicio Canario de la Salud, Inspección educativa y Dirección General de Empleo.', 'Servicio Canario de la Salud, Cabildos de todas las Islas y Ayuntamientos.', 'Servicio Canario de la Salud, INSS y Asociaciones de familiares o de enfermos mentales.', 'a');`,
    `INSERT INTO "preguntas" VALUES (405, '¿Cómo se denomina el recurso de la red de salud mental específico para la población con TMG y persistentes que presenta dificultades en su funcionamiento psicosocial y en su integración en la comunidad?', 'Alojamiento Alternativo.', 'Centros de Día Infanto-juveniles.', 'Centros de Día de Rehabilitación Psicosocial.', 'Miniresidencias.', 'c');`,
    `INSERT INTO "preguntas" VALUES (406, '¿Qué servicio de la red de salud mental tiene como objetivo la inserción laboral de personas con discapacidad derivada de un Trastorno Mental Grave (TMG), favoreciendo el acceso y el mantenimiento del empleo?', 'Viviendas tuteladas.', 'Unidades de Internamiento Breve.', 'Plan de Acceso al Empleo (PAE).', 'Miniresidencias.', 'c');`,
    `INSERT INTO "preguntas" VALUES (407, 'Participar en la planificación y ejecución de actividades clínicas, formativas, de investigación y coordinación. ¿A qué funciones del trabajador/a social pertenece dentro de la atención a la salud mental?', 'Documentación.', 'Asistenciales.', 'Gestión.', 'Docentes e investigadoras.', 'd');`,
    `INSERT INTO "preguntas" VALUES (408, '¿Con qué objetivo realizará intervenciones tanto rehabilitadoras como de integración social el/la trabajador/a social dentro del equipo rehabilitador de salud mental?', 'Realizar un diagnóstico clínico de la situación psicosocial, disminuir las situaciones de desventaja o discapacidad detectadas, promover valores relacionados con la adquisición de hábitos sociales.', 'Realizar un diagnóstico de la situación psicosocial, disminuir las situaciones de desventaja detectadas, promover valores relacionados con la adquisición de hábitos sociales, favorecer la adquisición de hábitos saludables y conductas socialmente competentes.', 'Promover valores relacionados con la adquisición de hábitos sociales, favorecer la adquisición de hábitos saludables y conductas socialmente competentes.', 'Realizar un diagnóstico de la situación psicosocial, favorecer la adquisición de hábitos saludables y conductas socialmente competentes, promover valores relacionados con la adquisición de hábitos sociales.', 'b');`,
    `INSERT INTO "preguntas" VALUES (409, 'La profesión Trabajo Social empezó a principio del siglo XX en EE.UU. ¿En qué año aparece por primera vez la figura del trabajo social sanitario, en el ámbito hospitalario?', '1925.', '1915.', '1955.', '1905.', 'd');`,
    `INSERT INTO "preguntas" VALUES (410, '¿Qué instrucción de la Directora del Servicio Canario de la Salud establece el Marco Funcional de los Trabajadores Sociales en Atención Primaria de la Salud?', '6/06.', '3/06', '3/03.', 'El documento no es una instrucción.', 'b');`,
    `INSERT INTO "preguntas" VALUES (411, '¿Qué fase del método de trabajo en la coordinación supone explicitar por escrito las líneas de coordinación en la zona básica de salud, teniendo en cuenta los grupos de población que deben verse beneficiados, acorde con las necesidades de la ZBS y las prioridades del SCS?', 'Organización', 'Ejecución.', 'Programación.', 'Estudio.', 'c');`,
    `INSERT INTO "preguntas" VALUES (412, '¿Qué fase del método de trabajo en la coordinación es una estrategia continua del procedimiento seguido en un proceso de acción-reflexión-acción y debe estar especificada en el programa de trabajo?', 'Ejecución.', 'Organización.', 'Programación.', 'Evaluación.', 'd');`,
    `INSERT INTO "preguntas" VALUES (413, '¿En qué programa asistencial, la función del trabajador social en materia de coordinación es incorporar el factor social de los problemas de salud mental, colaborando con la continuidad de cuidados, aportando recursos para la integración y reinserción social del enfermo?', 'Programa de Atención al Mayor.', 'Programa de Salud Mental.', 'Programa de Salud Infantil.', 'Programa Afectivo-Sexual y Atención Reproductora.', 'b');`,
    `INSERT INTO "preguntas" VALUES (414, 'En el Programa de Atención al Mayor las intervenciones de coordinación del trabajador/a social se centran en:', 'Incorporar el factor social de los problemas de salud mental, colaborando con la continuidad de cuidados, aportando recursos para la integración y reinserción social del enfermo.', 'Coordinación con el Equipo de Salud Mental correspondiente a la Zona Básica de Salud.', 'Detección y captación de menores para el programa cuando se realizan visitas domiciliarias o a otros recursos.', 'Coordinación y participación sistematizada con profesionales e instituciones: Centros de Servicios Sociales municipales y especializados, Hospitales de referencia, Asociaciones y ONGs (Parkinson, Alzheimer, cáncer...), Clubes de jubilados, Centros de día, Residencias, etc.', 'd');`,
    `INSERT INTO "preguntas" VALUES (415, 'La coordinación sociosanitaria es necesaria cuando se atiende a población de especial vulnerabilidad. Cuando se atiende a menores en situación de pre-riesgo y riesgo social, ¿a qué grupo de especial vulnerabilidad se refiere?', 'Los grupos de especial vulnerabilidad por dependencia funcional y pérdida de autonomía.', 'Los grupos de especial vulnerabilidad por su situación social.', 'Los grupos de especial vulnerabilidad por familias en situación de exclusión.', 'Los grupos de especial vulnerabilidad por edad y estado de salud.', 'b');`,
    `INSERT INTO "preguntas" VALUES (416, 'Las ventajas de la coordinación se dan gracias a determinadas sinergias que se establecen. Señala qué elementos NO son suficientes para que la coordinación sea eficaz.', 'La mejora de la calidad de vida y el fomento de un envejecimiento activo y saludable.', 'La optimización de recursos permite la sostenibilidad del sistema sanitario.', 'La mayor calidad asistencial, con tendencia a la excelencia.', 'La concienciación de la Administración y la buena actitud de los profesionales implicados.', 'd');`,
    `INSERT INTO "preguntas" VALUES (417, 'Empatizar con los sentimientos, normalizar los sentimientos, habilidades para el cuidado y acompañar en el proceso, entre otros son los componentes de uno de los abordajes sociales que expone Martín Muñoz, M. en su manual de indicadores para el diagnóstico social en salud. ¿A qué parte del abordaje pertenecen estos componentes?', 'Paliativo.', 'Preventivo.', 'Recursos.', 'Intervención.', 'd');`,
    `INSERT INTO "preguntas" VALUES (418, 'Señala, de las siguientes normativas legales, la que NO está vinculada a la Instrucción nº 03/06 que emite el Servicio Canario de la Salud:', 'Ley 18/2009, de 22 de octubre, de Salud Pública.', 'La Ley 14/1986, de 25 de abril, General de Sanidad.', 'Ley 16/2003, de 28 de mayo, de Cohesión y Calidad del Sistema Nacional de Salud.', 'La Ley 11/1994 de 26 de julio de Ordenación Sanitaria de Canarias.', 'a');`,
    `INSERT INTO "preguntas" VALUES (419, 'El Decreto 117/1997, de 26 de junio, por el que se aprueba el Reglamento de organización y funcionamiento de las ZBS en la CAC, desarrolla las funciones de los profesionales sanitarios y no sanitarios del EAP. ¿Qué profesional del EAP no tiene desarrollado en este Decreto sus funciones?', 'Los/as enfermeros/as.', 'Los/as trabajadores/as sociales.', 'Los/as auxiliares clínicos.', 'Se desarrollan las funciones de todos los profesionales.', 'b');`,
    `INSERT INTO "preguntas" VALUES (420, '¿Qué aportan los/as trabajadores/as sociales en los EAP desde su propio rol profesional?', 'Todas las respuestas son correctas.', 'Una intervención en diferentes niveles: individual/familiar, grupal, comunitaria.', 'Un abordaje preventivo, asistencial y rehabilitador del concepto de Salud.', 'Un método de trabajo (método base: estudio, diagnóstico, planificación, ejecución, evaluación).', 'a');`,
    `INSERT INTO "preguntas" VALUES (421, '¿Qué nº de Instrucción emitida por la Directora del Servicio Canario de la Salud establece el marco funcional de los/as trabajadores/as sociales de Atención Primaria de Salud?', '03/06.', '12/16.', '12/14.', '04/19.', 'a');`,
    `INSERT INTO "preguntas" VALUES (422, 'Para la realización de la instrucción nº 03/06, se parte como referencia de un documento de Atención Primaria. ¿Qué documento?', 'Libro Blanco de la Atención Temprana.', 'Documento Marco Rol del Profesional del TS en AP.', 'Documento Institucional del Consejo General de Trabajadores Sociales.', 'Código Deontológico.', 'b');`,
    `INSERT INTO "preguntas" VALUES (423, 'Señala cuál es la estructura que sigue la instrucción N.º 03/06 en su formulación:', 'Dispositivo asistencial. Identificación personal. Identificación representante legal o guardador/a de hecho. Situación que se plantea. Situación Sociosanitaria. Situación Familiar. Prestación de Cuidados. Actividad. Situación Económica. Características del entorno. Intervenciones directas e indirectas realizadas. Diagnóstico social y Plan de actuación.', 'Identificación personal. Dispositivo desde el que se emite. Dispositivo al que se remite. Motivo. Observaciones: relato de las intervenciones realizadas o en curso de atención/tratamiento social y Firma.', 'Implantación del Modelo de Informe Social. Instrucciones de cumplimentación. Implantación del modelo de Documento de Coordinación Sociosanitaria en el ámbito asistencial. Producción de efectos y Anexos.', 'Objeto y ámbito de aplicación. Funciones generales. Funciones específicas por áreas y Disposición Final.', 'd');`,
    `INSERT INTO "preguntas" VALUES (424, 'Señala cuál es la estructura que sigue la instrucción N.º 12/16 en su formulación:', 'Unidad de gestión de derivación de pacientes. Identificación de pacientes susceptibles de derivación. Seguimiento de estos pacientes. Preparación de la documentación. Formulación de la petición razonada e iniciación del procedimiento. Seguimiento del procedimiento. Traslado del paciente y Efectos del silencio en la resolución del procedimiento.', 'Identificación personal. Dispositivo desde el que se emite. Dispositivo al que se remite. Motivo. Observaciones: relato de las Intervenciones realizadas o en curso de atención/tratamiento social y Firma.', 'Todas las respuestas son erróneas.', 'Implantación del Modelo de Informe Social. Instrucciones de cumplimentación. Implantación del modelo de Documento de Coordinación Sociosanitaria en el ámbito asistencial. Producción de efectos y Anexos.', 'd');`,
    `INSERT INTO "preguntas" VALUES (425, 'Según la Instrucción 03/06 del SCS, ¿en referencia a qué ejerce su función específica el/la trabajador/a social en el área de atención directa?', 'Atención individual y familiar.', 'Coordinación con los servicios y recursos sociales y los recursos sociosanitarios de la zona.', 'Fomento de la red social de apoyo en salud.', 'Estrategias para la promoción de la salud, fomento de la participación en salud y del auto-cuidado.', 'a');`,
    `INSERT INTO "preguntas" VALUES (426, '¿Qué dos documentos propios del trabajo social contienen la información más cuantificable y, por tanto, susceptible de tratamiento posterior a efectos estadísticos o de investigación?', 'Ficha y el Informe Social.', 'Ficha e Historia Social.', 'SIUSS y DRAGO.', 'Diario de Campo e Informe Social.', 'a');`,
    `INSERT INTO "preguntas" VALUES (427, '¿Quién define el Informe social como "dictamen técnico que sirve de instrumento documental que elabora y firma con carácter exclusivo el profesional del trabajo social. Su contenido se deriva del estudio, a través de la observación y la entrevista, donde queda reflejada en síntesis la situación objeto, valoración, un dictamen técnico y una propuesta de intervención profesional"?', 'Tonon, G. (2005).', 'Consejo General de Trabajo Social (2012).', 'Pérez (2000).', 'Garcés Trullenque y Miranda Aranda (1998).', 'b');`,
    `INSERT INTO "preguntas" VALUES (428, 'Según Vélez Restrepo, O. L., el informe social como técnica documental cuenta con determinadas características, ¿Cuáles son?', 'Precisión de términos. Claridad expositiva y Hace uso de metodología.', 'Hace uso de metodología: Entrevista. Visita domiciliaria. Análisis documental y Fuentes consultadas.', 'Fiabilidad de los datos. Precisión de términos. Claridad expositiva y Argumentación didáctica.', 'Análisis documental. Fuentes consultadas y Fiabilidad de los datos.', 'c');`,
    `INSERT INTO "preguntas" VALUES (429, '¿Qué autor/a define el/a trabajador/a social en el ámbito sanitario como aquel profesional que "interviene desde su área específica de conocimiento en el análisis, diagnóstico y tratamiento de los problemas de salud, tanto desde el punto de vista epidemiológico como en la atención a los problemas personales de salud"?', 'Ituarte, A. 2009.', 'Abreu, C. 2009.', 'Colom, D. 2008.', 'Yepes, J. L. 1981.', 'a');`,
    `INSERT INTO "preguntas" VALUES (430, '¿A qué responde la definición "conjunto de acciones encaminadas a ordenar los sistemas sanitario y social para ofrecer una respuesta integral a las necesidades de atención sociosanitaria que se presentan simultáneamente en las personas que padecen situaciones de dependencia" de Sancho Castiello en 1999?', 'Coordinación asistencial.', 'Coordinación sociosanitaria.', 'Coordinación social.', 'Coordinación sociosasistencial.', 'b');`,
    `INSERT INTO "preguntas" VALUES (431, 'La Instrucción nº 12/16 implanta en el SCS el modelo de informe social y menciona una única excepción, en concreto se refiere a los informes emitidos en el ámbito del procedimiento de derivación de personas que, ocupando plazas hospitalarias y habiendo obtenido el alta médica, precisen atención sociosanitaria. ¿En qué normativa viene recogida esta tipología de informes?', 'Instrucción nº 03/06.', 'Instrucción nº 05/19.', 'Instrucción nº 03/14.', 'Instrucción nº 12/14.', 'd');`,
    `INSERT INTO "preguntas" VALUES (432, 'El enunciado "Problema de salud asociado a una situación de vulnerabilidad social con efecto en la adherencia terapéutica, que requiere atención sanitaria en atención especializada, corresponde a uno de los motivos de emisión de un documento de trabajo social en salud" ¿Qué documento?', 'El documento de coordinación sociosanitaria en el ámbito asistencial.', 'El informe social.', 'La historia clínica.', 'Ninguna respuesta es correcta.', 'b');`,
    `INSERT INTO "preguntas" VALUES (433, 'Uno de los apartados del modelo de informe social impuesto por la Instrucción nº 12/16 propone que se describan las propuestas de intervención que se establezcan tanto desde el dispositivo asistencial propio como desde la coordinación con otros dispositivos asistenciales de los diferentes sistemas de protección social, con la especificación que requiere el motivo del informe. ¿A qué apartado hace referencia?', 'Diagnóstico social.', 'Características del entorno.', 'Plan de actuación.', 'Prestación de cuidados.', 'c');`,
    `INSERT INTO "preguntas" VALUES (434, '¿Cómo se clasifican los recursos sociales?', 'Según su procedencia y naturaleza.', 'Según las necesidades reales y las políticas del momento.', 'Según su objetivo y la instrumentalización que se haga de ellos.', 'Todas las opciones son correctas.', 'a');`,
    `INSERT INTO "preguntas" VALUES (435, 'Señala cuáles de las siguientes necesidades específicas de los Servicios Sociales contribuyen a configurar su identidad como sistema, según García y Bueno Abad (1992):', 'Necesidades de subsistencia, información, de accesibilidad, de participación social.', 'Necesidades de subsistencia, información, de accesibilidad, de convivencia personal, de cooperación y solidaridad, de participación social.', 'Necesidades de subsistencia, información, de accesibilidad, de cooperación y solidaridad.', 'Necesidades de subsistencia, información, de accesibilidad, de convivencia personal.', 'b');`,
    `INSERT INTO "preguntas" VALUES (436, 'Cuando los Servicios Sociales responden ofertando a toda la población el conocimiento de los recursos sociales que cada uno precisa, estableciendo y procurando ofrecer una información cualificada, mediante un asesoramiento técnico, responden a la necesidad de:', 'Información.', 'Subsistencia.', 'Accesibilidad.', 'Convivencia personal.', 'a');`,
    `INSERT INTO "preguntas" VALUES (437, 'Cuando los Servicios Sociales utilizan estrategias que fomenten la normalización en el uso de los recursos ordinarios mediante técnicas que faciliten el desbloqueo de las dificultades, tanto si estas son de índole psicológica (minusválidos psíquicos), de índole social (ancianos), de índole cultural (minorías étnicas), responden a la necesidad de:', 'Accesibilidad.', 'Subsistencia.', 'Convivencia personal.', 'Información.', 'a');`,
    `INSERT INTO "preguntas" VALUES (438, 'Los procesos de abandono y soledad son frecuentes en el contexto de las sociedades modernas, como factores de riesgo que impiden el desarrollo de un ámbito convivencial. Desde esta situación, los Servicios Sociales responden a la necesidad de:', 'Accesibilidad.', 'Convivencia personal.', 'Información.', 'Subsistencia.', 'b');`,
    `INSERT INTO "preguntas" VALUES (439, 'Cuando los Servicios Sociales tratan de fomentar los programas de autoayuda buscando la colaboración de las personas próximas responden a la necesidad de:', 'Información.', 'Subsistencia.', 'Participación social.', 'Cooperación y solidaridad.', 'd');`,
    `INSERT INTO "preguntas" VALUES (440, '¿Cuáles de los siguientes se consideran recursos formales?', 'Servicios de ayuda a domicilio.', 'Grupos de autoayuda.', 'Voluntarios.', 'Todas son correctas.', 'a');`,
    `INSERT INTO "preguntas" VALUES (441, '¿De qué tipo de Grupo estamos hablando cuando los miembros se reúnen de forma voluntaria y libre, movidos por la necesidad de dar respuesta o encontrar una solución a un problema, de afrontar y superar una misma situación conflictiva, o de lograr cambios personales o sociales?', 'Grupo de Ayuda Mutua.', 'Grupo Informal.', 'Grupo Socioeducativo.', 'Grupo de Referencia.', 'a');`,
    `INSERT INTO "preguntas" VALUES (442, 'El voluntariado en Canarias se encuentra regulado en:', 'Ley 5/1998, de 15 de mayo, de Voluntariado de Canarias.', 'Ley 6/1998, de 15 de mayo, de Voluntariado de Canarias.', 'Ley 2/1998, de 15 de mayo, de Voluntariado de Canarias.', 'Ley 4/1998, de 15 de mayo, de Voluntariado de Canarias.', 'd');`,
    `INSERT INTO "preguntas" VALUES (443, 'En la Ley 4/1998, de 15 de mayo, de Voluntariado de Canarias, se entiende por voluntariado:', 'Conjunto de actividades desarrolladas por personas voluntarias en áreas de interés social.', 'Conjunto de actividades desarrolladas por entidades organizativas sin ánimo de lucro de interés social.', 'Conjunto de actividades desarrolladas por entidades públicas de interés comunitario.', 'Conjunto de actividades desarrolladas por entidades privadas con o sin ánimo de lucro de interés sociosanitario.', 'a');`,
    `INSERT INTO "preguntas" VALUES (444, 'La persona jurídica legalmente constituida que, careciendo de ánimo de lucro, desarrolla actividades en áreas de interés social de forma ordinaria y permanente, fundamentalmente a través de personas voluntarias, se denomina:', 'Entidad de voluntariado.', 'Entidad sin ánimo de lucro.', 'Entidad pública de carácter social.', 'Entidad privada de carácter público.', 'a');`,
    `INSERT INTO "preguntas" VALUES (445, '¿Cuáles de los siguientes son considerados derechos de las personas voluntarias en sus relaciones con la entidad en la que prestan sus servicios?', 'Participar en aquellas actividades de formación que les indique la entidad, al objeto de capacitarles para un mejor desempeño de su tarea.', 'Ser informadas de las actividades, programas o proyectos en los que vayan a participar, así como de la organización, funcionamiento, fines y objetivos de la entidad en la que colaboren.', 'Mantener la debida confidencialidad de la información recibida y conocida en el desarrollo de su actividad.', 'Respetar las normas de organización y funcionamiento interno de la entidad.', 'b');`,
    `INSERT INTO "preguntas" VALUES (446, '¿Qué Ley Canaria modificó la Ley 16/2003, de 8 de abril, de Prevención y Protección Integral de las Mujeres contra la Violencia de Género con el objeto de incorporar las disposiciones previstas en el Convenio de Estambul y las resoluciones de los organismos internacionales de los que España es parte?', 'Ley 4/2015, de 27 de abril', 'Ley 1/2017, de 17 de marzo', 'Ley Orgánica 8/2015, de 22 de julio', 'Ley 1/2010, de 26 de febrero', 'b');`,
    `INSERT INTO "preguntas" VALUES (447, 'Con independencia de que las formas de violencia estén o no tipificadas como delito o falta penal, o infracción administrativa por la legislación vigente, ¿en cuáles de las siguientes formas pueden ejercerse las violencias machistas?', 'Violencia física y psicológica', 'Violencia sexual y abusos sexuales', 'Violencia económica', 'Todas las respuestas son correctas', 'd');`,
    `INSERT INTO "preguntas" VALUES (448, 'Señala las posibles causas de la demora en la denuncia de la violencia de género:', 'Esperanza en que la situación cambie. Miedo a represalias en ellas o en sus hijos. Vergüenza ante la sensación de fracaso o culpa', 'Tolerancia a los comportamientos violentos. Dependencia de la mujer respecto a su pareja: psicológica y económica. Situación psicológica de la mujer', 'Sentimientos de ambivalencia o inseguridad. Miedo al aparato judicial. No saber a quién dirigirse o a qué servicios acudir. Falta de apoyo familiar, social o económico', 'Todas las respuestas son correctas', 'd');`,
    `INSERT INTO "preguntas" VALUES (449, 'Si la mujer se encuentra en una situación de riesgo (peligro) extremo de sufrir violencia de género, ¿qué actuaciones debe realizar el personal sanitario en cualquier nivel?', 'Se llamará al 1-1-2 (S.A.M.V.V.)', 'Todas las actuaciones descritas son correctas', 'Se registrará en la Historia Clínica el episodio y las actuaciones realizadas. Este registro podrá servir como prueba en un proceso judicial', 'Se emitirá el Parte de Lesiones y se remitirá al Juzgado de Violencia de Género o Juzgado de Guardia con carácter inmediato', 'b');`,
    `INSERT INTO "preguntas" VALUES (450, 'Si la víctima de violencia de género fuera menor de edad, ¿qué actuación sanitaria se añade en su atención?', 'Notificación a la Fiscalía del Menor', 'Traslado del menor a un centro de protección infantil de la Administración Pública', 'Traslado del menor a un punto de encuentro de la red de protección a víctimas de VG', 'Notificación a la Dirección General de Protección a la Infancia y la Familia', 'd');`,
    `INSERT INTO "preguntas" VALUES (451, 'Dentro de las actuaciones sanitarias, en un caso de violencia de género y ante una situación de urgencia, si la mujer ya tiene una orden de protección, ¿a quién hay que poner en conocimiento inmediato de la situación?', 'Al centro de salud para seguimiento', 'Al 1-1-2', 'A las Fuerzas y Cuerpos de Seguridad del Estado', 'A familiares o personas de referencia de la víctima', 'b');`,
    `INSERT INTO "preguntas" VALUES (452, 'El Parte de Lesiones del Servicio Canario de Salud fue revisado para adaptarlo a las nuevas normativas y procedimientos de actuación, ¿cuál es la normativa vigente que lo desarrolla?', 'La Instrucción N.º 3/03 del Director del Servicio Canario de la Salud', 'La Instrucción N.º 3/19 del Director del Servicio Canario de la Salud', 'La Instrucción N.º 4/03 del Director del Servicio Canario de la Salud', 'La Instrucción N.º 4/19 del Director del Servicio Canario de la Salud', 'd');`,
    `INSERT INTO "preguntas" VALUES (453, 'Señala la denominación del servicio de atención a la mujer víctima de violencia de género que cuenta con un carácter multidisciplinar, destinado a atender, informar, orientar y asesorar en las áreas social, jurídica, psicológica, laboral y educativa, realizando en su caso una intervención integral e interdisciplinar:', 'Recursos de acogida temporal', 'Servicios sociales generales', 'Servicios de atención, información e intervención integral', 'Centros de acogida inmediata', 'c');`,
    `INSERT INTO "preguntas" VALUES (454, '¿Qué caracteriza a la definición global del Trabajo Social consensuada y aprobada en Melbourne en julio de 2014 (Australia) por la Federación Internacional del Trabajo Social (FITS) y la Asociación Internacional de Escuelas de Trabajo Social (AIETS)?', 'Indica que se trata de una disciplina académica basada en la práctica que capacita a los profesionales para su ejercicio', 'Destaca la apuesta por el cambio social, los derechos humanos, el empoderamiento y la emancipación de las personas', 'Expresa que el trabajo social tiene su propia base teórica, que construye conocimiento con las personas con quienes trabaja', 'Todas las respuestas son correctas', 'd');`,
    `INSERT INTO "preguntas" VALUES (455, 'Las funciones que desarrolla el Trabajo Social para lograr los objetivos son:', 'Función de gerencia, administración y planificación de servicios sociales, de investigación y docente', 'Función alternativa, de atención directa, administración y planificación de servicios sociales, de investigación y docente', 'Función preventiva, de atención directa, de gerencia, administración y planificación de servicios sociales, de investigación y docente', 'Función preventiva, de gerencia, administración y planificación de servicios sociales, de investigación y docente', 'c');`,
    `INSERT INTO "preguntas" VALUES (456, 'Cuando el Trabajo Social actúa precozmente sobre las causas de determinadas condiciones que generan problemas en la colectividad o cuando elabora modelos de intervención con grupos de población que se hayan en situación de riesgo, ¿desde qué tipo de función lo hace?', 'Función alternativa', 'Función de gerencia, administración y planificación', 'Función de atención directa', 'Función preventiva', 'd');`,
    `INSERT INTO "preguntas" VALUES (457, 'Los principios de los derechos humanos y dignidad humana son bienes que deben ser defendidos y promocionados por trabajadores y trabajadoras sociales. ¿Qué significa que el trabajo social se basa en el respeto al valor y dignidad inherentes a toda persona, y a los derechos que de ello se desprenden?', 'Que el trabajador/a social respeta el derecho a la autodeterminación', 'Que el trabajador/a social promueve el derecho a la participación', 'Que el trabajador/a social trata a cada persona como un todo e identifica y desarrolla sus fortalezas', 'Que el trabajador/a social respeta el derecho a la autodeterminación, promueve el derecho a la participación, trata a cada persona como un todo e identifica y desarrolla sus fortalezas', 'd');`,
    `INSERT INTO "preguntas" VALUES (458, 'Entre otros, los objetivos de acotar responsabilidades profesionales, promover el incremento de los conocimientos científicos y técnicos, definir el correcto comportamiento profesional con la persona usuaria y con otros profesionales, evitar la competencia desleal, mantener el prestigio de la profesión, y servir de base para las relaciones disciplinarias. ¿A qué documento normativo responde?', 'Principio de Justicia social', 'Declaración Internacional de Principios Éticos', 'Consejo General del Trabajo Social', 'Código Deontológico', 'd');`,
    `INSERT INTO "preguntas" VALUES (459, 'La Integridad profesional, la promoción de los Derechos Humanos, tratar a las personas como un todo, la promoción de la Justicia Social, la promoción del derecho a la participación, el reconocimiento de la Dignidad Inherente al Ser Humano, la promoción del derecho a la participación, el uso ético de la tecnología y las redes sociales y el respeto por la confidencialidad y la privacidad de las personas ¿Qué son?', 'Principios éticos de la Declaración Global de Principios Éticos', 'Funciones regulativas del código de ética profesional', 'Enunciados descritos por De la Red, basados en los elementos relacionados con las dimensiones de la ética', 'Principios operativos éticos orientados a la acción social expuestos por De Robertis en la conferencia ofrecida en el Segundo Congreso Internacional de Trabajo Social', 'A');`,
    `INSERT INTO "preguntas" VALUES (460, 'En el articulado del código deontológico se regula...', 'La actividad y la conducta profesional en las relaciones con los colegas, otros profesionales, instituciones, la propia profesión, la sociedad, etc.', 'Las relaciones y modos de actuación que han de tener los profesionales con los usuarios y en las entidades y organizaciones donde presten sus servicios', 'El desarrollo del principio general de la profesión, similar a los establecidos en la Declaración Internacional de Principios Éticos', 'Todas las respuestas son correctas', 'D');`,
    `INSERT INTO "preguntas" VALUES (461, 'El actual código deontológico pretende confirmar el compromiso de la profesión del trabajo social con la sociedad, incluyendo los avances científico-técnicos y el desarrollo de nuevos derechos y responsabilidades tanto del profesional del trabajo social como de las personas usuarias. Para ello ¿Qué funciones debe desarrollar el profesional del trabajo social?', 'Planificar, proyectar, calcular, aplicar, evaluar y modificar los servicios y políticas sociales para los grupos y comunidades', 'Todas las respuestas son correctas', 'Información, Investigación, Prevención, Asistencia, Atención directa, Promoción e inserción social, Mediación, Planificación, Gerencia y Dirección, Evaluación, Supervisión, Docencia, y Coordinación', 'Trabajar en un amplio marco de ámbitos organizativos y proporcionan recursos y prestaciones a diversos sectores de la población a nivel micro, meso y macro social', 'B');`,
    `INSERT INTO "preguntas" VALUES (462, 'Las funciones de los profesionales del trabajo social son realizadas con unos instrumentos específicos del trabajo social. ¿Cuáles NO lo son?', 'Historia social, Ficha Social, Informe Social, Escalas de valoración social y Proyecto de intervención social', 'Asistencia, Atención directa, Promoción e inserción social, Mediación, Planificación', 'Escalas de valoración social y Proyecto de intervención social', 'Historia social, Ficha Social e Informe Social', 'B');`,
    `INSERT INTO "preguntas" VALUES (463, 'La Familia como unidad de intervención en el trabajo social,...', 'Es el lugar donde se construye la identidad individual y social de las personas', 'Todas las respuestas son correctas', 'Es una realidad compleja y dinámica dentro del gran sistema que forma la sociedad en su conjunto', 'Constituye una realidad compleja, pero sigue siendo la unidad básica de articulación social, escuela de valores, transmisión cultural, agente de cohesión e integración social, de solidaridad intergeneracional y espacio vital para el desarrollo y bienestar de sus miembros', 'B');`,
    `INSERT INTO "preguntas" VALUES (464, '¿Qué criterios generales o básicos han de tenerse en cuenta para la intervención con familias?', 'Seguir una política social que priorice las intervenciones de prevención social dirigida a todas las familias, la protección a la Infancia: como sujeto social y de derechos y el apoyo a la familia intentando pasar de la intervención individual a la familiar', 'Seguir una política social que priorice las intervenciones de prevención social dirigida a todas las familias y la protección a la Infancia: como sujeto social y de derechos', 'El apoyo a la familia intentando pasar de la intervención individual a la familiar y la protección a la Infancia: como sujeto social y de derechos', 'Seguir una política social que priorice las intervenciones de prevención social dirigida a todas las familias', 'A');`,
    `INSERT INTO "preguntas" VALUES (465, '¿A qué función del Trabajador social nos referimos cuando interviene con la familia realizando el estudio de las causas y la magnitud de los problemas y necesidades de los menores y el estudio de las problemáticas emergentes y de los indicadores diagnósticos de población?', 'Función de planificación y gestión', 'Función de investigación', 'Función preventiva', 'Función de coordinación y control', 'B');`,
    `INSERT INTO "preguntas" VALUES (466, '¿A qué función del Trabajador social nos referimos cuando interviene con la familia realizando seguimiento de los casos para ver la evolución del menor e introduciendo intervenciones profesionales que permitan un mejor ajuste social?', 'Función asistencial', 'Función de coordinación y control', 'Función de rehabilitación y corrección', 'Función de investigación', 'C');`,
    `INSERT INTO "preguntas" VALUES (467, '¿Qué función del Trabajador social consideramos la más importantes en el trabajo con familias pues aporta un conocimiento más amplio y objetivo sobre la realidad familiar y personal del niño y su familia, favoreciendo un diagnóstico más ajustado y fiable?.', 'Función asistencial', 'Función de rehabilitación y corrección', 'Función de planificación y gestión', 'Función de coordinación y control', 'D');`,
    `INSERT INTO "preguntas" VALUES (468, '¿Qué técnicas concretas se emplean en la intervención familiar dentro de la corriente sistémica?', 'La anulación de tareas, la desequilibración, la dramatización, la redefinición de los síntomas y la intención paradójica o la instigación', 'La desequilibración, la dramatización y la redefinición de los síntomas', 'La prescripción de tareas, la desequilibración, la dramatización, la redefinición de los síntomas, la intención paradójica o la instigación', 'La prescripción de tareas, la desequilibración y la dramatización', 'C');`,
    `INSERT INTO "preguntas" VALUES (469, 'De las siguientes afirmaciones sobre la intervención familiar del Trabajador/a social, señale la incorrecta:', 'La intervención debe ser globalizadora, dirigida a encauzar todos los aspectos que están distorsionados y evitar atender solamente alguno de ellos, parcializándolos', 'La atención a la familia empieza cuando el trabajador/a social establece la tercera relación con ésta y con la situación con la que deberá trabajar', 'El trabajador/a social tendrá que recibir y contener diferentes estados emocionales del grupo familiar', 'La eficacia de la intervención se podría valorar más con el aumento de la capacidad del grupo familiar para hacerse cargo y superar nuevas dificultades, que con la resolución concreta de la demanda que le ha llevado a acudir', 'B');`,
    `INSERT INTO "preguntas" VALUES (470, 'Para realizar el seguimiento de una situación familiar problemática, el Trabajador/a social interventor requiere tener un conocimiento de la misma que permita:', 'Establecer objetivos que previsiblemente se puedan lograr basados en la movilización de recursos', 'Determinar de qué manera se llevará a cabo el tratamiento y qué medios y recursos técnicos se tendrán que emplear', 'Decidir cuáles son los aspectos prioritarios y cuáles los que se han de atender en segundo lugar', 'Todas las respuestas son correctas', 'D');`,
    `INSERT INTO "preguntas" VALUES (471, 'La existencia de factores sociales (internos y externos) de riesgo impiden que en los cambios operados por el normal desarrollo familiar se obtengan nuevas relaciones funcionales o situaciones manejables con los propios recursos de la familia. Señale de la siguiente lista que factores son de carácter interno:', 'Deficiencias formativas para el desarrollo de los roles familiares en el contexto del cambio social', 'Falta de servicios para el tiempo libre y de recursos culturales', 'Falta de comunicación y solidaridad en el tejido social', 'Desfase entre los modelos educativos de los padres con respecto a los hijos', 'D');`,
    `INSERT INTO "preguntas" VALUES (472, '¿Qué disposiciones legales y reglamentarias representan el marco jurídico del diseño del nivel asistencial primario en salud?', 'La Declaración de Alma Ata, Real Decreto 137/1984 de 11 de enero, Ley 14/1986, de 25 de abril y Real Decreto 118/1991, de 25 de enero', 'Ley 14/1986, de 25 de abril y Real Decreto 118/1991, de 25 de enero', 'La Declaración de Alma Ata, 1978 y Ley 14/1986, de 25 de abril', 'Ley 14/1986, de 25 de abril y Real Decreto 137/1984 de 11 de enero', 'A');`,
    `INSERT INTO "preguntas" VALUES (473, 'De las siguientes normativas legales ¿Cuál es de aplicación en la comunidad autónoma de Canarias en materia de equipos de atención primaria en salud?', 'Ley 11/1994, de 26 de julio, de Ordenación Sanitaria', 'Ley 14/1986, de 25 de abril', 'Real Decreto 118/1991, de 25 de enero', 'Real Decreto 137/1984 de 11 de enero', 'A');`,
    `INSERT INTO "preguntas" VALUES (474, '¿Quién define la Atención Primaria como... "Una atención a la salud esencial, basada en métodos y tecnologías prácticas, científicamente comprobadas y socialmente aceptadas, garantizando la participación de todos los estratos sociales, a un costo que el país y las comunidades puedan soportar, en todas las etapas de su desarrollo, y con espíritu de autorresponsabilidad y determinación"?', 'La Organización Mundial de la Salud', 'Ninguna de las respuestas es correcta', 'El Institute of Medicine IOM', 'H. Vuori', 'A');`,
    `INSERT INTO "preguntas" VALUES (475, 'El objetivo último de la Atención Primaria es la mejora del nivel de salud ¿Cuáles son los objetivos intermedios sobre los que se sustenta este objetivo final?', 'El tratamiento apropiado de las enfermedades más habituales y de los traumatismos comunes', 'La globalidad y continuidad de la atención a lo largo de toda la vida del paciente', 'El adecuado abastecimiento de alimento y promoción de una buena alimentación', 'La calidad de los cuidados, la satisfacción del usuario y la eficiencia', 'D');`,
    `INSERT INTO "preguntas" VALUES (476, 'El énfasis en la prevención y promoción es uno de los elementos de la Atención Primaria. Señala otros elementos que aparezcan:', 'Acceso y cobertura universal', 'Intersectorialidad', 'Solidaridad', 'Participación', 'A');`,
    `INSERT INTO "preguntas" VALUES (477, 'Las estructuras básicas de la salud en atención primaria son:', 'Marco territorial: Zona Básica de Salud', 'Estructura física y funcional: Centro de Salud', 'Estructura humana: Equipo de Atención Primaria', 'Todas las respuestas son correctas', 'D');`,
    `INSERT INTO "preguntas" VALUES (478, 'La Ley General de Sanidad prescribe las siguientes funciones para los Centros de Salud. Señala la respuesta más completa que contenga las mencionadas funciones:', 'Albergar los recursos materiales precisos, servir como centro de reunión entre la comunidad y los profesionales y facilitar el trabajo en equipo de los profesionales', 'Albergar la estructura física de consultas y servicios asistenciales, albergar los recursos materiales precisos, servir como centro de reunión entre la comunidad y los profesionales, facilitar el trabajo en equipo de los profesionales y mejorar la organización administrativa de la atención', 'Facilitar el trabajo en equipo de los profesionales y mejorar la organización administrativa de la atención', 'Albergar la estructura física de consultas y servicios asistenciales, albergar los recursos materiales precisos y servir como centro de reunión entre la comunidad y los profesionales', 'B');`,
    `INSERT INTO "preguntas" VALUES (479, 'Las funciones del EAP son de atención directa y complementarias. Señala cuáles NO son las complementarias:', 'Administrativa, asistencial o curativa y docencia e investigación', 'Promoción de la salud, prevención de la enfermedad, asistencial o curativa y educación para la salud', 'Docencia e investigación, prevención de la enfermedad, organización y gestión', 'Coordinación con otros niveles de atención y asistencial o curativa', 'B');`,
    `INSERT INTO "preguntas" VALUES (480, 'Señala el objetivo general del Programa de Salud Infantil:', 'Realizar periódicamente controles de salud a la población infantil, según las actividades establecidas para cada grupo de edad y Fomentar la lactancia materna durante el primer semestre de vida del niño', 'Identificar población infantil con factores de riesgo social y con indicadores de maltrato y realizar el seguimiento de su situación de salud y vacunar correctamente a la población infantil de acuerdo al calendario vigente en la Comunidad Autónoma', 'Contribuir al adecuado desarrollo de la población infantil, mediante la promoción de estilos de vida saludables, la detección precoz y el abordaje integral de los problemas de salud más prevalentes, con mayores repercusiones y que sean susceptibles de intervenciones eficaces', 'Promover la incorporación de conocimientos, actitudes y hábitos positivos para la salud infantil en padres/madres, niñas/os y educadores y detectar precozmente metabolopatías congénitas', 'C');`,
    `INSERT INTO "preguntas" VALUES (481, '¿Cuáles son los principios rectores de una adecuada atención sanitaria?', 'Observación. Coordinación de niveles. Flexibilidad de acciones. Integridad. Inmediatez y Vigilancia', 'Obligatoriedad. Responsabilidad Pública. Temporalidad. Integridad. Universalidad y Observación', ' Observación y vigilancia. Homogeneidad de acciones. Coordinación de niveles asistenciales. Integridad y Territorialidad', 'Observación y vigilancia. Responsabilidad Pública. Obligatoriedad. Integridad. Inmediatez. Universalidad. Coordinación de niveles. Territorialidad y Atemporalidad', 'D');`,
    `INSERT INTO "preguntas" VALUES (482, '¿Qué funciones del trabajador/a social son comunes a los diferentes objetivos específicos del Programa de Salud Infantil?', 'Informar a los recursos de la zona básica de salud de la importancia de la prevención de la enfermedad y para ello, la asistencia preventiva a los controles de salud', 'Todas las respuestas son correctas', 'Diseño de material informativo con el resto del equipo del Programa para difundir en los recursos de la zona básica de salud', 'Detección y captación de menores para el Programa cuando se realizan visitas domiciliarias o a otros recursos', 'B');`,
    `INSERT INTO "preguntas" VALUES (483, 'Señala Los Factores de riesgo social que se contemplan en el Programa de Salud Infantil:', 'Enfermedad psíquica del padre/madre. Discapacidad intelectual de la madre. Antecedentes de maltrato de los padres', 'Dependencia al alcohol y/o otras drogas de los padres. Madre y/o padre adolescentes. Aislamiento social', 'Patología crónica grave y/o discapacidad del menor. Dificultades familiares. Marginalidad y ausencia continuada de los padres', 'Todas las respuestas son correctas', 'D');`,
    `INSERT INTO "preguntas" VALUES (484, 'Cuando el profesional trabajador/a social realiza la valoración sociofamiliar explorando e Identificando los recursos que la familia ha activado tanto formales como informales para solucionar lo planteado ¿Qué área o aspecto social está valorando respecto a los factores de riesgo?', 'El problema / demanda', 'Área familiar/ individual', 'Área de apoyo social y entorno', 'Todas las respuestas son correctas', 'A');`,
    `INSERT INTO "preguntas" VALUES (485, '"Acción, omisión o trato negligente, no accidental, que priva al menor de 18 años de sus derechos y su bienestar, que amenaza o interfiere en su ordenado desarrollo físico, psíquico o social y cuyos autores pueden ser personas, instituciones o la propia sociedad". ¿Qué concepto se está definiendo?', 'Vulnerabilidad infantil', 'Desprotección infantil', 'Maltrato Infantil', 'Desamparo infantil', 'C');`,
    `INSERT INTO "preguntas" VALUES (486, '¿Qué función del trabajador/a social NO es común a los diferentes objetivos específicos del Programa de Salud Infantil?', 'Detección y captación de menores para el Programa cuando se realizan visitas domiciliarias o a otros recursos', 'Informar a los recursos de la zona básica de salud de la importancia de la prevención de la enfermedad y para ello, la asistencia preventiva a los controles de salud', 'Diseño de material informativo con el resto del equipo del Programa para difundir en los recursos de la zona básica de salud', 'Estar alerta para la identificación de factores de riesgo social, para su identificación y posterior intervención', 'D');`,
    `INSERT INTO "preguntas" VALUES (487, '¿En qué año se legalizó la interrupción voluntaria del embarazo con una ley de plazos?', '1978', '1983', '1985', '1995', 'C');`,
    `INSERT INTO "preguntas" VALUES (488, 'Las actuaciones recomendadas en el programa del embarazo van dirigidas a todas las mujeres gestantes, a sus parejas y a sus familiares. ¿En qué mujeres debe hacerse especial énfasis al ofertarlo?', 'Embarazo en la adolescencia. Embarazo no deseado. Embarazo de alto riesgo por causa médica. Problemas laborales relacionados con el embarazo y/o la lactancia', 'Todas las respuestas son correctas', 'Discapacidad física o psíquica. Situación de violencia de género. Dificultades en las relaciones entre los miembros de la unidad de convivencias relacionadas con los cuidados', 'Dependencia del alcohol u otras drogas. Responsabilidad en los cuidados de persona dependiente. Aislamiento social o apoyo social insuficiente. Previsión de hospitalización del RN tras el parto', 'B');`,
    `INSERT INTO "preguntas" VALUES (489, '¿En qué artículo de la Ley 1/2010, de 26 de febrero, Canaria de igualdad entre mujeres y hombres, se hace mención al "Derecho al embarazo y la maternidad"?', 'Artículo 56', 'Artículo 63', 'Artículo 65', 'Artículo 36', 'A');`,
    `INSERT INTO "preguntas" VALUES (490, 'La Ley 41/2002 introduce profundas modificaciones y novedades respecto de la figura del menor en el ámbito de las decisiones en salud ¿Cuáles son?', 'Entre los 12 y 16 años aparece el criterio de "apreciación sobre la capacidad intelectual y emocional del menor" y a los 16 años se fija la mayoría de edad médica', 'Se fija la mayoría de edad médica en los 14 años, por eso deja de ser atendido por su pediatra', 'El menor de edad y emancipado puede prestar su consentimiento válido en el tratamiento o utilización de fármacos', 'Para reconocer la competencia del menor entre 12 y 16 años para dar su consentimiento sin informar a sus padres o tutores no se tienen en cuenta consideración alguna', 'A');`,
    `INSERT INTO "preguntas" VALUES (491, 'El tabaquismo, alcoholismo, drogadicción y el nivel socio-económico bajo, entre otros, en el parto ¿a qué grupo de factores de riesgo pertenece?', 'Antecedentes médicos', 'Sociodemográfico', 'Reproductivos', 'Embarazo actual', 'B');`,
    `INSERT INTO "preguntas" VALUES (492, '¿Cómo ha sido denominada la violencia contra las mujeres en los últimos años?', 'Violencia machista', 'Violencia doméstica', 'Violencia de género', 'Violencia psicológica', 'C');`,
    `INSERT INTO "preguntas" VALUES (493, '¿Cuál de los siguientes se considera dispositivo crucial para la prevención, detección precoz, captación e intervención con las mujeres víctimas de violencia?', 'Los Servicios Sociales Comunitarios', 'Los Equipos de Atención Primaria', 'Los Equipos de Atención Especializada', 'Los Equipos de Atención Hospitalaria', 'B');`,
    `INSERT INTO "preguntas" VALUES (494, 'En la atención a las mujeres en situaciones de violencia de género, al igual que en cualquier intervención sanitaria, las y los profesionales deben tener siempre presentes:', 'Los principios éticos de actuación', 'Los derechos que las mujeres tienen como pacientes', 'Los códigos deontológicos profesionales', 'Todas son correctas', 'D');`,
    `INSERT INTO "preguntas" VALUES (495, 'El ciclo de la violencia consta de tres fases. ¿Cuáles son?', 'Tensión, agresión, calma o remisión', 'Agresión, tensión, calma o remisión', 'Alteración, agresión, calma o remisión', 'Tensión, alteración, agresión', 'A');`,
    `INSERT INTO "preguntas" VALUES (496, 'Las víctimas de violencia de género reciben apoyos concretos y refuerzos en todas las fases de la violencia. ¿En cuál de ellas es esencial que decidan dar pasos encaminados a mejorar su situación?', 'Fase de tensión', 'Fase de agresión', 'Fase de calma', 'Fase de tensión, agresión, calma', 'B');`,
    `INSERT INTO "preguntas" VALUES (497, '¿Qué aspectos sociales que se vinculan a la existencia de violencia doméstica debemos explorar las y los profesionales del trabajo social?', 'Antecedentes familiares de violencia doméstica, presencia de autoritarismo, de aislamiento social, dependencia económica y existencia de otras posibles víctimas en el entorno familiar', 'Antecedentes familiares de violencia doméstica, presencia de aislamiento social y existencia de otras posibles víctimas en el entorno familiar', 'Antecedentes familiares de violencia doméstica, presencia de aislamiento social, dependencia económica y existencia de otras posibles víctimas en el entorno familiar', 'Antecedentes familiares de violencia doméstica y existencia de otras posibles víctimas en el entorno familiar', 'A');`,
    `INSERT INTO "preguntas" VALUES (498, 'El trabajo social sanitario es el responsable de abordar los elementos contextuales presentes en el proceso salud/enfermedad, en el caso de la violencia, aportando la dimensión social a este problema. ¿Qué tipo de intervención debemos desarrollar las y los trabajadores sociales?', 'Una intervención bio-psico-social, que abarque todas las consecuencias de la violencia de género', 'Una intervención psicosocial que abarque todas las consecuencias de la violencia de género', 'Una intervención social que abarque todas las consecuencias de la violencia de género', 'Una intervención centrada en la mujer que abarque todas las consecuencias de la violencia de género', 'A');`,
    `INSERT INTO "preguntas" VALUES (499, '¿Qué funciones desempeñan las y los trabajadores sociales que guardan relación con el diagnóstico y tratamiento de la violencia?', 'Tipología y gravedad del maltrato y los antecedentes personales y familiares de violencia, régimen de tenencia de la vivienda, búsqueda de alojamiento alternativo en caso necesario, situación económica, red de apoyo social, situaciones de vulnerabilidad y dependencia en el entorno familiar', 'Ayudar a la mujer a ser consciente de su propia situación, generar motivación suficiente para el cambio, superar las dependencias, fomentar la repercusión global que precisa tras la destrucción que acarrea la violencia', 'Desarrollar una labor de acompañamiento, dedicada al apoyo, escucha, aliento, orientación y coordinación', 'Desarrollar una labor de coordinación con el equipo interdisciplinar de salud, así como de derivación y colaboración con el resto de sistemas de protección social', 'A');`,
    `INSERT INTO "preguntas" VALUES (500, '¿Cuáles de las siguientes son funciones del DEMA?', 'Desplazamiento hasta el lugar donde se encuentra la mujer, acompañamiento al reconocimiento médico inmediato, si fuera necesario, acompañar y asistir a la mujer para que realice todos los trámites que en ese momento sean precisos para una correcta actuación judicial y policial y facilitar alojamiento, si procede, en un Centro de Acogida Inmediata (C.A.I.)', 'Asesoramiento jurídico, apoyo psicológico, realización de gestiones para el acogimiento en otros centros y asesoramiento laboral y de prestaciones sociales', 'Asistencia integral, apoyo psicológico, apoyo educativo y formativo (para la mujer y los hijos/as) asesoramiento jurídico, orientación para la búsqueda de empleo, alojamiento y manutención temporal, gestiones con otros centros o servicios de las administraciones públicas competentes', 'Asesoramiento socio-laboral y jurídico, apoyo psicológico y actividades grupales de crecimiento personal, habilidades sociales', 'A');`,
    `INSERT INTO "preguntas" VALUES (501, '¿A quién va dirigido el Protocolo de Atención a las Personas Mayores de la Comunidad Autónoma Canaria?', 'Hombres y mujeres de 65 o más años', 'Hombres y mujeres de 67 o más años', 'Hombres y mujeres entre 65 y 75 años', 'Hombres y mujeres de 75 o más años', 'A');`,
    `INSERT INTO "preguntas" VALUES (502, '¿Qué se entiende por Persona Mayor Autónoma?', 'Persona de 65 o más años que presenta enfermedad aguda o crónica con cierta limitación para la realización de las actividades de la vida diaria', 'Persona de 65 o más años que, presentando dependencia en algunas actividades de la vida diaria, cumple algún criterio de riesgo en el área física, mental y/o social', 'Persona de 65 y más años que no presenta enfermedad, o que aun padeciéndola de manera aguda o crónica, mantiene su autonomía e independencia en las actividades de la vida diaria', 'Persona de 65 y más años que presenta una limitación física, mental, intelectual o sensorial por la que precisa ayuda de otra/s persona/s para las actividades básicas de la vida diaria', 'C');`,
    `INSERT INTO "preguntas" VALUES (503, '¿Qué se entiende por Persona Mayor Frágil o en Riesgo de Dependencia?', 'Persona de 65 o más años que presenta enfermedad aguda o crónica con cierta limitación para la realización de las actividades de la vida diaria', 'Persona de 65 o más años que, presentando dependencia en algunas actividades de la vida diaria, cumple algún criterio de riesgo en el área física, mental y/o social', 'Persona de 65 y más años que no presenta enfermedad, o que aun padeciéndola de manera aguda o crónica, mantiene su autonomía e independencia en las actividades de la vida diaria', 'Persona de 65 y más años que presenta una limitación física, mental, intelectual o sensorial por la que precisa ayuda de otra/s persona/s para las actividades básicas de la vida diaria', 'B');`,
    `INSERT INTO "preguntas" VALUES (504, '¿Qué se entiende por Persona Mayor Dependiente?', 'Persona de 65 o más años que presenta enfermedad aguda o crónica con cierta limitación para la realización de las actividades de la vida diaria', 'Persona de 65 o más años que, presentando dependencia en algunas actividades de la vida diaria, cumple algún criterio de riesgo en el área física, mental y/o social', 'Persona de 65 y más años que no presenta enfermedad, o que aun padeciéndola de manera aguda o crónica, mantiene su autonomía e independencia en las actividades de la vida diaria', 'Persona de 65 y más años que presenta una limitación física, mental, intelectual o sensorial por la que precisa ayuda de otra/s persona/s para las actividades básicas de la vida diaria', 'D');`,
    `INSERT INTO "preguntas" VALUES (505, 'Señala de las siguientes, qué actividades de promoción de la salud realiza el/la trabajador/a social en cada contacto con el paciente mayor:', 'Promoción de la autoestima, del ejercicio físico, consejo sobre hábitos tóxicos y promoción de la adherencia a los programas de intervención comunitaria', 'Promoción del ejercicio físico y promoción del seguimiento de las actividades de promoción de salud y preventivas correspondientes a su grupo de edad', 'Despistaje de psicopatología, valoración del cumplimiento de los programas según problema de salud y valoración de la incorporación a las actividades comunitarias de apoyo', 'Valoración de la situación familiar, situación social y cumplimiento de los programas de salud específicos para la edad.', 'A');`,
    `INSERT INTO "preguntas" VALUES (506, '¿En qué área de la Valoración Geriátrica Integral (VGI) se valoran los Factores de Riesgo Social determinantes en la fragilidad cuando existe algún criterio de riesgo en las áreas física, funcional o mental?', 'Área física', 'Área funcional', 'Área mental', 'Área social', 'D');`,
    `INSERT INTO "preguntas" VALUES (507, '¿Cuáles de los siguientes factores de riesgo social afectan a la salud o a la autonomía de las personas mayores?', 'Si vive solo/a y presenta deterioro cognitivo o depresión, o su situación económica no le permite cubrir necesidades básicas, o el apoyo que recibe no se corresponde con sus necesidades', 'Si vive con otras personas y existen problemas en las relaciones de convivencia', 'Si carece de vivienda o reside en una infravivienda, o la vivienda presenta deficiencias o barreras', 'Todas son correctas', 'D');`,
    `INSERT INTO "preguntas" VALUES (508, 'La intervención del Trabajador/a Social se sustenta en los siguientes pilares básicos:', 'Desarrollo de actividades preventivas y de promoción de salud; adecuación y optimización de recursos sociales; intervenciones sobre el entorno familiar y comunitario, prestando especial atención a los cuidadores', 'Desarrollo de actividades preventivas y de promoción de salud', 'Adecuación y optimización de recursos sociales', 'Intervenciones sobre el entorno familiar y comunitario, prestando especial atención a los cuidadores', 'A');`,
    `INSERT INTO "preguntas" VALUES (509, 'Establecer reuniones periódicas de coordinación interdisciplinar con el fin de unificar criterios y establecer objetivos comunes para supervisar el proceso de intervención con la persona mayor, es una actividad propia de:', 'El Equipo de Atención Primaria', 'El/la trabajador/a social', 'El/la médico', 'El/la enfermero/a', 'B');`,
    `INSERT INTO "preguntas" VALUES (510, '¿Para qué tres tareas están cualificados los trabajadores/as sociales que forman parte de un grupo?', 'Consulta, facilitación y coordinación', 'Control, coordinación y movilización', 'Evaluación, control y consulta', 'Facilitación, audición y coordinación', 'A');`,
    `INSERT INTO "preguntas" VALUES (511, 'A. Brown (1988) es uno de los autores que con mayor claridad plantea una serie de objetivos posibles de alcanzar a través del trabajo social de grupo. Estos son:', 'Valoración individual o autoevaluación y apoyo y/o cambio en el grupo', 'Apoyo y mantenimiento individual, cambio en el entorno y cambio social', 'Cambio individual, información y educación, ocio compensatorio y mediación entre individuos y organizaciones', 'Todas las respuestas son correctas', 'D');`,
    `INSERT INTO "preguntas" VALUES (512, '¿Qué autor/a define el trabajo social de grupo como "un método de Trabajo Social que ayuda a los individuos a mejorar su funcionamiento social a través de las experiencias constructivas en grupo y a enfrentarse con los problemas personales, de grupo o comunidad, de una manera efectiva"?', 'G. Wilson', 'G. Konopka', 'N. Kisnerman', 'Ninguno', 'B');`,
    `INSERT INTO "preguntas" VALUES (513, 'Según las fases inicial, formativa, intermedia, revisoria, intermedia II, madurez y terminal ¿Qué autor/a las establece como las etapas o las fases del proceso evolutivo del grupo desde su formación hasta su desintegración?', 'G. Konopka', 'Ninguna respuesta es correcta', 'Robert Vinter', 'N. Kisnerman', 'C');`,
    `INSERT INTO "preguntas" VALUES (514, 'Cuando Natalio Kisnerman habla del trabajo social en grupos, establece distintas etapas del grupo en función de su estado y dinámica. Cuando menciona la etapa de integración se refiere a:', 'A pesar del buen nivel de madurez del grupo, en un momento dado disminuye el interés de los miembros', 'El grupo adquiere la capacidad de auto dirigirse', 'Las diferentes estructuras parciales o roles se hallan suficientemente o perfectamente ajustados entre sí', 'La coordinación entre los miembros respecto a la actividad no se efectúa basada en la autoridad', 'C');`,
    `INSERT INTO "preguntas" VALUES (515, '¿Qué autor clasifica los tipos de diagnósticos en los procesos del método de Trabajo Social en Grupo, como: dinámico, clínico y etiológico?', 'De Robertis y Pascal', 'Vinter', 'Konopka', 'N. Kisnerman', 'D');`,
    `INSERT INTO "preguntas" VALUES (516, '¿En base a qué se adopta un tratamiento al grupo, de acuerdo con la naturaleza del diagnóstico establecido?', 'Todas las respuestas son correctas', 'Planeamiento y el tipo de grupo', 'La estructura y técnicas de motivación', 'Determinación del momento oportuno de tratar el problema y el tipo de ayuda que puede brindar la institución', 'A');`,
    `INSERT INTO "preguntas" VALUES (517, 'La flexibilidad de organización como principio de las dinámicas de grupo ¿Qué significa?', 'El grupo debe formular sus objetivos, contenidos y métodos de actuación, y estos deben ser la pauta de la acción hasta que se formulen nuevos en base a las nuevas necesidades', 'La flexibilidad de organización no es un principio de las dinámicas de grupo', 'La formulación explicita de los objetivos aumenta la cohesión y el sentimiento de "nosotros" en el grupo, por lo que han de definirse conjuntamente por todos, para que todos se sientan vinculados', 'Se han de reducir al máximo las tensiones y los sentimientos de intimidación, por lo que habrá que solucionar los conflictos', 'A');`,
    `INSERT INTO "preguntas" VALUES (518, '¿Qué significa el liderazgo compartido como principio de las dinámicas de grupo?', 'Se han de reducir al máximo las tensiones y los sentimientos de intimidación, por lo que habrá que solucionar los conflictos', 'Cuando las funciones del liderazgo están distribuidas en todo el grupo, existe una mayor dedicación a la tarea, se permite la máxima evolución de los integrantes del grupo y las decisiones grupales son más realistas y validas', 'El grupo ha de conseguir una comunicación fluida y espontánea para que surja participación espontánea y libre', 'La comprensión del proceso grupal aumenta la posibilidad de una orientación hacia un objetivo y permite una rápida modificación de objetivos', 'B');`,
    `INSERT INTO "preguntas" VALUES (519, '¿A qué principio de las dinámicas de grupo responde la siguiente afirmación? Una evaluación continua permite una depuración y una modificación inteligente del proceso de resolución de problemas. En todo momento se ha de adaptar los objetivos, contenidos y actividades a los intereses del grupo.', 'Comunicación y consenso en la adopción de decisiones', 'Clima de confianza', 'Evaluación de objetivos y actividades', 'Creación de un ambiente conducente a la resolución del problema', 'C');`,
    `INSERT INTO "preguntas" VALUES (520, 'Termina esta afirmación... el Trabajo Social ha participado activamente en incluir "lo social"', 'En circunstancias de soledad y enfermedad', 'De discapacidad y dependencia', 'No sólo en circunstancias extraordinarias o de extrema necesidad', 'En autonomía', 'C');`,
    `INSERT INTO "preguntas" VALUES (521, 'La entrevista como técnica cuenta con ventajas y desventajas. Señala la respuesta que contiene solo las desventajas:', 'Relación interpersonal. Costo elevado de tiempo y esfuerzo. Se obtiene gran cantidad de información y variada y posibilidad de observación', 'Interferencia de sesgos que pueden incidir en la calidad de la información: del entrevistador, el entrevistado, de la situación. Flexibilidad y Posibilidad de evaluar personas que no podrían ser evaluadas con otros instrumentos', 'Costo elevado de tiempo y esfuerzo e Interferencia de sesgos que pueden incidir en la calidad de la información: del entrevistador, el entrevistado, de la situación', 'Costo elevado de tiempo y esfuerzo. Posibilidad de observación. Posibilidad de evaluar personas que no podrían ser evaluadas con otros instrumentos e Interferencia de sesgos que pueden incidir en la calidad de la información: del entrevistador, el entrevistado, de la situación', 'C');`,
    `INSERT INTO "preguntas" VALUES (522, 'Según Molleda, E., para que tenga lugar una entrevista en el proceso de intervención social es necesario que se den tres elementos ¿De qué tres elementos habla la autora?', 'Un/a trabajador/a social, una persona usuaria y un despacho que les permita confidencialidad', 'Una persona usuaria, una demanda de cambio y una grabadora', 'Un/a trabajador/a social, una persona usuaria y una grabadora', 'Un/a trabajador/a social, una persona usuaria y una demanda de cambio que la persona usuaria hace al trabajador/a social', 'D');`,
    `INSERT INTO "preguntas" VALUES (523, 'De las siguientes definiciones de la entrevista en Trabajo social ¿Cuál pertenece a Teresa Rusell?', '"La entrevista es una conversación seria, que se propone un fin determinado, distinto del simple placer de la conversación. Tiene como funciones principales: recoger datos, informar y motivar"', '"Es el intercambio verbal, cara a cara, entre dos o más personas en la que una, el entrevistador, trata de obtener información de otra u otras"', '"La entrevista es una relación interpersonal"', '"Se considera un método de investigación científica que utiliza un proceso de comunicación verbal para recoger más información en relación a una determinada finalidad"', 'C');`,
    `INSERT INTO "preguntas" VALUES (524, 'La entrevista no estructurada, ...', 'Es aquella en la que se hacen a todos/as los/as entrevistados/as las mismas "preguntas" con la misma formulación', 'Es aquella en la que el/la entrevistador/a dispone de un guion que recoge los temas que se deben tratar, si bien tanto el orden de las "preguntas" como el modo de formularlas se deja a su criterio', 'Es aquella en la que tanto el contenido como la forma son libres e incluso pueden variar en función del sujeto', 'Todas las entrevistas son estructuradas', 'C');`,
    `INSERT INTO "preguntas" VALUES (525, '¿En qué fase de la entrevista, el entrevistador no debe aceptar la inclusión de nuevos temas?', 'Cuerpo o parte central de la entrevista', 'Terminación o cierre de la entrevista', 'Preparación de la entrevista o pre-entrevista', 'Inicio de la entrevista', 'B');`,
    `INSERT INTO "preguntas" VALUES (526, 'Existen diversos tipos de entrevista y su adecuación dependerá de los objetivos que se persigan durante el caso. La entrevista de motivación pertenece al tipo clasificado según:', 'La libertad', 'Su finalidad', 'El número de personas que intervienen', 'La forma', 'B');`,
    `INSERT INTO "preguntas" VALUES (527, '¿A qué tipo de clasificación responde las entrevistas dirigidas, no dirigidas, directas, indirectas y extensivas Intensivas o profundas?', 'La modalidad', 'Su finalidad', 'Al momento', 'La forma', 'D');`,
    `INSERT INTO "preguntas" VALUES (528, 'Durante el proceso de la entrevista la observación de la conducta es fundamental y si ésta se manifiesta mediante: sudoración, cambio de la temperatura de las manos, irregularidades respiratorias, ¿a través de qué variable o aspecto se manifiesta el usuario?', 'Variables mentales', 'Variables relacionadas con la verbalización', 'Aspectos fisiológicos', 'Variables motoras', 'C');`,
    `INSERT INTO "preguntas" VALUES (529, 'Lo que el sujeto manifiesta sobre sí mismo, en aspectos tales como: la historia de sus problemas, su situación actual, sus preocupaciones y objetivos, etc. Según Sandin (1985) ¿a qué aspecto clave de la entrevista se refiere?', 'Contenido', 'Estructura', 'Proceso', 'Ninguna respuesta es correcta', 'A');`,
    `INSERT INTO "preguntas" VALUES (530, '¿Qué autor/a define la entrevista terapéutica como..." la reunión de dos personas, una de ellas reconocida como experta en materia de relaciones interpersonales, y la otra, conocida como cliente, entrevistado o paciente, que espera extraer algún provecho de una discusión seria con el experto, sobre sus necesidades"?', 'Mª Begoña Roji (1982)', 'Teresa Rossell (1987)', 'Sullivan (1984)', 'Sandin (1985)', 'C');`,
    `INSERT INTO "preguntas" VALUES (531, '¿Qué es lo que según Freud surge como respuesta emocional del analista a los estímulos que provienen del paciente, como resultado de la influencia de éste sobre los sentimientos inconscientes del analista?', 'Transferencia', 'Señalamientos', 'Neutralidad', 'Contratransferencia', 'D');`,
    `INSERT INTO "preguntas" VALUES (532, 'El trabajador/a social debe ser consciente de estar situado en un proceso donde tiene que adaptar sus intervenciones al ritmo del cliente, a la vez que ha de tener una actitud de apertura que lo disponga a compartir los elementos generados ¿Qué autor/a realiza esta afirmación?', 'Acinas', 'Rossell', 'Ituarte', 'Richmond', 'B');`,
    `INSERT INTO "preguntas" VALUES (533, 'La relación profesional puede definirse como un tipo particular de interacción, cuyo desarrollo se fragua y desarrolla en y a través de la comunicación que se establece entre trabajador/a social y cliente. ¿Qué autor/a realiza esta afirmación?', 'Freud', 'Richmond', 'Sullivan', 'Ituarte', 'C');`,
    `INSERT INTO "preguntas" VALUES (534, 'La elaboración de programas específicos de intervención para grupos de población con características articulares que se hallen en situación de riesgo ¿A qué función de la intervención indirecta hace referencia?', 'Prevención de la aparición de conflictos y/o problemas psicosociales, interviniendo precozmente sobre las situaciones que producen desigualdad o pérdida de la salud o bienestar de individuos, grupos o comunidades', 'Supervisión de los profesionales, trabajadores sociales y miembros de otras profesiones', 'Participación en los procesos de planificación, dirección y gestión de políticas de bienestar social', 'Dirección de los servicios dispuestos para lograr la mejora de la calidad de vida y el bienestar de la población', 'A');`,
    `INSERT INTO "preguntas" VALUES (535, 'Las técnicas de obtención y sistematización: fichero de recursos sociales, fichas, hemeroteca y bibliografía. ¿A qué conjunto de técnicas de intervención pertenece?', 'Exploración, conocimiento e intervención', 'Acción y movilización', 'Comunicación social', 'Documentación', 'D');`,
    `INSERT INTO "preguntas" VALUES (536, 'Las técnicas de animación sociocultural: actividades culturales, cooperativismo y las técnicas de grupo .¿A qué conjunto de técnicas de intervención pertenece?', 'Tratamiento e intervención', 'Investigación social', 'Intervención en la comunidad', 'Planificación', 'C');`,
    `INSERT INTO "preguntas" VALUES (537, 'De las siguientes afirmaciones sobre el acompañamiento en la relación de ayuda, señala la correcta:', 'El acompañamiento se trata más de una forma de entender la relación social y educativa, que de un concepto', 'El acompañamiento es una forma de entender la relación entre profesional y persona atendida', 'El acompañamiento es una forma de trabajar con recursos, métodos y técnicas diversas para facilitar el desarrollo personal, la maduración social y la autonomía de las personas', 'Todas las respuestas son correctas', 'D');`,
    `INSERT INTO "preguntas" VALUES (538, '"Las redes se erigen como una forma de organización social que permite a un grupo de personas potenciar sus recursos y contribuir a la resolución de problemas" ¿Qué autor/a hace esta definición?', 'Giddens, 2007', 'Folgheraiter, 2011', 'Rizo, 2006', 'Bonet, 2006', 'C');`,
    `INSERT INTO "preguntas" VALUES (539, '¿En la intervención en red, el rol del trabajador social es?', 'Catalizador de iniciativas colaboradoras con el centro o institución de cara a desarrollar acciones preventivas y formativas que se consideran necesarias', 'Referente organizativo y de recursos', 'Todas las respuestas son correctas', 'Facilitador de procesos individuales y colectivos', 'C');`,
    `INSERT INTO "preguntas" VALUES (540, '¿Qué autor/a definió el desarrollo comunitario como la "acción coordinada y sistemática que, en respuesta a las necesidades o a la demanda social, trata de organizar el progreso global de una comunidad territorial bien delimitada o de una población objetivo con la participación de los interesados"?', 'Resohazy, R.', 'Rothman, J.', 'Payne, M.', 'Bauman', 'A');`,
]